    return newMessage;
  }

  async updateEmailMessage(id: string, message: Partial<InsertEmailMessage>): Promise<EmailMessage> {
    const [updatedMessage] = await db.update(emailMessages)
      .set(message)
      .where(eq(emailMessages.id, id))
      .returning();
