import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { useAuth } from "@/hooks/use-auth";
import AppShell from "@/components/layout/app-shell";
import NotFound from "@/pages/not-found";
import Dashboard from "@/pages/dashboard";
import Login from "@/pages/login";
import Leden from "@/pages/leden";
import Lidgelden from "@/pages/lidgelden";
import FinancePage from "@/pages/FinancePage";
import PubliekeSchermen from "@/pages/publieke-schermen";
import PublicScreensPage from "@/pages/PublicScreensPage";
import PublicScreenViewPage from "@/pages/PublicScreenViewPage";
import { PublicViewPage } from "@/pages/PublicViewPage";
import Instellingen from "@/pages/instellingen";
import Profiel from "@/pages/profiel";
import { CardPage } from "@/pages/card";
import CardVerifyPage from "@/pages/card-verify";
import PublicCard from "@/pages/public-card";
import Lidkaarten from "@/pages/lidkaarten";
import Bestuur from "@/pages/bestuur";
import Berichten from "@/pages/berichten";
import { InstallPrompt } from "@/components/pwa/install-prompt";
import { OfflineIndicator } from "@/components/pwa/offline-indicator";

function LoadingSpinner() {
  return (
    <div className="min-h-screen flex items-center justify-center bg-background">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
        <p className="mt-4 text-muted-foreground">Laden...</p>
      </div>
    </div>
  );
}

function ProtectedRoute({ component: Component, ...props }: { component: React.ComponentType }) {
  const { user, loading } = useAuth();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (!user) {
    return <Login />;
  }

  return (
    <AppShell>
      <Component {...props} />
    </AppShell>
  );
}

function Router() {
  return (
    <Switch>
      <Route path="/login" component={Login} />
      <Route path="/" component={() => <ProtectedRoute component={Dashboard} />} />
      <Route path="/dashboard" component={() => <ProtectedRoute component={Dashboard} />} />
      <Route path="/leden" component={() => <ProtectedRoute component={Leden} />} />
      <Route path="/lidkaarten" component={() => <ProtectedRoute component={Lidkaarten} />} />
      <Route path="/lidgelden" component={() => <ProtectedRoute component={Lidgelden} />} />
      <Route path="/financien" component={() => <ProtectedRoute component={FinancePage} />} />
      <Route path="/publieke-schermen" component={() => <ProtectedRoute component={PublicScreensPage} />} />
      <Route path="/public/screen/:publicToken" component={PublicScreenViewPage} />
      <Route path="/screen/:publicToken" component={PublicScreenViewPage} />
      <Route path="/card/:memberId" component={CardPage} />
      <Route path="/card/verify/:qrToken" component={CardVerifyPage} />
      <Route path="/public/card/:memberId" component={PublicCard} />
      <Route path="/bestuur" component={() => <ProtectedRoute component={Bestuur} />} />
      <Route path="/berichten" component={() => <ProtectedRoute component={Berichten} />} />
      <Route path="/instellingen" component={() => <ProtectedRoute component={Instellingen} />} />
      <Route path="/profiel" component={() => <ProtectedRoute component={Profiel} />} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
        <InstallPrompt />
        <OfflineIndicator />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

