  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-feature-settings: "cv11", "cv02", "cv03", "cv04";
  }

  /* HubSpot-style focus management */
  :focus-visible {
    @apply outline-none ring-2 ring-primary ring-offset-2 ring-offset-background;
  }

  /* Modern button hover effects */
  .btn-hover {
    @apply transition-all duration-300 ease-out hover:shadow-lg;
  }

  /* Card hover effects with glassmorphism */
  .card-hover {
    @apply transition-all duration-300 ease-out;
  }

  /* Glassmorphism card effect */
  .glass-card {
    @apply backdrop-blur-xl bg-white/80 dark:bg-gray-900/80 border border-white/20 dark:border-gray-700/50;
    box-shadow: var(--glass-shadow);
  }

  .glass-card:hover {
