  /* Premium button styles */
  .btn-premium {
    @apply relative overflow-hidden bg-gradient-to-r from-blue-600 via-blue-500 to-blue-600 text-white font-medium rounded-xl px-6 py-3 shadow-lg transition-all duration-300;
    background-size: 200% 100%;
  }

  .btn-premium:hover {
