import React, { useState, useMemo } from 'react';
import { SegmentedControl } from '@/components/ui/segmented-control';
import { useQuery } from '@tanstack/react-query';
import { formatCurrencyShortEUR } from '@/lib/utils/currency';

interface IncomeData {
  category: string;
  amount: number;
  members: number;
}

export default function IncomeByCategoryCard() {
  const [period, setPeriod] = useState<'monthly' | 'yearly'>('yearly');

  const { data: transactions } = useQuery({
    queryKey: ["/api/transactions"],
  });

  const { data: members } = useQuery({
    queryKey: ["/api/members"],
  });

  const data = useMemo(() => {
    if (!Array.isArray(transactions) || !Array.isArray(members)) {
      return [
        { category: 'Senior', amount: 0, members: 0 },
        { category: 'Standaard', amount: 0, members: 0 },
        { category: 'Student', amount: 0, members: 0 },
      ];
    }

    // Groepeer leden per categorie
    const membersByCategory = members.reduce((acc: any, member: any) => {
      // Map database categories to display categories
      let category = 'Standaard';
      if (member.category === 'STUDENT') category = 'Student';
      else if (member.category === 'SENIOR') category = 'Senior';
      else if (member.category === 'STANDAARD') category = 'Standaard';

      if (!acc[category]) {
        acc[category] = { count: 0, totalIncome: 0 };
      }
      acc[category].count++;
      return acc;
    }, {});

    // Bereken inkomsten per categorie - voor nu simuleren we data gebaseerd op aantal leden
    const incomeByCategory: any = {};

    // Simuleer lidgelden gebaseerd op aantal leden per categorie
    Object.keys(membersByCategory).forEach(category => {
      const memberCount = membersByCategory[category].count;
      let monthlyFee = 25; // Default fee

      if (category === 'Student') monthlyFee = 15;
      else if (category === 'Senior') monthlyFee = 20;
      else if (category === 'Standaard') monthlyFee = 25;

      const yearlyIncome = memberCount * monthlyFee * (period === 'yearly' ? 12 : 1);
      incomeByCategory[category] = yearlyIncome;
    });

    // Zorg er altijd voor dat alle categorieÃ«n getoond worden
    const allCategories = ['Student', 'Standaard', 'Senior'];

    const result = allCategories.map(category => ({
      category,
      amount: incomeByCategory[category] || 0,
      members: membersByCategory[category]?.count || 0
    }));

    return result;
  }, [transactions, members, period]);

  const maxAmount = Math.max(...data.map(item => item.amount), 1);

  const periodOptions = [
    { value: 'monthly', label: 'Maandelijks' },
    { value: 'yearly', label: 'Jaarlijks' },
  ];

  return (
    <div className="glass-card rounded-2xl p-6 card-hover animate-fade-in group col-span-full">
      {/* Header */}
      <div className="flex items-center justify-between mb-6 pb-4 border-b border-gray-200">
        <div>
          <h3 className="text-lg font-semibold text-foreground group-hover:text-primary transition-colors duration-300 font-['Poppins']">Inkomsten per Categorie</h3>
          <p className="text-sm text-muted-foreground font-['Poppins']">Lidgelden opgesplitst per lidcategorie</p>
        </div>
        <SegmentedControl
          options={periodOptions}
          value={period}
          onChange={(value) => setPeriod(value as 'monthly' | 'yearly')}
        />
      </div>

      {/* Horizontal Bars */}
      <div className="space-y-6">
        {data.map((item, index) => {
          const widthPercentage = maxAmount > 0 ? (item.amount / maxAmount) * 100 : 0;

          // Gradient colors for each category
          const getBarGradient = (category: string) => {
            switch (category) {
              case 'Senior':
                return 'linear-gradient(135deg, #1E3A8A 0%, #3B82F6 100%)'; // Dark blue to medium blue
              case 'Standaard':
                return 'linear-gradient(135deg, #3B82F6 0%, #60A5FA 100%)'; // Medium blue to light blue
              case 'Student':
                return 'linear-gradient(135deg, #06B6D4 0%, #67E8F9 100%)'; // Cyan to light cyan
              default:
                return 'linear-gradient(135deg, #3B82F6 0%, #60A5FA 100%)'; // Default gradient
            }
          };

          return (
            <div key={index} className="flex items-center">
              {/* Category Label */}
              <div className="w-20 flex-shrink-0">
                <span className="text-sm font-medium text-gray-900 font-['Poppins']">
                  {item.category}
                </span>
              </div>

              {/* Bar Container */}
              <div className="flex-1 mx-4">
                <div 
                  className="h-5 bg-gray-100 rounded-full relative overflow-hidden"
                  role="progressbar"
                  aria-label={`${item.category}: ${formatCurrencyShortEUR(item.amount)}`}
                  aria-valuenow={item.amount}
                  aria-valuemax={maxAmount}
                >
                  <div
                    className="h-full rounded-full transition-[width] duration-200 ease-out"
                    style={{
                      width: `${widthPercentage}%`,
                      background: getBarGradient(item.category),
                    }}
                  />
                </div>
              </div>

              {/* Right Values */}
              <div className="w-24 text-right flex-shrink-0">
                <div className="text-sm font-bold text-blue-600 font-['Poppins']">
                  {formatCurrencyShortEUR(item.amount)}
                </div>
                <div className="text-xs text-gray-500 font-['Poppins']">
                  {item.members} leden
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Area, AreaChart } from 'recharts';
import { useQuery } from '@tanstack/react-query';
import { useState, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import { ChevronLeft, ChevronRight } from 'lucide-react';

export default function RevenueChart() {
  const [quarterOffset, setQuarterOffset] = useState(0); // 0 = huidig kwartaal, -1 = vorig kwartaal, +1 = volgend kwartaal

  const { data: transactions } = useQuery({
    queryKey: ["/api/transactions"],
  });

  const data = useMemo(() => {
    if (!Array.isArray(transactions)) {
      // Toon 3 maanden voor huidig trimester
      const result = [];
      for (let i = 2; i >= 0; i--) {
        const date = new Date();
        date.setMonth(date.getMonth() - i + (quarterOffset * 3));
        const monthName = date.toLocaleDateString('nl-BE', { month: 'short' });
        result.push({ month: monthName, revenue: 0 });
      }
      return result;
    }

    // Bereken inkomsten voor 3 maanden van geselecteerd trimester
    const monthlyRevenue = [];
    for (let i = 2; i >= 0; i--) {
      const date = new Date();
      date.setMonth(date.getMonth() - i + (quarterOffset * 3));
      const year = date.getFullYear();
      const month = date.getMonth();
      const monthName = date.toLocaleDateString('nl-BE', { month: 'short' });

      const monthRevenue = transactions
        .filter((t: any) => {
          const transactionDate = new Date(t.date);
          return transactionDate.getFullYear() === year && 
                 transactionDate.getMonth() === month &&
                 t.type === 'INCOME';
        })
        .reduce((sum: number, t: any) => sum + Math.abs(parseFloat(t.amount)), 0);

      monthlyRevenue.push({ month: monthName, revenue: monthRevenue });
    }

    return monthlyRevenue;
  }, [transactions, quarterOffset]);

  // Bereken dynamische Y-as schaal
  const maxRevenue = Math.max(...data.map(d => d.revenue), 100);
  const yAxisMax = Math.ceil(maxRevenue * 1.1);

  // Helper functies voor kwartaal navigatie
  const goToPreviousQuarter = () => setQuarterOffset(quarterOffset - 1);
  const goToNextQuarter = () => setQuarterOffset(quarterOffset + 1);
  const goToCurrentQuarter = () => setQuarterOffset(0);

  // Bepaal kwartaal label
  const currentQuarterDate = new Date();
  currentQuarterDate.setMonth(currentQuarterDate.getMonth() + (quarterOffset * 3));
  const year = currentQuarterDate.getFullYear();
  const month = currentQuarterDate.getMonth();
  const quarter = Math.floor(month / 3) + 1;

  // Bepaal periode label
  const periodLabel = `${quarter}e trimester ${year}`;

  return (
    <div className="glass-card rounded-2xl p-6 card-hover animate-fade-in group">
      <div className="mb-6 pb-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-foreground group-hover:text-primary transition-colors duration-300">Evolutie Inkomsten</h3>
            <p className="text-sm text-muted-foreground">Lidgelden per maand</p>
          </div>
          <div className="flex items-center gap-2">
            <Button 
              variant="outline" 
              size="sm" 
              onClick={goToPreviousQuarter}
              className="h-8 w-8 p-0"
            >
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={goToCurrentQuarter}
              className="text-xs px-3"
              disabled={quarterOffset === 0}
            >
              Vandaag
            </Button>
            <Button 
              variant="outline" 
              size="sm" 
              onClick={goToNextQuarter}
              className="h-8 w-8 p-0"
            >
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </div>
      <div className="relative">
        <h4 className="text-lg font-semibold mb-4 text-center text-[#2463eb]">{periodLabel}</h4>
        <div className="h-64">
        <ResponsiveContainer width="100%" height="100%">
          <AreaChart data={data}>
            <defs>
              <linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#2563EB" stopOpacity={0.3}/>
                <stop offset="95%" stopColor="#2563EB" stopOpacity={0}/>
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" stroke="#F3F4F6" />
            <XAxis 
              dataKey="month" 
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 12, fill: '#64748B' }}
            />
            <YAxis 
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 12, fill: '#64748B' }}
              domain={[0, yAxisMax]}
              tickFormatter={(value) => {
                if (value >= 1000) {
                  return `â‚¬${(value / 1000).toFixed(1)}k`;
                }
                return `â‚¬${value}`;
              }}
            />
            <Tooltip 
              contentStyle={{
                backgroundColor: 'white',
                border: '1px solid #E5E7EB',
                borderRadius: '8px',
                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
              }}
              formatter={(value: number) => [`â‚¬${value.toLocaleString('nl-BE')}`, 'Inkomsten']}
            />
            <Area
              type="monotone"
              dataKey="revenue"
              stroke="#2563EB"
              strokeWidth={3}
              fill="url(#colorRevenue)"
              dot={{ fill: 'white', stroke: '#2563EB', strokeWidth: 2, r: 4 }}
              activeDot={{ r: 6, fill: '#2563EB' }}
            />
          </AreaChart>
        </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
}

