import { useState } from "react";
import { Sheet, SheetContent, SheetDescription, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { CalendarIcon, Filter, X } from "lucide-react";
import { format } from "date-fns";
import { nl } from "date-fns/locale";
import { cn } from "@/lib/utils";

interface FilterValues {
  periodFrom?: Date;
  periodTo?: Date;
  categories: string[];
  amountMin?: number;
  amountMax?: number;
  paidFrom?: Date;
  paidTo?: Date;
  onlyWithMandate: boolean;
  onlyOverdue: boolean;
}

interface FiltersDrawerProps {
  filters: FilterValues;
  onFiltersChange: (filters: FilterValues) => void;
  onReset: () => void;
}

export function FiltersDrawer({ filters, onFiltersChange, onReset }: FiltersDrawerProps) {
  const [open, setOpen] = useState(false);

  const updateFilter = (key: keyof FilterValues, value: any) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  const toggleCategory = (category: string) => {
    const newCategories = filters.categories.includes(category)
      ? filters.categories.filter(c => c !== category)
      : [...filters.categories, category];
    updateFilter("categories", newCategories);
  };

  const categories = ["STUDENT", "VOLWASSEN", "SENIOR"];

  return (
    <Sheet open={open} onOpenChange={setOpen}>
      <SheetTrigger asChild>
        <Button variant="outline" className="flex items-center gap-2">
          <Filter className="h-4 w-4" />
          Geavanceerde filters
        </Button>
      </SheetTrigger>
      <SheetContent className="w-96 overflow-y-auto">
        <SheetHeader>
          <SheetTitle>Geavanceerde filters</SheetTitle>
          <SheetDescription>
            Verfijn je zoekresultaten met uitgebreide filteropties
          </SheetDescription>
        </SheetHeader>

        <div className="space-y-6 mt-6">
          {/* Period Range */}
          <div className="space-y-3">
            <Label className="text-sm font-medium">Periode</Label>
            <div className="grid grid-cols-2 gap-2">
              <div>
                <Label className="text-xs text-gray-500">Van</Label>
                <Popover>
                  <PopoverTrigger asChild>
                    <Button
                      variant="outline"
                      className={cn(
                        "w-full justify-start text-left font-normal",
                        !filters.periodFrom && "text-muted-foreground"
                      )}
                    >
                      <CalendarIcon className="mr-2 h-4 w-4" />
                      {filters.periodFrom ? format(filters.periodFrom, "dd-MM-yyyy", { locale: nl }) : "Selecteer datum"}
                    </Button>
                  </PopoverTrigger>
                  <PopoverContent className="w-auto p-0">
                    <Calendar
                      mode="single"
                      selected={filters.periodFrom}
                      onSelect={(date) => updateFilter("periodFrom", date)}
                      initialFocus
                    />
                  </PopoverContent>
                </Popover>
              </div>
              <div>
                <Label className="text-xs text-gray-500">Tot</Label>
                <Popover>
                  <PopoverTrigger asChild>
                    <Button
                      variant="outline"
                      className={cn(
                        "w-full justify-start text-left font-normal",
                        !filters.periodTo && "text-muted-foreground"
                      )}
                    >
                      <CalendarIcon className="mr-2 h-4 w-4" />
                      {filters.periodTo ? format(filters.periodTo, "dd-MM-yyyy", { locale: nl }) : "Selecteer datum"}
                    </Button>
                  </PopoverTrigger>
                  <PopoverContent className="w-auto p-0">
                    <Calendar
                      mode="single"
                      selected={filters.periodTo}
                      onSelect={(date) => updateFilter("periodTo", date)}
                      initialFocus
                    />
                  </PopoverContent>
                </Popover>
              </div>
            </div>
          </div>

          {/* Categories */}
          <div className="space-y-3">
            <Label className="text-sm font-medium">CategorieÃ«n</Label>
            <div className="space-y-2">
              {categories.map(category => (
                <div key={category} className="flex items-center space-x-2">
                  <Checkbox
                    id={category}
                    checked={filters.categories.includes(category)}
                    onCheckedChange={() => toggleCategory(category)}
                  />
                  <Label htmlFor={category} className="text-sm">
                    {category === "STUDENT" ? "Student" :
                     category === "STANDAARD" ? "Standaard" : "Senior"}
                  </Label>
                </div>
              ))}
            </div>
          </div>

          {/* Amount Range */}
          <div className="space-y-3">
            <Label className="text-sm font-medium">Bedrag (â‚¬)</Label>
            <div className="grid grid-cols-2 gap-2">
              <div>
                <Label className="text-xs text-gray-500">Minimum</Label>
                <Input
                  type="number"
                  placeholder="0"
                  value={filters.amountMin || ""}
                  onChange={(e) => updateFilter("amountMin", e.target.value ? parseFloat(e.target.value) : undefined)}
                />
              </div>
              <div>
                <Label className="text-xs text-gray-500">Maximum</Label>
                <Input
                  type="number"
                  placeholder="1000"
                  value={filters.amountMax || ""}
                  onChange={(e) => updateFilter("amountMax", e.target.value ? parseFloat(e.target.value) : undefined)}
                />
              </div>
            </div>
          </div>

          {/* Paid Date Range */}
          <div className="space-y-3">
            <Label className="text-sm font-medium">Betaald op</Label>
            <div className="grid grid-cols-2 gap-2">
              <div>
                <Label className="text-xs text-gray-500">Van</Label>
                <Popover>
                  <PopoverTrigger asChild>
                    <Button
                      variant="outline"
                      className={cn(
                        "w-full justify-start text-left font-normal",
                        !filters.paidFrom && "text-muted-foreground"
                      )}
                    >
                      <CalendarIcon className="mr-2 h-4 w-4" />
                      {filters.paidFrom ? format(filters.paidFrom, "dd-MM-yyyy", { locale: nl }) : "Selecteer datum"}
                    </Button>
                  </PopoverTrigger>
                  <PopoverContent className="w-auto p-0">
                    <Calendar
                      mode="single"
                      selected={filters.paidFrom}
                      onSelect={(date) => updateFilter("paidFrom", date)}
                      initialFocus
                    />
                  </PopoverContent>
                </Popover>
              </div>
              <div>
                <Label className="text-xs text-gray-500">Tot</Label>
                <Popover>
                  <PopoverTrigger asChild>
                    <Button
                      variant="outline"
                      className={cn(
                        "w-full justify-start text-left font-normal",
                        !filters.paidTo && "text-muted-foreground"
                      )}
                    >
                      <CalendarIcon className="mr-2 h-4 w-4" />
                      {filters.paidTo ? format(filters.paidTo, "dd-MM-yyyy", { locale: nl }) : "Selecteer datum"}
                    </Button>
                  </PopoverTrigger>
                  <PopoverContent className="w-auto p-0">
                    <Calendar
                      mode="single"
                      selected={filters.paidTo}
                      onSelect={(date) => updateFilter("paidTo", date)}
                      initialFocus
                    />
                  </PopoverContent>
                </Popover>
              </div>
            </div>
          </div>

          {/* Checkboxes */}
          <div className="space-y-3">
            <div className="flex items-center space-x-2">
              <Checkbox
                id="onlyWithMandate"
                checked={filters.onlyWithMandate}
                onCheckedChange={(checked) => updateFilter("onlyWithMandate", checked)}
              />
              <Label htmlFor="onlyWithMandate" className="text-sm">
                Alleen met SEPA-mandaat
              </Label>
            </div>
            <div className="flex items-center space-x-2">
              <Checkbox
                id="onlyOverdue"
                checked={filters.onlyOverdue}
                onCheckedChange={(checked) => updateFilter("onlyOverdue", checked)}
              />
              <Label htmlFor="onlyOverdue" className="text-sm">
                Alleen vervallen
              </Label>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-3 pt-4 border-t">
            <Button onClick={() => setOpen(false)} className="flex-1">
              Toepassen
            </Button>
            <Button onClick={onReset} variant="outline" className="flex items-center gap-2">
              <X className="h-4 w-4" />
              Reset
            </Button>
          </div>
        </div>
      </SheetContent>
    </Sheet>
  );
}

client/src/components/fees/import-dialog.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { FileText, Check, X, AlertTriangle } from "lucide-react";
import { CiImport, CiExport } from "react-icons/ci";
import { formatCurrencyBE, formatDateBE } from "@/lib/format";
import { parseCsv, parseMt940, guessMatches } from "@/lib/mock/fees";
import { Fee } from "@shared/fees-schema";

interface ImportDialogProps {
  open: boolean;
  onClose: () => void;
  fees: Fee[];
  onImport: (matches: any[]) => void;
}

export function ImportDialog({ open, onClose, fees, onImport }: ImportDialogProps) {
  const [step, setStep] = useState<"upload" | "preview" | "confirm">("upload");
  const [fileType, setFileType] = useState<"CSV" | "MT940" | "CODA">("CSV");
  const [file, setFile] = useState<File | null>(null);
  const [transactions, setTransactions] = useState<any[]>([]);
  const [matches, setMatches] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);

  const handleFileUpload = async (uploadedFile: File) => {
    setFile(uploadedFile);
    setLoading(true);

    try {
      const content = await uploadedFile.text();
      let parsedTransactions: any[] = [];

      if (fileType === "CSV") {
        parsedTransactions = parseCsv(content);
      } else if (fileType === "MT940") {
        parsedTransactions = parseMt940(content);
      } else {
        // CODA - would need specific implementation
        parsedTransactions = parseCsv(content); // Fallback to CSV for demo
      }

      setTransactions(parsedTransactions);

      // Auto-match transactions with fees
      const guessedMatches = guessMatches(parsedTransactions, fees);
      setMatches(guessedMatches);

      setStep("preview");
    } catch (error) {
      console.error("Error parsing file:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleConfirmImport = () => {
    const confirmedMatches = matches.filter(m => m.match && m.confidence !== "unknown");
    onImport(confirmedMatches);
    handleClose();
  };

  const handleClose = () => {
    setStep("upload");
    setFile(null);
    setTransactions([]);
    setMatches([]);
    onClose();
  };

  const updateMatch = (index: number, feeId: string | null) => {
    const newMatches = [...matches];
    if (feeId) {
      const fee = fees.find(f => f.id === feeId);
      newMatches[index] = {
        ...newMatches[index],
        match: fee,
        confidence: "manual"
      };
    } else {
      newMatches[index] = {
        ...newMatches[index],
        match: null,
        confidence: "unknown"
      };
    }
    setMatches(newMatches);
  };

  const getConfidenceBadge = (confidence: string) => {
    const variants = {
      certain: "bg-green-100 text-green-800",
      possible: "bg-yellow-100 text-yellow-800",
      manual: "bg-blue-100 text-blue-800",
      unknown: "bg-gray-100 text-gray-800",
    };

    const labels = {
      certain: "Zeker",
      possible: "Mogelijk",
      manual: "Handmatig",
      unknown: "Onbekend",
    };

    return (
      <Badge className={variants[confidence as keyof typeof variants]}>
        {labels[confidence as keyof typeof labels]}
      </Badge>
    );
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Betalingen importeren</DialogTitle>
          <DialogDescription>
            Import betalingen uit bankbestanden en koppel ze automatisch aan openstaande lidgelden
          </DialogDescription>
        </DialogHeader>

        <Tabs value={step} className="mt-6">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="upload" disabled={step !== "upload"}>
              1. Upload bestand
            </TabsTrigger>
            <TabsTrigger value="preview" disabled={step !== "preview"}>
              2. Controleer matches
            </TabsTrigger>
            <TabsTrigger value="confirm" disabled={step !== "confirm"}>
              3. Bevestig import
            </TabsTrigger>
          </TabsList>

          <TabsContent value="upload" className="space-y-6">
            <div className="space-y-4">
              <div>
                <Label>Bestandstype</Label>
                <Select value={fileType} onValueChange={(value: any) => setFileType(value)}>
                  <SelectTrigger className="w-48">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="CSV">CSV</SelectItem>
                    <SelectItem value="MT940">MT940 (SWIFT)</SelectItem>
                    <SelectItem value="CODA">CODA (BelgiÃ«)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
                <CiImport className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                <div className="space-y-2">
                  <p className="text-lg font-medium">Sleep een bestand hierheen</p>
                  <p className="text-gray-600">of klik om een bestand te selecteren</p>
                  <Input
                    type="file"
                    accept=".csv,.txt,.mt940,.cod"
                    onChange={(e) => {
                      const uploadedFile = e.target.files?.[0];
                      if (uploadedFile) {
                        handleFileUpload(uploadedFile);
                      }
                    }}
                    className="hidden"
                    id="file-upload"
                  />
                  <Label
                    htmlFor="file-upload"
                    className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-md cursor-pointer hover:bg-blue-700"
                  >
                    Bestand selecteren
                  </Label>
                </div>
              </div>

              {loading && (
                <div className="text-center py-4">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="mt-2 text-gray-600">Bestand wordt verwerkt...</p>
                </div>
              )}
            </div>
          </TabsContent>

          <TabsContent value="preview" className="space-y-6">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-lg font-semibold">Gevonden transacties</h3>
                <p className="text-gray-600">
                  {matches.filter(m => m.match).length} van {matches.length} transacties gekoppeld
                </p>
              </div>
              <Button onClick={() => setStep("confirm")}>
                Volgende stap
              </Button>
            </div>

            <div className="border rounded-lg overflow-hidden">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Datum</TableHead>
                    <TableHead>Bedrag</TableHead>
                    <TableHead>Beschrijving</TableHead>
                    <TableHead>Gekoppeld aan</TableHead>
                    <TableHead>Betrouwbaarheid</TableHead>
                    <TableHead>Actie</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {matches.map((match, index) => (
                    <TableRow key={index}>
                      <TableCell>
                        {formatDateBE(match.transaction.date)}
                      </TableCell>
                      <TableCell className="font-medium">
                        {formatCurrencyBE(match.transaction.amount)}
                      </TableCell>
                      <TableCell className="max-w-xs truncate">
                        {match.transaction.description}
                      </TableCell>
                      <TableCell>
                        {match.match ? (
                          <div>
                            <p className="font-medium">
                              {match.match.memberFirstName} {match.match.memberLastName}
                            </p>
                            <p className="text-sm text-gray-600">
                              #{match.match.memberNumber} - {formatDateBE(match.match.periodStart)}
                            </p>
                          </div>
                        ) : (
                          <span className="text-gray-500">Geen koppeling</span>
                        )}
                      </TableCell>
                      <TableCell>
                        {getConfidenceBadge(match.confidence)}
                      </TableCell>
                      <TableCell>
