import { useState } from "react";
import { CalendarIcon } from "lucide-react";
import { format } from "date-fns";
import { nl } from "date-fns/locale";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Calendar } from "@/components/ui/calendar";
import { cn } from "@/lib/utils";
import { formatDateBE } from "../../../../../lib/period";

interface ConfirmDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onConfirm: (markAsPaid?: boolean, paidDate?: Date) => void;
  loading?: boolean;
}

export function ConfirmDialog({ open, onOpenChange, onConfirm, loading = false }: ConfirmDialogProps) {
  const [markAsPaid, setMarkAsPaid] = useState(false);
  const [paidDate, setPaidDate] = useState<Date>(new Date());
  const [paidDateInput, setPaidDateInput] = useState("");
  const [datePopoverOpen, setDatePopoverOpen] = useState(false);

  const formatDateInput = (value: string) => {
    const numbers = value.replace(/\D/g, '');
    if (numbers.length <= 2) {
      return numbers;
    } else if (numbers.length <= 4) {
      return `${numbers.slice(0, 2)}/${numbers.slice(2)}`;
    } else {
      return `${numbers.slice(0, 2)}/${numbers.slice(2, 4)}/${numbers.slice(4, 8)}`;
    }
  };

  const parseDate = (dateString: string): Date | undefined => {
    const cleanString = dateString.replace(/\D/g, '');
    if (cleanString.length === 8) {
      const day = parseInt(cleanString.slice(0, 2));
      const month = parseInt(cleanString.slice(2, 4)) - 1;
      const year = parseInt(cleanString.slice(4, 8));

      const date = new Date(year, month, day);
      if (date.getDate() === day && date.getMonth() === month && date.getFullYear() === year) {
        return date;
      }
    }
    return undefined;
  };

  const handlePaidDateInput = (value: string) => {
    const formatted = formatDateInput(value);
    setPaidDateInput(formatted);

    const parsedDate = parseDate(formatted);
    if (parsedDate) {
      setPaidDate(parsedDate);
    }
  };

  const handleConfirm = () => {
    onConfirm(markAsPaid, markAsPaid ? paidDate : undefined);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Lidgeld bevestigen</DialogTitle>
          <DialogDescription>
            Weet je zeker dat je dit lidgeld wilt aanmaken?
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="flex items-center space-x-2">
            <Checkbox
              id="mark-paid"
              checked={markAsPaid}
              onCheckedChange={(checked) => setMarkAsPaid(checked as boolean)}
            />
            <Label htmlFor="mark-paid" className="text-sm cursor-pointer">
              Markeer als betaald
            </Label>
          </div>

          {markAsPaid && (
            <div className="space-y-2">
              <Label className="text-sm font-medium">Betaaldatum</Label>
              <div className="relative">
                <Input
                  value={paidDateInput || formatDateBE(paidDate)}
                  onChange={(e) => handlePaidDateInput(e.target.value)}
                  placeholder="dd/mm/jjjj"
                  className="w-full h-9 border-gray-200 pr-10"
                  maxLength={10}
                />
                <Popover open={datePopoverOpen} onOpenChange={setDatePopoverOpen}>
                  <PopoverTrigger asChild>
                    <Button
                      variant="ghost"
                      size="sm"
                      className="absolute right-1 top-1/2 -translate-y-1/2 h-7 w-7 p-0 hover:bg-gray-100"
                      onClick={() => setDatePopoverOpen(true)}
                    >
                      <CalendarIcon className="h-4 w-4" />
                    </Button>
                  </PopoverTrigger>
                  <PopoverContent className="w-auto p-0" align="start">
                    <Calendar
                      mode="single"
                      selected={paidDate}
                      onSelect={(date) => {
                        if (date) {
                          setPaidDate(date);
                          setPaidDateInput("");
                          setDatePopoverOpen(false);
                        }
                      }}
                      locale={nl}
                      disabled={(date) => date > new Date()}
                      initialFocus
                    />
                  </PopoverContent>
                </Popover>
              </div>
              <p className="text-xs text-gray-500">
                Kies de datum waarop het lidgeld werd betaald
              </p>
            </div>
          )}
        </div>

        <div className="flex justify-end gap-2 pt-4">
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={loading}
          >
            Annuleren
          </Button>
          <Button
            onClick={handleConfirm}
            disabled={loading}
            data-testid="confirm-create-fee"
          >
            {loading ? "Bezig..." : "Lidgeld aanmaken"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/fees/new/member-se.content:
import { useState, useMemo } from "react";
import { X } from "lucide-react";
import { cn } from "@/lib/utils";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";

interface MemberSelectProps {
  value?: string;
  onChange: (memberId: string) => void;
  error?: string;
  members?: any[];
}

export function MemberSelect({ value, onChange, error, members = [] }: MemberSelectProps) {
  const [inputValue, setInputValue] = useState("");
  const [showSuggestions, setShowSuggestions] = useState(false);

  const selectedMember = value ? members.find((m: any) => m.id === value) : null;

  // Filter members based on input
  const filteredMembers = useMemo(() => {
    if (!inputValue.trim()) return members;

    const searchTerm = inputValue.toLowerCase();
    return members.filter((member: any) => 
      member.firstName.toLowerCase().includes(searchTerm) ||
      member.lastName.toLowerCase().includes(searchTerm) ||
      member.memberNumber.includes(searchTerm) ||
      `${member.firstName} ${member.lastName}`.toLowerCase().includes(searchTerm) ||
      `${member.lastName}, ${member.firstName}`.toLowerCase().includes(searchTerm)
    );
  }, [inputValue, members]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    setInputValue(newValue);
    setShowSuggestions(newValue.length > 0);

    // Clear selection if input is changed
    if (selectedMember && newValue !== `${selectedMember.lastName}, ${selectedMember.firstName}`) {
      onChange("");
    }
  };

  const handleMemberSelect = (member: any) => {
    onChange(member.id);
    setInputValue(`${member.lastName}, ${member.firstName}`);
    setShowSuggestions(false);
  };

  const handleClearSelection = () => {
    onChange("");
    setInputValue("");
    setShowSuggestions(false);
  };

  const displayValue = selectedMember 
    ? `${selectedMember.lastName}, ${selectedMember.firstName}`
    : inputValue;

  return (
    <div className="space-y-2 relative">
      <div className="relative">
        <Input
          value={displayValue}
          onChange={handleInputChange}
          onFocus={() => setShowSuggestions(inputValue.length > 0 || !selectedMember)}
          onBlur={() => {
            // Delay hiding suggestions to allow for clicks
            setTimeout(() => setShowSuggestions(false), 200);
          }}
          placeholder="Typ een naam of lidnummer..."
          className={cn(
            "w-full h-10 border-gray-200 pr-8",
            error && "border-red-500"
          )}
          data-testid="member-select"
        />
        {selectedMember && (
          <button
            type="button"
            onClick={handleClearSelection}
            className="absolute right-2 top-1/2 -translate-y-1/2 p-1 hover:bg-gray-100 rounded"
          >
            <X className="h-4 w-4 text-gray-400" />
          </button>
        )}
      </div>

      {/* Suggestions Dropdown */}
      {showSuggestions && !selectedMember && filteredMembers.length > 0 && (
        <div className="absolute top-full left-0 right-0 z-50 bg-white border border-gray-200 rounded-lg shadow-lg max-h-64 overflow-y-auto">
          {filteredMembers.slice(0, 10).map((member) => (
            <div
              key={member.id}
              onClick={() => handleMemberSelect(member)}
              className="flex items-center justify-between p-3 hover:bg-gray-50 cursor-pointer border-b border-gray-100 last:border-b-0"
            >
              <div className="flex flex-col">
                <span className="font-medium">
                  {member.lastName}, {member.firstName}
                </span>
                <span className="text-xs text-gray-500">
                  #{member.memberNumber} â€¢ {member.category}
                </span>
              </div>
              <Badge 
                variant={member.hasMandate ? "default" : "secondary"}
                className={cn(
                  "text-xs",
                  member.hasMandate 
                    ? "bg-green-100 text-green-800 border-green-200" 
                    : "bg-gray-100 text-gray-600 border-gray-200"
                )}
              >
                {member.hasMandate ? "MANDAAT" : "GEEN MANDAAT"}
              </Badge>
            </div>
          ))}
          {filteredMembers.length > 10 && (
            <div className="p-2 text-xs text-gray-500 text-center border-t">
              {filteredMembers.length - 10} meer resultaten... typ verder om te filteren
            </div>
          )}
        </div>
      )}

      {showSuggestions && !selectedMember && filteredMembers.length === 0 && inputValue.trim() && (
        <div className="absolute top-full left-0 right-0 z-50 bg-white border border-gray-200 rounded-lg shadow-lg p-3">
          <div className="text-sm text-gray-500 text-center">
            Geen leden gevonden voor "{inputValue}"
          </div>
        </div>
      )}

      {selectedMember && (
        <div className="flex items-center gap-2 mt-2">
          <Badge 
            variant={selectedMember.hasMandate ? "default" : "secondary"}
            className={cn(
              "text-xs",
              selectedMember.hasMandate 
                ? "bg-green-100 text-green-800 border-green-200" 
                : "bg-gray-100 text-gray-600 border-gray-200"
            )}
          >
            {selectedMember.hasMandate ? "SEPA mandaat: JA" : "SEPA mandaat: NEE"}
          </Badge>
          <span className="text-sm text-gray-600">
            Categorie: {selectedMember.category}
          </span>
        </div>
      )}

      {error && (
        <p className="text-sm text-red-600">{error}</p>
      )}
    </div>
  );
}

