import { cn } from "@/lib/utils";
import { FeeStatus } from "@shared/fees-schema";

interface StatusChipProps {
  status: FeeStatus;
  className?: string;
}

export function StatusChip({ status, className }: StatusChipProps) {
  const variants = {
    OPEN: "bg-blue-100 text-blue-800 border-blue-200",
    PAID: "bg-green-100 text-green-800 border-green-200", 
    OVERDUE: "bg-red-100 text-red-800 border-red-200",
  };

  const labels = {
    OPEN: "Openstaand",
    PAID: "Betaald",
    OVERDUE: "Vervallen",
  };

  return (
    <span
      className={cn(
        "inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border",
        variants[status],
        className
      )}
    >
      {labels[status]}
    </span>
  );
}

client/src/components/fees/toolbar.tsx.content:
import { useState } from "react";
import { Search, CreditCard, Plus, CalendarIcon } from "lucide-react";
import { CiExport, CiImport } from "react-icons/ci";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { format } from "date-fns";
import { nl } from "date-fns/locale";
import { cn } from "@/lib/utils";
import { RiResetLeftFill } from "react-icons/ri";

interface ToolbarProps {
  searchTerm: string;
  onSearchChange: (value: string) => void;
  statusFilter: string;
  onStatusFilterChange: (value: string) => void;
  yearFilter: string;
  onYearFilterChange: (value: string) => void;
  methodFilter: string;
  onMethodFilterChange: (value: string) => void;
  categoryFilter: string;
  onCategoryFilterChange: (value: string) => void;
  amountMin?: number;
  onAmountMinChange: (value: number | undefined) => void;
  amountMax?: number;
  onAmountMaxChange: (value: number | undefined) => void;
  periodFrom?: Date;
  onPeriodFromChange: (date: Date | undefined) => void;
  periodTo?: Date;
  onPeriodToChange: (date: Date | undefined) => void;
  onlyWithMandate: boolean;
  onOnlyWithMandateChange: (value: boolean) => void;
  onlyOverdue: boolean;
  onOnlyOverdueChange: (value: boolean) => void;
  onResetFilters: () => void;
  selectedCount: number;
  sepaSelectedCount: number;
  onExport: () => void;
  onImport: () => void;
  onGenerateSepa: () => void;
  onBulkMarkPaid: () => void;
  newButton?: React.ReactNode;
}

export function Toolbar({
  searchTerm,
  onSearchChange,
  statusFilter,
  onStatusFilterChange,
  yearFilter,
  onYearFilterChange,
  methodFilter,
  onMethodFilterChange,
  categoryFilter,
  onCategoryFilterChange,
  amountMin,
  onAmountMinChange,
  amountMax,
  onAmountMaxChange,
  periodFrom,
  onPeriodFromChange,
  periodTo,
  onPeriodToChange,
  onlyWithMandate,
  onOnlyWithMandateChange,
  onlyOverdue,
  onOnlyOverdueChange,
  onResetFilters,
  selectedCount,
  sepaSelectedCount,
  onExport,
  onImport,
  onGenerateSepa,
  onBulkMarkPaid,
  newButton,
}: ToolbarProps) {
  const [fromDateInput, setFromDateInput] = useState("");
  const [toDateInput, setToDateInput] = useState("");
  const [fromPopoverOpen, setFromPopoverOpen] = useState(false);
  const [toPopoverOpen, setToPopoverOpen] = useState(false);

  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 5 }, (_, i) => currentYear - i);

  const formatDateInput = (value: string) => {
    // Remove all non-numeric characters
    const numbers = value.replace(/\D/g, '');

    // Apply DD/MM/YYYY formatting
    if (numbers.length <= 2) {
      return numbers;
    } else if (numbers.length <= 4) {
      return `${numbers.slice(0, 2)}/${numbers.slice(2)}`;
    } else {
      return `${numbers.slice(0, 2)}/${numbers.slice(2, 4)}/${numbers.slice(4, 8)}`;
    }
  };

  const parseDate = (dateString: string): Date | undefined => {
    const cleanString = dateString.replace(/\D/g, '');
    if (cleanString.length === 8) {
      const day = parseInt(cleanString.slice(0, 2));
      const month = parseInt(cleanString.slice(2, 4)) - 1; // Month is 0-indexed
      const year = parseInt(cleanString.slice(4, 8));

      const date = new Date(year, month, day);
      if (date.getDate() === day && date.getMonth() === month && date.getFullYear() === year) {
        return date;
      }
    }
    return undefined;
  };

  const handleFromDateInput = (value: string) => {
    const formatted = formatDateInput(value);
    setFromDateInput(formatted);

    const parsedDate = parseDate(formatted);
    if (parsedDate) {
      onPeriodFromChange(parsedDate);
    }
  };

  const handleToDateInput = (value: string) => {
    const formatted = formatDateInput(value);
    setToDateInput(formatted);

    const parsedDate = parseDate(formatted);
    if (parsedDate) {
      onPeriodToChange(parsedDate);
    }
  };

  return (
    <Card className="glass-card card-hover animate-fade-in group mb-6">
      <CardContent className="p-6">
        <div className="space-y-4">
          {/* Search and Action Buttons */}
          <div className="flex flex-col lg:flex-row gap-4">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <Input
                placeholder="Zoek op lidnummer, naam of periode..."
                value={searchTerm}
                onChange={(e) => onSearchChange(e.target.value)}
                className="pl-10 h-10 border-gray-200 focus:border-blue-500 focus:ring-blue-500/20"
                data-testid="search-fees"
              />
            </div>

            <div className="flex gap-2 lg:flex-shrink-0">
              <Button 
                variant="outline" 
                onClick={onExport}
                className="h-10 px-4 border-gray-200 hover:border-gray-300"
                data-testid="export-button"
              >
                <CiExport className="h-4 w-4 mr-2" />
                Export
              </Button>
              <Button 
                variant="outline" 
                onClick={onImport}
                className="h-10 px-4 border-gray-200 hover:border-gray-300"
                data-testid="import-button"
              >
                <CiImport className="h-4 w-4 mr-2" />
                Import
              </Button>
              {newButton && newButton}
            </div>
          </div>

          {/* All Filters */}
          <div className="flex flex-col gap-4">
            <div className="flex flex-wrap gap-3">
              <Select value={statusFilter} onValueChange={onStatusFilterChange}>
                <SelectTrigger className="w-[160px] h-9 border-gray-200" data-testid="status-filter">
                  <SelectValue placeholder="Alle statussen" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Alle statussen</SelectItem>
                  <SelectItem value="OPEN">Openstaand</SelectItem>
                  <SelectItem value="PAID">Betaald</SelectItem>
                  <SelectItem value="OVERDUE">Vervallen</SelectItem>
                </SelectContent>
              </Select>

              <Select value={yearFilter} onValueChange={onYearFilterChange}>
                <SelectTrigger className="w-[120px] h-9 border-gray-200" data-testid="year-filter">
                  <SelectValue placeholder="Alle jaren" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Alle jaren</SelectItem>
                  {years.map(year => (
                    <SelectItem key={year} value={year.toString()}>
                      {year}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={methodFilter} onValueChange={onMethodFilterChange}>
                <SelectTrigger className="w-[170px] h-9 border-gray-200" data-testid="method-filter">
                  <SelectValue placeholder="Alle methodes" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Alle methodes</SelectItem>
                  <SelectItem value="SEPA">SEPA</SelectItem>
                  <SelectItem value="OVERSCHRIJVING">Overschrijving</SelectItem>
                  <SelectItem value="BANCONTACT">Bancontact</SelectItem>
                  <SelectItem value="CASH">Contant</SelectItem>
                </SelectContent>
              </Select>

              <Select value={categoryFilter} onValueChange={onCategoryFilterChange}>
                <SelectTrigger className="w-[170px] h-9 border-gray-200" data-testid="category-filter">
                  <SelectValue placeholder="Alle categorieÃ«n" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Alle categorieÃ«n</SelectItem>
                  <SelectItem value="STUDENT">Student</SelectItem>
                  <SelectItem value="STANDAARD">Standaard</SelectItem>
                  <SelectItem value="SENIOR">Senior</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Second row of filters */}
            <div className="flex flex-wrap gap-3 items-center">
              <div className="flex items-center gap-2">
                <span className="text-sm text-gray-600">Bedrag:</span>
                <Input
                  type="number"
                  placeholder="Min â‚¬"
                  value={amountMin || ""}
                  onChange={(e) => onAmountMinChange(e.target.value ? parseFloat(e.target.value) : undefined)}
                  className="w-[100px] h-9 border-gray-200"
                  data-testid="amount-min"
                />
                <span className="text-sm text-gray-400">tot</span>
                <Input
                  type="number"
                  placeholder="Max â‚¬"
                  value={amountMax || ""}
                  onChange={(e) => onAmountMaxChange(e.target.value ? parseFloat(e.target.value) : undefined)}
                  className="w-[100px] h-9 border-gray-200"
                  data-testid="amount-max"
                />
              </div>

              <div className="flex items-center gap-2">
                <span className="text-sm text-gray-600">Periode:</span>

                <div className="relative">
                  <Input
                    value={fromDateInput || (periodFrom ? format(periodFrom, "dd/MM/yyyy", { locale: nl }) : "")}
                    onChange={(e) => handleFromDateInput(e.target.value)}
                    placeholder="DD/MM/YYYY"
                    className="w-[160px] h-9 border-gray-200 pr-10"
                    data-testid="paid-date-from"
                    maxLength={10}
                  />
                  <Popover open={fromPopoverOpen} onOpenChange={setFromPopoverOpen}>
                    <PopoverTrigger asChild>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="absolute right-1 top-1/2 -translate-y-1/2 h-7 w-7 p-0 hover:bg-gray-100"
                        onClick={() => setFromPopoverOpen(true)}
                      >
                        <CalendarIcon className="h-4 w-4" />
                      </Button>
                    </PopoverTrigger>
                    <PopoverContent className="w-auto p-0" align="start" side="bottom">
                      <div className="p-3 border-b">
                        <div className="flex items-center justify-between space-x-2">
                          <Select
                            value={periodFrom ? periodFrom.getMonth().toString() : ""}
                            onValueChange={(month) => {
                              const currentDate = periodFrom || new Date();
                              const newDate = new Date(currentDate.getFullYear(), parseInt(month), currentDate.getDate());
                              onPeriodFromChange(newDate);
                              setFromDateInput("");
                            }}
                          >
                            <SelectTrigger className="w-[120px] h-8">
                              <SelectValue placeholder="Maand" />
                            </SelectTrigger>
                            <SelectContent>
                              {Array.from({ length: 12 }, (_, i) => (
                                <SelectItem key={i} value={i.toString()}>
                                  {format(new Date(2000, i, 1), "MMMM", { locale: nl })}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <Select
                            value={periodFrom ? periodFrom.getFullYear().toString() : ""}
                            onValueChange={(year) => {
                              const currentDate = periodFrom || new Date();
                              const newDate = new Date(parseInt(year), currentDate.getMonth(), currentDate.getDate());
                              onPeriodFromChange(newDate);
                              setFromDateInput("");
                            }}
                          >
                            <SelectTrigger className="w-[80px] h-8">
                              <SelectValue placeholder="Jaar" />
                            </SelectTrigger>
                            <SelectContent>
                              {Array.from({ length: 10 }, (_, i) => {
                                const year = new Date().getFullYear() - i;
                                return (
                                  <SelectItem key={year} value={year.toString()}>
                                    {year}
                                  </SelectItem>
                                );
                              })}
                            </SelectContent>
                          </Select>
                        </div>
                      </div>
                      <Calendar
                        mode="single"
                        selected={periodFrom}
                        onSelect={(date) => {
                          console.log('Calendar from date selected:', date);
                          onPeriodFromChange(date);
                          setFromDateInput("");
                          setFromPopoverOpen(false);
                        }}
                        locale={nl}
                        month={periodFrom || new Date()}
                        onMonthChange={(month) => onPeriodFromChange(month)}
                        showOutsideDays={false}
                        className="p-3"
                        defaultMonth={new Date()}
                      />
                    </PopoverContent>
                  </Popover>
                </div>

                <span className="text-sm text-gray-400">tot</span>

                <div className="relative">
                  <Input
                    value={toDateInput || (periodTo ? format(periodTo, "dd/MM/yyyy", { locale: nl }) : "")}
                    onChange={(e) => handleToDateInput(e.target.value)}
                    placeholder="DD/MM/YYYY"
                    className="w-[160px] h-9 border-gray-200 pr-10"
                    data-testid="paid-date-to"
                    maxLength={10}
                  />
                  <Popover open={toPopoverOpen} onOpenChange={setToPopoverOpen}>
                    <PopoverTrigger asChild>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="absolute right-1 top-1/2 -translate-y-1/2 h-7 w-7 p-0 hover:bg-gray-100"
                        onClick={() => setToPopoverOpen(true)}
                      >
                        <CalendarIcon className="h-4 w-4" />
                      </Button>
                    </PopoverTrigger>
                    <PopoverContent className="w-auto p-0" align="start" side="bottom">
                      <div className="p-3 border-b">
                        <div className="flex items-center justify-between space-x-2">
                          <Select
                            value={periodTo ? periodTo.getMonth().toString() : ""}
                            onValueChange={(month) => {
                              const currentDate = periodTo || new Date();
                              const newDate = new Date(currentDate.getFullYear(), parseInt(month), currentDate.getDate());
                              onPeriodToChange(newDate);
                              setToDateInput("");
                            }}
                          >
                            <SelectTrigger className="w-[120px] h-8">
                              <SelectValue placeholder="Maand" />
                            </SelectTrigger>
                            <SelectContent>
                              {Array.from({ length: 12 }, (_, i) => (
                                <SelectItem key={i} value={i.toString()}>
                                  {format(new Date(2000, i, 1), "MMMM", { locale: nl })}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <Select
                            value={periodTo ? periodTo.getFullYear().toString() : ""}
                            onValueChange={(year) => {
                              const currentDate = periodTo || new Date();
                              const newDate = new Date(parseInt(year), currentDate.getMonth(), currentDate.getDate());
                              onPeriodToChange(newDate);
                              setToDateInput("");
                            }}
                          >
                            <SelectTrigger className="w-[80px] h-8">
                              <SelectValue placeholder="Jaar" />
                            </SelectTrigger>
                            <SelectContent>
                              {Array.from({ length: 10 }, (_, i) => {
                                const year = new Date().getFullYear() - i;
                                return (
                                  <SelectItem key={year} value={year.toString()}>
                                    {year}
                                  </SelectItem>
                                );
                              })}
                            </SelectContent>
                          </Select>
                        </div>
                      </div>
                      <Calendar
                        mode="single"
                        selected={periodTo}
                        onSelect={(date) => {
                          onPeriodToChange(date);
                          setToDateInput("");
                          setToPopoverOpen(false);
                        }}
                        locale={nl}
                        month={periodTo || new Date()}
                        onMonthChange={(month) => onPeriodToChange(month)}
                        disabled={(date) => periodFrom ? date < periodFrom : false}
                        showOutsideDays={false}
                        className="p-3"
                        defaultMonth={new Date()}
                      />
                    </PopoverContent>
                  </Popover>
                </div>
              </div>


              <Button 
                variant="outline" 
                size="sm"
                onClick={onResetFilters}
                className="h-9 px-3 border-gray-200 hover:border-gray-300"
                data-testid="reset-filters-button"
              >
                <RiResetLeftFill className="h-4 w-4 mr-2" />
                Reset filters
              </Button>
            </div>

            {/* Bulk Actions */}
            {selectedCount > 0 && (
              <div className="flex items-center gap-3 p-3 bg-blue-50 rounded-lg border border-blue-200">
                <span className="text-sm text-blue-700 font-medium">
                  {selectedCount} geselecteerd
                </span>
                <div className="flex gap-2">
                  <Button 
                    onClick={onBulkMarkPaid}
                    size="sm"
                    className="h-8"
                    data-testid="bulk-mark-paid"
                  >
                    Markeer betaald
                  </Button>
                  {sepaSelectedCount > 0 && (
                    <Button 
                      onClick={onGenerateSepa}
                      size="sm"
                      variant="outline"
                      className="h-8 flex items-center gap-1.5"
                      data-testid="generate-sepa"
                    >
                      <CreditCard className="h-3 w-3" />
                      Genereer SEPA ({sepaSelectedCount})
                    </Button>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/members/filters-dr.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Sheet, 
  SheetContent, 
  SheetDescription, 
  SheetHeader, 
  SheetTitle, 
  SheetFooter,
  SheetClose
} from "@/components/ui/sheet";
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue
} from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";

interface FilterValues {
  categories: string[];
  statuses: string[];
  genders: string[];
  joinDateFrom: string;
  joinDateTo: string;
  paymentStatuses: string[];
  onlyActivePayments: boolean;
}

interface FiltersDrawerProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  filters: FilterValues;
  onFiltersChange: (filters: FilterValues) => void;
}

const initialFilters: FilterValues = {
  categories: [],
  statuses: [],
  genders: [],
  joinDateFrom: "",
  joinDateTo: "",
  paymentStatuses: [],
  onlyActivePayments: false,
};

export function FiltersDrawer({ 
  open, 
  onOpenChange, 
  filters, 
  onFiltersChange 
}: FiltersDrawerProps) {
  const [localFilters, setLocalFilters] = useState<FilterValues>(filters);

  const updateLocalFilter = (key: keyof FilterValues, value: any) => {
    setLocalFilters(prev => ({ ...prev, [key]: value }));
  };

  const handleCategoryChange = (category: string, checked: boolean) => {
    const newCategories = checked 
      ? [...localFilters.categories, category]
      : localFilters.categories.filter(c => c !== category);
    updateLocalFilter('categories', newCategories);
  };

  const handleStatusChange = (status: string, checked: boolean) => {
    const newStatuses = checked
      ? [...localFilters.statuses, status]
      : localFilters.statuses.filter(s => s !== status);
    updateLocalFilter('statuses', newStatuses);
  };

  const handleGenderChange = (gender: string, checked: boolean) => {
    const newGenders = checked
      ? [...localFilters.genders, gender]
      : localFilters.genders.filter(g => g !== gender);
    updateLocalFilter('genders', newGenders);
  };

  const handlePaymentStatusChange = (status: string, checked: boolean) => {
    const newPaymentStatuses = checked
      ? [...localFilters.paymentStatuses, status]
      : localFilters.paymentStatuses.filter(s => s !== status);
    updateLocalFilter('paymentStatuses', newPaymentStatuses);
  };

  const handleApply = () => {
    onFiltersChange(localFilters);
    onOpenChange(false);
  };

  const handleReset = () => {
    setLocalFilters(initialFilters);
    onFiltersChange(initialFilters);
    onOpenChange(false);
  };

  const hasActiveFilters = Object.values(localFilters).some(value => 
    Array.isArray(value) ? value.length > 0 : value !== "" && value !== false
  );

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent className="sm:max-w-md overflow-y-auto">
        <SheetHeader>
          <SheetTitle>Meer filters</SheetTitle>
          <SheetDescription>
            Verfijn je zoekopdracht met geavanceerde filteropties
          </SheetDescription>
        </SheetHeader>

        <div className="py-6 space-y-6">
          {/* Categorie Filter */}
          <div className="space-y-3">
            <Label className="text-sm font-medium text-gray-900">Categorie</Label>
            <Card>
              <CardContent className="p-4 space-y-3">
                {[
                  { value: 'SENIOR', label: 'Senior' },
                  { value: 'STANDAARD', label: 'Standaard' },
                  { value: 'STUDENT', label: 'Student' },
                  { value: 'JEUGD', label: 'Jeugd' }
                ].map((category) => (
                  <div key={category.value} className="flex items-center space-x-2">
                    <Checkbox
                      id={`category-${category.value}`}
                      checked={localFilters.categories.includes(category.value)}
                      onCheckedChange={(checked) => handleCategoryChange(category.value, !!checked)}
                    />
                    <Label 
                      htmlFor={`category-${category.value}`}
                      className="text-sm font-normal cursor-pointer"
                    >
                      {category.label}
                    </Label>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>

          {/* Status Filter */}
          <div className="space-y-3">
            <Label className="text-sm font-medium text-gray-900">Status</Label>
            <Card>
              <CardContent className="p-4 space-y-3">
                {[
                  { value: 'ACTIEF', label: 'Actief' },
                  { value: 'INACTIEF', label: 'Inactief' }
                ].map((status) => (
                  <div key={status.value} className="flex items-center space-x-2">
                    <Checkbox
                      id={`status-${status.value}`}
                      checked={localFilters.statuses.includes(status.value)}
                      onCheckedChange={(checked) => handleStatusChange(status.value, !!checked)}
                    />
                    <Label 
                      htmlFor={`status-${status.value}`}
                      className="text-sm font-normal cursor-pointer"
                    >
                      {status.label}
                    </Label>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>

          {/* Geslacht Filter */}
          <div className="space-y-3">
            <Label className="text-sm font-medium text-gray-900">Geslacht</Label>
            <Card>
              <CardContent className="p-4 space-y-3">
                {[
                  { value: 'M', label: 'Man' },
                  { value: 'V', label: 'Vrouw' }
                ].map((gender) => (
                  <div key={gender.value} className="flex items-center space-x-2">
                    <Checkbox
                      id={`gender-${gender.value}`}
                      checked={localFilters.genders.includes(gender.value)}
                      onCheckedChange={(checked) => handleGenderChange(gender.value, !!checked)}
                    />
                    <Label 
                      htmlFor={`gender-${gender.value}`}
                      className="text-sm font-normal cursor-pointer"
                    >
                      {gender.label}
                    </Label>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>

          {/* Inschrijvingsdatum */}
          <div className="space-y-3">
            <Label className="text-sm font-medium text-gray-900">Inschrijvingsdatum</Label>
            <Card>
              <CardContent className="p-4 space-y-3">
                <div>
                  <Label htmlFor="join-date-from" className="text-xs text-gray-500">Van</Label>
                  <Input
                    id="join-date-from"
                    type="date"
                    value={localFilters.joinDateFrom}
                    onChange={(e) => updateLocalFilter('joinDateFrom', e.target.value)}
                    className="mt-1"
                  />
                </div>
                <div>
                  <Label htmlFor="join-date-to" className="text-xs text-gray-500">Tot</Label>
                  <Input
                    id="join-date-to"
                    type="date"
                    value={localFilters.joinDateTo}
                    onChange={(e) => updateLocalFilter('joinDateTo', e.target.value)}
                    className="mt-1"
                  />
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Betaalstatus */}
          <div className="space-y-3">
            <Label className="text-sm font-medium text-gray-900">Betaalstatus</Label>
            <Card>
              <CardContent className="p-4 space-y-3">
                {[
                  { value: 'PAID', label: 'Betaald' },
                  { value: 'OPEN', label: 'Open' },
                  { value: 'OVERDUE', label: 'Vervallen' }
                ].map((status) => (
                  <div key={status.value} className="flex items-center space-x-2">
                    <Checkbox
                      id={`payment-${status.value}`}
                      checked={localFilters.paymentStatuses.includes(status.value)}
                      onCheckedChange={(checked) => handlePaymentStatusChange(status.value, !!checked)}
                    />
                    <Label 
                      htmlFor={`payment-${status.value}`}
                      className="text-sm font-normal cursor-pointer"
                    >
                      {status.label}
                    </Label>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>

          {/* Alleen actieve mandaten */}
          <div className="space-y-3">
            <Card>
              <CardContent className="p-4">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="active-payments-only"
                    checked={localFilters.onlyActivePayments}
                    onCheckedChange={(checked) => updateLocalFilter('onlyActivePayments', !!checked)}
                  />
                  <Label 
                    htmlFor="active-payments-only"
                    className="text-sm font-normal cursor-pointer"
                  >
                    Alleen actieve mandaten
                  </Label>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>

        <SheetFooter className="gap-2 pt-6 border-t">
          <Button 
            variant="outline" 
            onClick={handleReset}
            disabled={!hasActiveFilters}
            data-testid="reset-filters"
          >
            Reset
          </Button>
          <Button 
            onClick={handleApply}
            className="bg-blue-600 hover:bg-blue-700 text-white flex-1"
            data-testid="apply-filters"
          >
            Toepassen
          </Button>
        </SheetFooter>
      </SheetContent>
    </Sheet>
  );
}

client/src/components/members/members-ta.content:
import { useState } from "react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { StatusBadge } from "@/components/ui/status-badge";
import { RowActions } from "./row-actions";
import { ChevronUp, ChevronDown, Users, UserX, Mail, Calendar, User, MoreVertical } from "lucide-react";
import { BoardMemberBadge } from "@/components/ui/board-member-badge";
import { CiExport } from "react-icons/ci";
import { formatDate, getMemberCategoryLabel } from "@/lib/format";
import { cn } from "@/lib/utils";
import { VotingStatus } from "@/components/members/voting-status";
import { Member as MemberSchema } from "@shared/schema";
import { useIsMobile } from "@/hooks/use-mobile";

interface Member {
  id: string;
  memberNumber: string;
  firstName: string;
  lastName: string;
  email?: string;
  category: string;
  active: boolean;
  createdAt: string;
  gender?: string;
  birthDate?: string;
  votingRights: boolean;
}

interface SortConfig {
  key: keyof Member | null;
  direction: 'asc' | 'desc';
}

interface MembersTableProps {
  members: Member[];
  total: number;
  page: number;
  perPage: number;
  onPageChange: (page: number) => void;
  onPerPageChange: (perPage: number) => void;
  onRowAction: (action: string, memberId: string) => void;
  onBulkAction: (action: string, memberIds: string[]) => void;
  onView?: (id: string) => void;
  onEdit?: (id: string) => void;
  onToggleStatus?: (id: string, currentStatus: boolean) => void;
  onDelete?: (id: string) => void;
  loading?: boolean;
}

export function MembersTable({
  members,
  total,
  page,
  perPage,
  onPageChange,
  onPerPageChange,
  onRowAction,
  onBulkAction,
  onView,
  onEdit,
  onToggleStatus,
  onDelete,
  loading = false
}: MembersTableProps) {
  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const [sortConfig, setSortConfig] = useState<SortConfig>({ key: null, direction: 'asc' });
  const isMobile = useIsMobile();

  const handleSort = (key: keyof Member) => {
    const direction = sortConfig.key === key && sortConfig.direction === 'asc' ? 'desc' : 'asc';
    setSortConfig({ key, direction });
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedIds(members.map(m => m.id));
    } else {
      setSelectedIds([]);
    }
  };

  const handleSelectRow = (memberId: string, checked: boolean) => {
    if (checked) {
      setSelectedIds([...selectedIds, memberId]);
    } else {
      setSelectedIds(selectedIds.filter(id => id !== memberId));
    }
  };

  const handleBulkExport = () => {
    onBulkAction('export', selectedIds);
    setSelectedIds([]);
  };

  const handleBulkDeactivate = () => {
    onBulkAction('deactivate', selectedIds);
    setSelectedIds([]);
  };

  const getSortIcon = (key: keyof Member) => {
    if (sortConfig.key !== key) return null;
    return sortConfig.direction === 'asc' ? 
      <ChevronUp className="h-4 w-4" /> : 
      <ChevronDown className="h-4 w-4" />;
  };

  const totalPages = Math.ceil(total / perPage);
  const startItem = (page - 1) * perPage + 1;
  const endItem = Math.min(page * perPage, total);

  if (loading) {
    return <SkeletonTable />;
  }

  if (isMobile) {
    return (
      <div className="space-y-4">
        {/* Mobile Header */}
        <Card className="glass-card border-0">
          <CardHeader className="px-4 py-3">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-lg font-semibold text-gray-900">Ledenlijst</h3>
                <p className="text-sm text-gray-500">
                  {startItem}-{endItem} van {total} leden
                </p>
              </div>
              <div className="flex items-center gap-2">
                <span className="text-xs text-gray-500">Per pagina:</span>
                <Select value={perPage.toString()} onValueChange={(value) => onPerPageChange(Number(value))}>
                  <SelectTrigger className="w-16 h-8 text-xs">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="10">10</SelectItem>
                    <SelectItem value="20">20</SelectItem>
                    <SelectItem value="50">50</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardHeader>
        </Card>

        {/* Mobile Bulk Actions */}
        {selectedIds.length > 0 && (
          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium text-blue-900">
                  {selectedIds.length} geselecteerd
                </span>
                <div className="flex gap-2">
                  <Button 
                    size="sm" 
                    variant="outline"
                    onClick={handleBulkExport}
                    className="border-blue-200 text-blue-700 hover:bg-blue-100"
                  >
                    <CiExport className="h-4 w-4" />
                  </Button>
                  <Button 
                    size="sm" 
                    variant="outline"
                    onClick={handleBulkDeactivate}
                    className="border-red-200 text-red-700 hover:bg-red-100"
                  >
                    <UserX className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Mobile Member Cards */}
        <div className="space-y-3">
          {members.map((member) => (
            <Card key={member.id} className="glass-card border-0 overflow-hidden">
              <CardContent className="p-4">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center gap-3">
                    <Checkbox
                      checked={selectedIds.includes(member.id)}
                      onCheckedChange={(checked) => handleSelectRow(member.id, checked as boolean)}
                      className="mt-1"
                    />
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <h4 className="font-semibold text-gray-900 text-sm">
                          {member.firstName} {member.lastName}
                        </h4>
                        <BoardMemberBadge memberId={member.id} size="sm" />
                      </div>
                      <p className="text-xs text-gray-500">#{member.memberNumber}</p>
                    </div>
                  </div>
                  <RowActions
                    member={member}
                    onView={onView || (() => {})}
                    onEdit={onEdit || (() => {})}
                    onToggleStatus={onToggleStatus || (() => {})}
                    onDelete={onDelete || (() => {})}
                  />
                </div>

                <div className="grid grid-cols-2 gap-3 text-xs">
                  <div className="flex items-center gap-2 text-gray-600">
                    <Mail className="h-3 w-3" />
                    <span className="truncate">{member.email || 'Geen email'}</span>
                  </div>
                  <div className="flex items-center gap-2 text-gray-600">
                    <Calendar className="h-3 w-3" />
                    <span>{formatDate(member.createdAt)}</span>
                  </div>
                </div>

                <div className="flex items-center justify-between mt-3">
                  <div className="flex items-center gap-2">
                    <StatusBadge status={member.active ? 'ACTIEF' : 'INACTIEF'} />
                    <Badge variant="outline" className="text-xs">
                      {getMemberCategoryLabel(member.category)}
                    </Badge>
                  </div>
                  <VotingStatus member={member as any} />
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Mobile Pagination */}
        <Card className="glass-card border-0">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <Button
                onClick={() => onPageChange(page - 1)}
                disabled={page <= 1}
                variant="outline"
                size="sm"
              >
                Vorige
              </Button>
              <span className="text-sm text-gray-600">
                Pagina {page} van {totalPages}
              </span>
              <Button
                onClick={() => onPageChange(page + 1)}
                disabled={page >= totalPages}
                variant="outline"
                size="sm"
              >
                Volgende
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <Card className="glass-card card-hover animate-fade-in group border-0">
      {selectedIds.length > 0 && (
        <div className="px-6 py-4 bg-blue-50 border-b border-blue-200">
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium text-blue-900">
              {selectedIds.length} geselecteerd
            </span>
            <div className="flex gap-2">
              <Button 
                size="sm" 
                variant="outline"
                onClick={handleBulkExport}
                className="border-blue-200 text-blue-700 hover:bg-blue-100"
                data-testid="bulk-export"
              >
                <CiExport className="h-4 w-4 mr-2" />
                Export selectie
              </Button>
              <Button 
                size="sm" 
                variant="outline"
                onClick={handleBulkDeactivate}
                className="border-red-200 text-red-700 hover:bg-red-100"
                data-testid="bulk-deactivate"
              >
                <UserX className="h-4 w-4 mr-2" />
                Markeer inactief
              </Button>
            </div>
          </div>
        </div>
      )}

      <CardHeader className="px-6 py-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-gray-900">Ledenlijst</h3>
            <p className="text-sm text-gray-500">
              {startItem}-{endItem} van {total} leden
            </p>
          </div>

          <div className="hidden sm:flex items-center gap-4">
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-500">Per pagina:</span>
              <Select value={perPage.toString()} onValueChange={(value) => onPerPageChange(Number(value))}>
                <SelectTrigger className="w-20 h-8" data-testid="per-page-select">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="10">10</SelectItem>
                  <SelectItem value="20">20</SelectItem>
                  <SelectItem value="50">50</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-0">
        {members.length === 0 ? (
          <EmptyState />
        ) : (
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead className="w-12">
                <Checkbox
                  checked={selectedIds.length === members.length && members.length > 0}
                  onCheckedChange={handleSelectAll}
                  aria-label="Selecteer alle leden"
                />
              </TableHead>
              <TableHead 
                className="cursor-pointer hover:bg-[#f9fafb]"
                onClick={() => handleSort('memberNumber')}
              >
                <div className="flex items-center gap-2">
                  Lidnummer
                  {getSortIcon('memberNumber')}
                </div>
              </TableHead>
              <TableHead 
                className="cursor-pointer hover:bg-[#f9fafb]"
                onClick={() => handleSort('lastName')}
              >
                <div className="flex items-center gap-2">
                  Naam
                  {getSortIcon('lastName')}
                </div>
              </TableHead>
              <TableHead className="hidden lg:table-cell">
                Voornaam
              </TableHead>
              <TableHead>
                Status
              </TableHead>
              <TableHead className="hidden md:table-cell">
                Categorie
              </TableHead>
              <TableHead 
                className="hidden lg:table-cell cursor-pointer hover:bg-[#f9fafb]"
                onClick={() => handleSort('createdAt')}
              >
                <div className="flex items-center gap-2">
                  Inschrijvingsdatum
                  {getSortIcon('createdAt')}
                </div>
              </TableHead>
              <TableHead className="text-right">
                Acties
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {members.map((member) => (
              <TableRow 
                key={member.id} 
                className={cn(
                  "hover:bg-gray-50 transition-colors",
                  selectedIds.includes(member.id) && "bg-blue-50"
                )}
                data-testid={`member-row-${member.id}`}
              >
                <TableCell>
                  <Checkbox
                    checked={selectedIds.includes(member.id)}
                    onCheckedChange={(checked) => handleSelectRow(member.id, !!checked)}
                    aria-label={`Selecteer ${member.firstName} ${member.lastName}`}
                  />
                </TableCell>
                <TableCell className="font-mono">
                  {member.memberNumber}
                </TableCell>
                <TableCell>
                  <div className="font-medium flex items-center gap-2">
                    {member.lastName}
                    <BoardMemberBadge memberId={member.id} size="sm" />
                  </div>
                  <div className="text-sm text-gray-500 lg:hidden">
                    {member.firstName}
                  </div>
                </TableCell>
                <TableCell className="hidden lg:table-cell">
                  {member.firstName}
                </TableCell>
                <TableCell>
                  <StatusBadge status={member.active ? 'ACTIEF' : 'INACTIEF'} />
                </TableCell>
                <TableCell className="hidden md:table-cell">
                  <Badge variant="secondary" className="text-xs">
                    {getMemberCategoryLabel(member.category)}
                  </Badge>
                </TableCell>
                <TableCell className="hidden lg:table-cell text-gray-500">
                  {formatDate(member.createdAt)}
                </TableCell>
                <TableCell className="text-right">
                  <RowActions
                    memberId={member.id}
                    memberName={`${member.firstName} ${member.lastName}`}
                    isActive={member.active}
                    onView={onView || ((id) => onRowAction('view', id))}
                    onEdit={onEdit || ((id) => onRowAction('edit', id))}
                    onToggleStatus={onToggleStatus || ((id, currentStatus) => onRowAction('toggleStatus', id))}
                    onDelete={onDelete || ((id) => onRowAction('delete', id))}
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        )}
      </CardContent>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="px-6 py-4 border-t border-gray-200">
          <div className="flex items-center justify-center">
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onPageChange(page - 1)}
                disabled={page === 1}
                data-testid="prev-page"
              >
                Vorige
              </Button>

              {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                const pageNum = i + 1;
                return (
                  <Button
                    key={pageNum}
                    variant={page === pageNum ? "default" : "outline"}
                    size="sm"
                    onClick={() => onPageChange(pageNum)}
                    className={page === pageNum ? "bg-blue-600 hover:bg-blue-700" : ""}
                    data-testid={`page-${pageNum}`}
                  >
                    {pageNum}
                  </Button>
                );
              })}

              <Button
                variant="outline"
                size="sm"
                onClick={() => onPageChange(page + 1)}
                disabled={page === totalPages}
                data-testid="next-page"
              >
                Volgende
              </Button>
            </div>
          </div>
        </div>
      )}
    </Card>
  );
}

function SkeletonTable() {
  return (
    <Card className="shadow-sm border border-gray-200">
      <CardHeader className="px-6 py-4 border-b border-gray-200">
        <div className="animate-pulse">
          <div className="h-6 bg-gray-200 rounded w-1/4 mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-1/6"></div>
        </div>
      </CardHeader>
      <CardContent className="p-0">
        <div className="space-y-0">
          {[...Array(5)].map((_, i) => (
            <div key={i} className="px-6 py-4 border-b border-gray-200 animate-pulse">
              <div className="flex items-center space-x-4">
                <div className="w-4 h-4 bg-gray-200 rounded"></div>
                <div className="w-16 h-4 bg-gray-200 rounded"></div>
                <div className="flex items-center space-x-3">
                  <div className="w-8 h-8 bg-gray-200 rounded-full"></div>
                  <div className="space-y-1">
                    <div className="w-24 h-4 bg-gray-200 rounded"></div>
                    <div className="w-16 h-3 bg-gray-200 rounded"></div>
                  </div>
                </div>
                <div className="w-16 h-4 bg-gray-200 rounded"></div>
                <div className="w-12 h-6 bg-gray-200 rounded-full"></div>
                <div className="w-16 h-6 bg-gray-200 rounded-full"></div>
                <div className="w-20 h-4 bg-gray-200 rounded"></div>
                <div className="w-8 h-4 bg-gray-200 rounded"></div>
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

function EmptyState() {
  return (
    <div className="flex flex-col items-center justify-center py-12 px-4 text-center">
      <div className="flex items-center justify-center w-16 h-16 bg-gray-100 dark:bg-gray-800 rounded-full mb-4">
        <Users className="w-8 h-8 text-gray-400" />
      </div>
      <h3 className="text-lg font-medium text-gray-900 dark:text-gray-100 mb-2">
        Geen leden gevonden
      </h3>
      <p className="text-gray-500 dark:text-gray-400 mb-6 max-w-sm">
        Voeg je eerste lid toe om te beginnen met het beheren van je ledenbestand.
      </p>
    </div>
  );
}

client/src/components/members/row-action.content:
import { Button } from "@/components/ui/button";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuTrigger,
  DropdownMenuSeparator 
} from "@/components/ui/dropdown-menu";
import { 
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { MoreVertical, Eye, Edit2, UserX, UserCheck, Trash2 } from "lucide-react";
import { useState } from "react";

interface RowActionsProps {
  memberId: string;
  memberName: string;
  isActive: boolean;
  onView: (id: string) => void;
  onEdit: (id: string) => void;
  onToggleStatus: (id: string, currentStatus: boolean) => void;
  onDelete: (id: string) => void;
}

export function RowActions({ 
  memberId, 
  memberName, 
  isActive, 
  onView, 
  onEdit, 
  onToggleStatus,
  onDelete 
}: RowActionsProps) {
  const [showDeactivateDialog, setShowDeactivateDialog] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);

  const handleToggleStatus = () => {
    if (isActive) {
      setShowDeactivateDialog(true);
    } else {
      onToggleStatus(memberId, isActive);
    }
  };

  const handleConfirmDeactivate = () => {
    onToggleStatus(memberId, isActive);
    setShowDeactivateDialog(false);
  };

  return (
    <>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button 
            variant="ghost" 
            size="sm" 
            className="h-8 w-8 p-0 text-gray-400 hover:text-gray-600 hover:bg-gray-50"
            data-testid={`member-actions-${memberId}`}
            aria-label="Lidacties"
          >
            <MoreVertical className="h-4 w-4" />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="end" className="w-48">
          <DropdownMenuItem 
            onClick={() => onView(memberId)}
            className="cursor-pointer"
            data-testid={`action-view-${memberId}`}
          >
            <Eye className="h-4 w-4 mr-2" />
            Bekijk
          </DropdownMenuItem>
          <DropdownMenuItem 
            onClick={() => onEdit(memberId)}
            className="cursor-pointer"
            data-testid={`action-edit-${memberId}`}
          >
            <Edit2 className="h-4 w-4 mr-2" />
            Bewerken
          </DropdownMenuItem>
          <DropdownMenuSeparator />
          <DropdownMenuItem 
            onClick={handleToggleStatus}
            className={`cursor-pointer ${
              isActive 
                ? '' 
                : 'text-green-600 focus:text-green-600 focus:bg-green-50'
            }`}
            data-testid={`action-toggle-status-${memberId}`}
          >
            {isActive ? (
              <>
                <UserX className="h-4 w-4 mr-2" />
                Deactiveren
              </>
            ) : (
              <>
                <UserCheck className="h-4 w-4 mr-2" />
                Activeren
              </>
            )}
          </DropdownMenuItem>
          <DropdownMenuSeparator />
          <DropdownMenuItem 
            onClick={() => setShowDeleteDialog(true)}
            className="cursor-pointer text-red-600 focus:text-red-600 focus:bg-red-50"
            data-testid={`action-delete-${memberId}`}
          >
            <Trash2 className="h-4 w-4 mr-2" />
            Verwijderen
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>

      <AlertDialog open={showDeactivateDialog} onOpenChange={setShowDeactivateDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Lid deactiveren</AlertDialogTitle>
            <AlertDialogDescription>
              Weet je zeker dat je <strong>{memberName}</strong> wilt deactiveren? 
              <br /><br />
              <strong>Dit gebeurt automatisch:</strong>
              <br />â€¢ Lid wordt gemarkeerd als inactief
              <br />â€¢ Lidkaart krijgt status "VERLOPEN"
              <br />â€¢ Geen toegang meer tot diensten
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Annuleren</AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleConfirmDeactivate}
              className="bg-red-600 hover:bg-red-700 text-white"
            >
              Deactiveren
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      <AlertDialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Lid permanent verwijderen</AlertDialogTitle>
            <AlertDialogDescription>
              Weet je zeker dat je <strong>{memberName}</strong> permanent wilt verwijderen? 
              Deze actie kan niet ongedaan worden gemaakt. Alle gegevens, betalingshistoriek en transacties van dit lid worden definitief gewist.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Annuleren</AlertDialogCancel>
            <AlertDialogAction 
              onClick={() => {
                onDelete(memberId);
                setShowDeleteDialog(false);
              }}
              className="bg-red-600 hover:bg-red-700 text-white"
            >
              Permanent Verwijderen
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

