"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Transaction } from "@/lib/mock/transactions";
import { formatDateBE, formatCurrencyBE } from "@/lib/format";
import { exportToCSV } from "@/lib/csv";
import { FileText, FileSpreadsheet, FileImage } from "lucide-react";
import { CiExport } from "react-icons/ci";

interface ExportDialogProps {
  open: boolean;
  onClose: () => void;
  transactions: Transaction[];
}

type ExportFormat = 'csv' | 'excel' | 'pdf';

export function ExportDialog({ open, onClose, transactions }: ExportDialogProps) {
  const [format, setFormat] = useState<ExportFormat>('csv');
  const [loading, setLoading] = useState(false);

  const handleExport = async () => {
    setLoading(true);

    try {
      const exportData = transactions.map(t => ({
        Datum: formatDateBE(t.date),
        Type: t.type === 'INCOME' ? 'Inkomsten' : 'Uitgaven',
        Categorie: t.category,
        Lid: t.memberName || '',
        Bedrag: t.amount,
        'Bedrag (geformatteerd)': formatCurrencyBE(t.amount),
        Methode: t.method,
        Omschrijving: t.description || ''
      }));

      if (format === 'csv') {
        exportToCSV(exportData, `transacties_${new Date().toISOString().split('T')[0]}.csv`);
      } else if (format === 'excel') {
        // Mock Excel export - in real app would use a library like xlsx
        exportToCSV(exportData, `transacties_${new Date().toISOString().split('T')[0]}.xlsx`);
        alert('Excel export is een mock implementatie. In productie zou dit een echte Excel file genereren.');
      } else if (format === 'pdf') {
        // Mock PDF export - in real app would use a library like jsPDF
        alert('PDF export is een mock implementatie. In productie zou dit een echte PDF genereren.');
      }

      onClose();
    } catch (error) {
      alert('Fout bij het exporteren van gegevens');
    } finally {
      setLoading(false);
    }
  };

  const formatOptions = [
    {
      value: 'csv',
      label: 'CSV',
      description: 'Comma-separated values, compatible met Excel',
      icon: FileText,
      recommended: true
    },
    {
      value: 'excel',
      label: 'Excel',
      description: 'Microsoft Excel spreadsheet (.xlsx)',
      icon: FileSpreadsheet,
      recommended: false
    },
    {
      value: 'pdf',
      label: 'PDF',
      description: 'Portable Document Format voor rapporten',
      icon: FileImage,
      recommended: false
    }
  ];

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Transacties exporteren</DialogTitle>
          <DialogDescription>
            Exporteer {transactions.length} transacties naar het gewenste formaat
          </DialogDescription>
        </DialogHeader>

        <div className="py-6">
          <div className="space-y-4">
            <Label className="text-base font-medium">Selecteer export formaat:</Label>

            <RadioGroup value={format} onValueChange={(value) => setFormat(value as ExportFormat)}>
              {formatOptions.map((option) => {
                const Icon = option.icon;
                return (
                  <div key={option.value} className="flex items-center space-x-3">
                    <RadioGroupItem value={option.value} id={option.value} />
                    <Card className="flex-1 cursor-pointer" onClick={() => setFormat(option.value as ExportFormat)}>
                      <CardHeader className="pb-2">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-2">
                            <Icon className="h-5 w-5" />
                            <CardTitle className="text-sm">{option.label}</CardTitle>
                            {option.recommended && (
                              <span className="text-xs bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-400 px-2 py-0.5 rounded-full">
                                Aanbevolen
                              </span>
                            )}
                          </div>
                        </div>
                        <CardDescription className="text-xs">
                          {option.description}
                        </CardDescription>
                      </CardHeader>
                    </Card>
                  </div>
                );
              })}
            </RadioGroup>
          </div>

          {/* Export Summary */}
          <div className="mt-6 p-4 bg-gray-50 dark:bg-gray-900 rounded-lg">
            <h4 className="text-sm font-medium mb-2">Export overzicht:</h4>
            <div className="text-sm text-gray-600 dark:text-gray-400 space-y-1">
              <div className="flex justify-between">
                <span>Aantal transacties:</span>
                <span className="font-medium">{transactions.length}</span>
              </div>
              <div className="flex justify-between">
                <span>Formaat:</span>
                <span className="font-medium">{formatOptions.find(f => f.value === format)?.label}</span>
              </div>
              <div className="flex justify-between">
                <span>Bestandsnaam:</span>
                <span className="font-medium">
                  transacties_{new Date().toISOString().split('T')[0]}.{format === 'excel' ? 'xlsx' : format}
                </span>
              </div>
            </div>
          </div>
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={onClose} data-testid="button-cancel-export">
            Annuleren
          </Button>
          <Button 
            onClick={handleExport} 
            disabled={loading}
            className="gap-2"
            data-testid="button-start-export"
          >
            <CiExport className="h-4 w-4" />
            {loading ? 'Exporteren...' : 'Exporteren'}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/finance/FiltersDra.content:
"use client";

import { useState } from "react";
import { CalendarIcon } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { format } from "date-fns";
import { nl } from "date-fns/locale";
import { categories, mockMembers } from "@/lib/mock/transactions";
import { FilterData } from "@/lib/zod/transaction";

interface FiltersToolbarProps {
  filters: FilterData;
  onFiltersChange: (filters: FilterData) => void;
  onClearFilters: () => void;
  typeFilter?: string; // Type filter from main toolbar
}

export function FiltersToolbar({
  filters,
  onFiltersChange,
  onClearFilters,
  typeFilter
}: FiltersToolbarProps) {
  const [fromDateInput, setFromDateInput] = useState("");
  const [toDateInput, setToDateInput] = useState("");
  const [fromPopoverOpen, setFromPopoverOpen] = useState(false);
  const [toPopoverOpen, setToPopoverOpen] = useState(false);

  const updateFilter = (key: keyof FilterData, value: any) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  const formatDateInput = (value: string) => {
    const numbers = value.replace(/\D/g, '');
    if (numbers.length <= 2) {
      return numbers;
    } else if (numbers.length <= 4) {
      return `${numbers.slice(0, 2)}/${numbers.slice(2)}`;
    } else {
      return `${numbers.slice(0, 2)}/${numbers.slice(2, 4)}/${numbers.slice(4, 8)}`;
    }
  };

  const parseDate = (dateString: string): Date | undefined => {
    const cleanString = dateString.replace(/\D/g, '');
    if (cleanString.length === 8) {
      const day = parseInt(cleanString.slice(0, 2));
      const month = parseInt(cleanString.slice(2, 4)) - 1;
      const year = parseInt(cleanString.slice(4, 8));

      const date = new Date(year, month, day);
      if (date.getDate() === day && date.getMonth() === month && date.getFullYear() === year) {
        return date;
      }
    }
    return undefined;
  };

  const handleFromDateInput = (value: string) => {
    const formatted = formatDateInput(value);
    setFromDateInput(formatted);

    const parsedDate = parseDate(formatted);
    if (parsedDate) {
      updateFilter('dateFrom', parsedDate.toISOString().split('T')[0]);
    }
  };

  const handleToDateInput = (value: string) => {
    const formatted = formatDateInput(value);
    setToDateInput(formatted);

    const parsedDate = parseDate(formatted);
    if (parsedDate) {
      updateFilter('dateTo', parsedDate.toISOString().split('T')[0]);
    }
  };

  // Filter categories based on selected type
  const getAvailableCategories = () => {
    if (typeFilter === 'INCOME') {
      return categories.INCOME.sort();
    } else if (typeFilter === 'EXPENSE') {
      return categories.EXPENSE.sort();
    }
    // Show all categories if no type is selected or ALL is selected
    return [...categories.INCOME, ...categories.EXPENSE].sort();
  };

  const availableCategories = getAvailableCategories();

  const hasActiveFilters = filters.category || filters.method !== "ALL" || filters.memberId || 
                          filters.dateFrom || filters.dateTo || filters.amountMin || filters.amountMax;

  return (
    <div className="flex flex-col gap-4">
      {/* First row: Main filter selects */}
      <div className="flex flex-wrap gap-3">
        <Select value={filters.category || "ALL_CATEGORIES"} onValueChange={(value) => updateFilter('category', value === 'ALL_CATEGORIES' ? undefined : value)}>
          <SelectTrigger className="w-[170px] h-9 border-gray-200" data-testid="select-category-filter">
            <SelectValue placeholder="Alle categorieÃ«n" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="ALL_CATEGORIES">Alle categorieÃ«n</SelectItem>
            {availableCategories.map((category) => (
              <SelectItem key={category} value={category}>
                {category}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        <Select value={filters.method || "ALL"} onValueChange={(value) => updateFilter('method', value)}>
          <SelectTrigger className="w-[170px] h-9 border-gray-200" data-testid="select-method-filter">
            <SelectValue placeholder="Alle methodes" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="ALL">Alle methodes</SelectItem>
            <SelectItem value="SEPA">SEPA</SelectItem>
            <SelectItem value="OVERSCHRIJVING">Overschrijving</SelectItem>
            <SelectItem value="BANCONTACT">Bancontact</SelectItem>
            <SelectItem value="CASH">Contant</SelectItem>
          </SelectContent>
        </Select>

        <Select value={filters.memberId || "ALL_MEMBERS"} onValueChange={(value) => updateFilter('memberId', value === 'ALL_MEMBERS' ? undefined : value)}>
          <SelectTrigger className="w-[170px] h-9 border-gray-200" data-testid="select-member-filter">
            <SelectValue placeholder="Alle leden" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="ALL_MEMBERS">Alle leden</SelectItem>
            {mockMembers.map((member) => (
              <SelectItem key={member.id} value={member.id}>
                {member.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Second row: Amount and Date ranges with labels */}
      <div className="flex flex-wrap gap-3 items-center">
        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-600">Bedrag:</span>
          <Input
            type="number"
            step="0.01"
            min="0"
            placeholder="Min â‚¬"
            value={filters.amountMin || ""}
            onChange={(e) => updateFilter('amountMin', e.target.value ? parseFloat(e.target.value) : undefined)}
            className="w-[100px] h-9 border-gray-200"
            data-testid="input-amount-min"
          />
          <span className="text-sm text-gray-400">tot</span>
          <Input
            type="number"
            step="0.01"
            min="0"
            placeholder="Max â‚¬"
            value={filters.amountMax || ""}
            onChange={(e) => updateFilter('amountMax', e.target.value ? parseFloat(e.target.value) : undefined)}
            className="w-[100px] h-9 border-gray-200"
            data-testid="input-amount-max"
          />
        </div>

        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-600">Periode:</span>

          <div className="relative">
            <Input
              value={fromDateInput || (filters.dateFrom ? format(new Date(filters.dateFrom), "dd/MM/yyyy", { locale: nl }) : "")}
              onChange={(e) => handleFromDateInput(e.target.value)}
              placeholder="DD/MM/YYYY"
              className="w-[160px] h-9 border-gray-200 pr-10"
              data-testid="input-date-from"
              maxLength={10}
            />
            <Popover open={fromPopoverOpen} onOpenChange={setFromPopoverOpen}>
              <PopoverTrigger asChild>
                <Button
                  variant="ghost"
                  size="sm"
                  className="absolute right-1 top-1/2 -translate-y-1/2 h-7 w-7 p-0 hover:bg-gray-100"
                  onClick={() => setFromPopoverOpen(true)}
                >
                  <CalendarIcon className="h-4 w-4" />
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-auto p-0" align="start" side="bottom">
                <Calendar
                  mode="single"
                  selected={filters.dateFrom ? new Date(filters.dateFrom) : undefined}
                  onSelect={(date) => {
                    if (date) {
                      updateFilter('dateFrom', date.toISOString().split('T')[0]);
                    } else {
                      updateFilter('dateFrom', undefined);
                    }
                    setFromDateInput("");
                    setFromPopoverOpen(false);
                  }}
                  locale={nl}
                  showOutsideDays={false}
                  className="p-3"
                  defaultMonth={new Date()}
                />
              </PopoverContent>
            </Popover>
          </div>

          <span className="text-sm text-gray-400">tot</span>

          <div className="relative">
            <Input
              value={toDateInput || (filters.dateTo ? format(new Date(filters.dateTo), "dd/MM/yyyy", { locale: nl }) : "")}
              onChange={(e) => handleToDateInput(e.target.value)}
              placeholder="DD/MM/YYYY"
              className="w-[160px] h-9 border-gray-200 pr-10"
              data-testid="input-date-to"
              maxLength={10}
            />
            <Popover open={toPopoverOpen} onOpenChange={setToPopoverOpen}>
              <PopoverTrigger asChild>
                <Button
                  variant="ghost"
                  size="sm"
                  className="absolute right-1 top-1/2 -translate-y-1/2 h-7 w-7 p-0 hover:bg-gray-100"
                  onClick={() => setToPopoverOpen(true)}
                >
                  <CalendarIcon className="h-4 w-4" />
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-auto p-0" align="start" side="bottom">
                <Calendar
                  mode="single"
                  selected={filters.dateTo ? new Date(filters.dateTo) : undefined}
                  onSelect={(date) => {
                    if (date) {
                      updateFilter('dateTo', date.toISOString().split('T')[0]);
                    } else {
                      updateFilter('dateTo', undefined);
                    }
                    setToDateInput("");
                    setToPopoverOpen(false);
                  }}
                  locale={nl}
                  disabled={(date) => filters.dateFrom ? date < new Date(filters.dateFrom) : false}
                  showOutsideDays={false}
                  className="p-3"
                  defaultMonth={new Date()}
                />
              </PopoverContent>
            </Popover>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/finance/ImportDial.content:
"use client";

import { useState, useRef } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { ImportMappingSchema, ImportMappingData } from "@/lib/zod/transaction";
import { parseCSV, parseMT940, parseCODA, CSVRow } from "@/lib/csv";
import { generateTransactionId, Transaction } from "@/lib/mock/transactions";
import { FileText, AlertTriangle, CheckCircle, Upload } from "lucide-react";
import { CiImport } from "react-icons/ci";

interface ImportDialogProps {
  open: boolean;
  onClose: () => void;
  onImport: (transactions: Transaction[]) => void;
}

type ImportStep = 'upload' | 'mapping' | 'preview' | 'complete';

export function ImportDialog({ open, onClose, onImport }: ImportDialogProps) {
  const [step, setStep] = useState<ImportStep>('upload');
  const [fileName, setFileName] = useState('');
  const [fileType, setFileType] = useState<'csv' | 'mt940' | 'coda'>('csv');
  const [headers, setHeaders] = useState<string[]>([]);
  const [rows, setRows] = useState<CSVRow[]>([]);
  const [mappedTransactions, setMappedTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const form = useForm<ImportMappingData>({
    resolver: zodResolver(ImportMappingSchema),
    defaultValues: {
      dateColumn: '',
      amountColumn: '',
      descriptionColumn: '',
      categoryColumn: ''
    }
  });

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setLoading(true);
    setFileName(file.name);

    try {
      const content = await file.text();
      let parseResult;

      // Determine file type and parse accordingly
      if (file.name.toLowerCase().endsWith('.csv')) {
        setFileType('csv');
        parseResult = parseCSV(content);
      } else if (file.name.toLowerCase().includes('mt940')) {
        setFileType('mt940');
        parseResult = parseMT940(content);
      } else if (file.name.toLowerCase().includes('coda')) {
        setFileType('coda');
        parseResult = parseCODA(content);
      } else {
        // Default to CSV
        setFileType('csv');
        parseResult = parseCSV(content);
      }

      if (parseResult.success) {
        setHeaders(parseResult.headers);
        setRows(parseResult.rows.slice(0, 10)); // Preview first 10 rows
        setStep('mapping');
      } else {
        alert(parseResult.error || 'Fout bij het parseren van het bestand');
      }
    } catch (error) {
      alert('Fout bij het lezen van het bestand');
    } finally {
      setLoading(false);
    }
  };

  const handleMapping = (data: ImportMappingData) => {
    setLoading(true);

    try {
      const transactions: Transaction[] = rows.map(row => {
        const dateStr = row[data.dateColumn];
        const amountStr = row[data.amountColumn];
        const description = data.descriptionColumn ? row[data.descriptionColumn] : '';
        const category = data.categoryColumn ? row[data.categoryColumn] : 'Onbekend';

        // Parse amount and determine type
        const amount = Math.abs(parseFloat(amountStr.replace(',', '.')) || 0);
        const isNegative = amountStr.includes('-') || amountStr.startsWith('D') || row.type === 'D' || row.type === 'Debet';
        const type = isNegative ? 'EXPENSE' : 'INCOME';

        // Parse date (try different formats)
        let date = dateStr;
        if (dateStr.includes('/')) {
          const parts = dateStr.split('/');
          if (parts.length === 3) {
            date = `${parts[2]}-${parts[1].padStart(2, '0')}-${parts[0].padStart(2, '0')}`;
          }
        } else if (dateStr.includes('-') && dateStr.length === 8) {
          // Format: ddmmyyyy or yyyymmdd
          if (dateStr.startsWith('20')) {
            date = `${dateStr.substr(0, 4)}-${dateStr.substr(4, 2)}-${dateStr.substr(6, 2)}`;
          } else {
            date = `20${dateStr.substr(4, 2)}-${dateStr.substr(2, 2)}-${dateStr.substr(0, 2)}`;
          }
        }

        return {
          id: generateTransactionId(),
          date,
          type,
          category,
          amount,
          method: fileType === 'csv' ? 'OVERSCHRIJVING' : 'SEPA',
          description
        } as Transaction;
      });

      setMappedTransactions(transactions);
      setStep('preview');
    } catch (error) {
      alert('Fout bij het verwerken van de gegevens');
    } finally {
      setLoading(false);
    }
  };

  const handleImport = () => {
    onImport(mappedTransactions);
    setStep('complete');
  };

  const handleClose = () => {
    setStep('upload');
    setFileName('');
    setHeaders([]);
    setRows([]);
    setMappedTransactions([]);
    form.reset();
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    onClose();
  };

  const renderUploadStep = () => (
    <div className="space-y-6">
      <div className="text-center">
        <Upload className="mx-auto h-12 w-12 text-gray-400" />
        <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-gray-100">
          Upload bestand
        </h3>
        <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          Selecteer een CSV, MT940 of CODA bestand om te importeren
        </p>
      </div>

      <div className="mt-6">
        <input
          ref={fileInputRef}
          type="file"
          accept=".csv,.txt,.mt940,.coda"
          onChange={handleFileUpload}
          className="hidden"
          data-testid="input-file-upload"
        />
        <Button
          onClick={() => fileInputRef.current?.click()}
          className="w-full"
          disabled={loading}
          data-testid="button-select-file"
        >
          {loading ? 'Bezig met laden...' : 'Bestand selecteren'}
        </Button>
      </div>

      <div className="text-xs text-gray-500 dark:text-gray-400 space-y-1">
        <p><strong>Ondersteunde formaten:</strong></p>
        <ul className="list-disc list-inside space-y-1">
          <li>CSV: Comma/semicolon separated values</li>
          <li>MT940: SWIFT bank statement format</li>
          <li>CODA: Belgian bank statement format</li>
        </ul>
      </div>
    </div>
  );

  const renderMappingStep = () => (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-medium">Kolom mapping</h3>
        <p className="text-sm text-gray-500 dark:text-gray-400">
          Koppel de kolommen uit je bestand aan de juiste velden
        </p>
      </div>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(handleMapping)} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="dateColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Datum kolom *</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-date-column">
                        <SelectValue placeholder="Selecteer kolom" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="amountColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Bedrag kolom *</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-amount-column">
                        <SelectValue placeholder="Selecteer kolom" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="descriptionColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Omschrijving kolom</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-description-column">
                        <SelectValue placeholder="Selecteer kolom (optioneel)" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="none">Geen</SelectItem>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="categoryColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Categorie kolom</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-category-column">
                        <SelectValue placeholder="Selecteer kolom (optioneel)" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="none">Geen</SelectItem>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          {/* Preview of first few rows */}
          <div className="mt-6">
            <h4 className="text-sm font-medium mb-2">Voorbeeld data:</h4>
            <div className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden">
              <Table>
                <TableHeader>
                  <TableRow>
                    {headers.map(header => (
                      <TableHead key={header} className="text-xs">{header}</TableHead>
                    ))}
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {rows.slice(0, 3).map((row, index) => (
                    <TableRow key={index}>
                      {headers.map(header => (
                        <TableCell key={header} className="text-xs">
                          {row[header]}
                        </TableCell>
                      ))}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          </div>

          <div className="flex justify-between pt-4">
            <Button type="button" variant="outline" onClick={() => setStep('upload')}>
              Terug
            </Button>
            <Button type="submit" disabled={loading} data-testid="button-continue-mapping">
              {loading ? 'Verwerken...' : 'Doorgaan'}
            </Button>
          </div>
        </form>
      </Form>
    </div>
  );

  const renderPreviewStep = () => (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-medium">Voorbeeld import</h3>
        <p className="text-sm text-gray-500 dark:text-gray-400">
          Controleer de geÃ¯mporteerde transacties voordat je ze toevoegt
        </p>
      </div>

      <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
        <div className="flex items-center gap-2">
          <FileText className="h-5 w-5 text-blue-600" />
          <span className="text-sm font-medium">
            {mappedTransactions.length} transacties gevonden in {fileName}
          </span>
        </div>
      </div>

      <div className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden max-h-80 overflow-y-auto">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Datum</TableHead>
              <TableHead>Type</TableHead>
              <TableHead>Categorie</TableHead>
              <TableHead>Bedrag</TableHead>
              <TableHead>Omschrijving</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {mappedTransactions.slice(0, 20).map((transaction, index) => (
              <TableRow key={index}>
                <TableCell className="text-xs">{transaction.date}</TableCell>
                <TableCell>
                  <Badge variant={transaction.type === 'INCOME' ? 'secondary' : 'destructive'}>
                    {transaction.type === 'INCOME' ? 'Inkomst' : 'Uitgave'}
                  </Badge>
                </TableCell>
                <TableCell className="text-xs">{transaction.category}</TableCell>
                <TableCell className="text-xs">â‚¬{transaction.amount.toFixed(2)}</TableCell>
                <TableCell className="text-xs max-w-[200px] truncate">
                  {transaction.description || '-'}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      {mappedTransactions.length > 20 && (
        <p className="text-xs text-gray-500 dark:text-gray-400">
          ... en nog {mappedTransactions.length - 20} transacties
        </p>
      )}

      <div className="flex justify-between pt-4">
        <Button type="button" variant="outline" onClick={() => setStep('mapping')}>
          Terug
        </Button>
        <Button onClick={handleImport} data-testid="button-confirm-import">
          <CiImport className="h-4 w-4 mr-2" />
          Transacties importeren
        </Button>
      </div>
    </div>
  );

  const renderCompleteStep = () => (
    <div className="text-center space-y-6">
      <div>
        <CheckCircle className="mx-auto h-12 w-12 text-green-500" />
        <h3 className="mt-2 text-lg font-medium text-gray-900 dark:text-gray-100">
          Import voltooid!
        </h3>
        <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          {mappedTransactions.length} transacties zijn succesvol geÃ¯mporteerd
        </p>
      </div>

      <Button onClick={handleClose} className="w-full" data-testid="button-close-import">
        Sluiten
      </Button>
    </div>
  );

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-[800px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Transacties importeren</DialogTitle>
          <DialogDescription>
            Import transacties vanuit CSV, MT940 of CODA bestanden
          </DialogDescription>
        </DialogHeader>

        <div className="py-6">
          {step === 'upload' && renderUploadStep()}
          {step === 'mapping' && renderMappingStep()}
          {step === 'preview' && renderPreviewStep()}
          {step === 'complete' && renderCompleteStep()}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/finance/KpiCards.t.content:
"use client";

import { formatCurrencyBE } from "@/lib/format";
import { Euro } from "lucide-react";
import { HiInboxIn } from "react-icons/hi";
import { MdOutlineOutbox } from "react-icons/md";
import { TbClockExclamation, TbAlertTriangle } from "react-icons/tb";
import { useMemo } from "react";
import { useQuery } from "@tanstack/react-query";

interface KpiCardProps {
  title: string;
  value: string;
  delta: {
    value: string;
    positive: boolean;
  };
  icon: React.ReactNode;
  iconBgColor?: string;
}

function KpiCard({ title, value, delta, icon, iconBgColor = "bg-blue-50" }: KpiCardProps) {
  const getColorClasses = () => {
    if (delta.positive) {
      return {
        textClass: 'text-green-600',
        bgClass: 'bg-green-50'
      };
    } else {
      return {
        textClass: 'text-red-500',
        bgClass: 'bg-red-50'
      };
    }
  };

  const colors = getColorClasses();

  return (
    <div className="glass-card rounded-xl p-4 card-hover animate-fade-in group relative overflow-hidden">
      {/* Dynamic gradient overlay based on card type */}
      <div className={`absolute inset-0 pointer-events-none opacity-0 group-hover:opacity-100 transition-opacity duration-500 ${
        title === 'Totale Inkomsten' ? 'bg-gradient-to-br from-green-500/5 to-transparent' :
        title === 'Vervallen Lidgeld' ? 'bg-gradient-to-br from-red-500/5 to-transparent' :
        title === 'Openstaand Lidgeld' ? 'bg-gradient-to-br from-orange-500/5 to-transparent' :
        'bg-gradient-to-br from-blue-500/5 to-transparent'
      }`}></div>

      <div className="flex items-center justify-between relative z-10">
        <div className="flex-1">
          <p className="text-xs font-medium text-muted-foreground mb-1">{title}</p>
          <p className="text-lg font-bold text-foreground mb-2 group-hover:text-primary transition-colors duration-300 tabular-nums">{value}</p>
          <div className="flex items-center space-x-1">
            <span className={`text-xs font-medium ${colors.textClass} group-hover:text-opacity-80 transition-all duration-300`}>
              {delta.value}
            </span>
          </div>
        </div>
        <div className={`w-10 h-10 ${iconBgColor} rounded-xl flex items-center justify-center shadow-md ring-2 ring-white/10 group-hover:scale-110 transition-all duration-300 relative`}>
          <div className="absolute inset-0 bg-gradient-to-br from-white/20 to-transparent rounded-xl"></div>
          <div className="relative">{icon}</div>
        </div>
      </div>
    </div>
  );
}

export function KpiCards() {
  const { data: transactions } = useQuery({
    queryKey: ["/api/transactions"],
  });

  const { data: fees } = useQuery({
    queryKey: ["/api/fees"],
  });

  const kpiData = useMemo(() => {
    const totalIncome = Array.isArray(transactions) ? transactions.filter((t: any) => t.type === 'INCOME').reduce((sum: number, t: any) => sum + Math.abs(parseFloat(t.amount)), 0) : 0;
    const totalExpense = Array.isArray(transactions) ? transactions.filter((t: any) => t.type === 'EXPENSE').reduce((sum: number, t: any) => sum + Math.abs(parseFloat(t.amount)), 0) : 0;
    const balance = totalIncome - totalExpense;
    const openFees = Array.isArray(fees) ? fees.filter((f: any) => f.status === 'OPEN').reduce((sum: number, f: any) => sum + parseFloat(f.amount), 0) : 0;
    const overdueFees = Array.isArray(fees) ? fees.filter((f: any) => f.status === 'OVERDUE').reduce((sum: number, f: any) => sum + parseFloat(f.amount), 0) : 0;

    return {
      income: totalIncome,
      expense: totalExpense,
      balance,
      openFees,
      overdueFees
    };
  }, [transactions, fees]);

  const kpis = [
    {
      title: "Totale Inkomsten",
      value: formatCurrencyBE(kpiData.income),
      delta: { value: "Totaal inkomsten", positive: true },
      icon: <HiInboxIn className="h-4 w-4 text-green-600" />,
      iconBgColor: "bg-green-50"
    },
    {
      title: "Totale Uitgaven",
      value: formatCurrencyBE(kpiData.expense),
      delta: { value: "Totaal uitgaven", positive: false },
      icon: <MdOutlineOutbox className="h-4 w-4 text-red-600" />,
      iconBgColor: "bg-red-50"
    },
    {
      title: "Netto Saldo",
      value: formatCurrencyBE(Math.abs(kpiData.balance)),
      delta: { 
        value: kpiData.balance >= 0 ? "Overschot" : "Tekort", 
        positive: kpiData.balance >= 0 
      },
      icon: <Euro className="h-4 w-4 text-blue-600" />,
      iconBgColor: "bg-blue-50"
    },
    {
      title: "Openstaand Lidgeld",
      value: formatCurrencyBE(kpiData.openFees),
      delta: { value: "Openstaande bedragen", positive: false },
      icon: <TbClockExclamation className="h-4 w-4 text-orange-600" />,
      iconBgColor: "bg-orange-50"
    },
    {
      title: "Vervallen Lidgeld",
      value: formatCurrencyBE(kpiData.overdueFees),
      delta: { value: "Vervallen betalingen", positive: false },
      icon: <TbAlertTriangle className="h-4 w-4 text-red-600" />,
      iconBgColor: "bg-red-50"
    }
  ];

  return (
    <div className="grid grid-cols-1 sm:grid-cols-3 lg:grid-cols-5 gap-4 mb-6">
      {kpis.map((kpi, index) => (
        <KpiCard
          key={index}
          title={kpi.title}
          value={kpi.value}
          delta={kpi.delta}
          icon={kpi.icon}
          iconBgColor={kpi.iconBgColor}
        />
      ))}
    </div>
  );
}

client/src/components/finance/NewTransac.content:
"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { TransactionSchema, TransactionFormData } from "@/lib/zod/transaction";
import { categories } from "@/lib/mock/transactions";
import { format } from "date-fns";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface NewTransactionDialogProps {
  open: boolean;
  onClose: () => void;
  editTransaction?: any | null;
}

export function NewTransactionDialog({
  open,
  onClose,
  editTransaction
}: NewTransactionDialogProps) {
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('basis');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const isEditing = !!editTransaction;

  // Fetch members for dropdown
  const { data: members = [] } = useQuery({
    queryKey: ["/api/members"],
  });

  const form = useForm<TransactionFormData>({
    resolver: zodResolver(TransactionSchema),
    defaultValues: {
      type: editTransaction?.type || 'INCOME',
      date: editTransaction?.date || format(new Date(), 'yyyy-MM-dd'),
      category: editTransaction?.category || '',
      amount: editTransaction?.amount || 0,
      method: editTransaction?.method || 'SEPA',
      memberId: editTransaction?.memberId || '',
      memberName: editTransaction?.memberName || '',
      description: editTransaction?.description || ''
    }
  });

  const selectedType = form.watch('type');
  const selectedMemberId = form.watch('memberId');

  // Create transaction mutation
  const createTransactionMutation = useMutation({
    mutationFn: async (data: TransactionFormData) => {
      const response = await apiRequest("POST", "/api/transactions", data);
      return response.json();
    },
    onSuccess: async (newTransaction, variables) => {
      // Optimistic update - voeg transactie toe aan lijst
      queryClient.setQueryData(["/api/transactions"], (oldData: any) => {
        if (!Array.isArray(oldData)) return [newTransaction];
        return [newTransaction, ...oldData];
      });

      await queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/financial/reports"] });
      toast({
        title: "Transactie toegevoegd",
        description: `${variables.type === 'INCOME' ? 'Inkomst' : 'Uitgave'} van â‚¬${variables.amount} is toegevoegd.`,
      });
      setLoading(false);
      handleClose();
    },
    onError: (error: any) => {
      console.error('Create transaction error:', error);
      toast({
        title: "Fout bij toevoegen transactie",
        description: error?.message || "Er is een fout opgetreden bij het toevoegen van de transactie.",
        variant: "destructive",
      });
      setLoading(false);
    },
  });

  // Edit transaction mutation
  const editTransactionMutation = useMutation({
    mutationFn: async (data: TransactionFormData) => {
      const response = await apiRequest("PUT", `/api/transactions/${editTransaction.id}`, data);
      return response.json();
    },
    onSuccess: async (updatedTransaction, variables) => {
      await queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/financial/reports"] });
      toast({
        title: "Transactie bijgewerkt",
        description: `Wijzigingen zijn opgeslagen.`,
      });
      setLoading(false);
      handleClose();
    },
    onError: (error: any) => {
      console.error('Edit transaction error:', error);
      toast({
        title: "Fout bij bijwerken transactie",
        description: error?.message || "Er is een fout opgetreden bij het bijwerken van de transactie.",
        variant: "destructive",
      });
      setLoading(false);
    },
  });

  // Handle member change to auto-fill member name
  const handleMemberChange = (memberId: string) => {
    if (memberId === "none" || !memberId) {
      form.setValue("memberName", "");
      return;
    }

    const member = Array.isArray(members) ? members.find((m: any) => m.id === memberId) : null;
    if (member) {
      form.setValue("memberName", `${member.firstName} ${member.lastName}`);
    }
  };

  const onSubmit = async (data: TransactionFormData) => {
    try {
      setLoading(true);

      // Clean up memberId if "none" selected
      if (data.memberId === "none" || !data.memberId) {
        data.memberId = "";
        data.memberName = "";
      }

      if (isEditing) {
        editTransactionMutation.mutate(data);
      } else {
        createTransactionMutation.mutate(data);
      }
    } catch (error) {
      console.error('Error saving transaction:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    if (!isEditing) {
      form.reset();
    }
    setActiveTab('basis');
    onClose();
  };

  const nextTab = () => {
    if (activeTab === 'basis') setActiveTab('details');
    else if (activeTab === 'details') setActiveTab('overzicht');
  };

  const prevTab = () => {
    if (activeTab === 'details') setActiveTab('basis');
    else if (activeTab === 'overzicht') setActiveTab('details');
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-[800px] min-h-[600px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>
            {isEditing ? 'Transactie bewerken' : 'Nieuwe transactie'}
          </DialogTitle>
          <DialogDescription>
            {isEditing 
              ? 'Wijzig de details van deze transactie.'
              : 'Voeg een nieuwe inkomst of uitgave toe aan je financiÃ«le administratie.'
            }
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)}>
            <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
              <TabsList className="grid w-full grid-cols-3">
                <TabsTrigger value="basis">Basis Gegevens</TabsTrigger>
                <TabsTrigger value="details">Details</TabsTrigger>
                <TabsTrigger value="overzicht">Overzicht</TabsTrigger>
              </TabsList>

              <TabsContent value="basis" className="space-y-6 mt-6">
                {/* Type */}
                <FormField
                  control={form.control}
                  name="type"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Type *</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-transaction-type">
                            <SelectValue placeholder="Selecteer type" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="INCOME">Inkomsten</SelectItem>
                          <SelectItem value="EXPENSE">Uitgaven</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Date and Amount Row */}
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="date"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Datum *</FormLabel>
                        <FormControl>
                          <Input
                            type="date"
                            {...field}
                            data-testid="input-transaction-date"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="amount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Bedrag (â‚¬) *</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="0.01"
                            min="0"
                            placeholder="0,00"
                            {...field}
                            onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                            data-testid="input-transaction-amount"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Category */}
                <FormField
                  control={form.control}
                  name="category"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Categorie *</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-transaction-category">
                            <SelectValue placeholder="Selecteer categorie" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {categories[selectedType].map((category) => (
                            <SelectItem key={category} value={category}>
                              {category}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>

              <TabsContent value="details" className="space-y-6 mt-6">
                {/* Payment Method */}
                <FormField
                  control={form.control}
                  name="method"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Betaalmethode *</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-transaction-method">
                            <SelectValue placeholder="Selecteer methode" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="SEPA">SEPA</SelectItem>
                          <SelectItem value="OVERSCHRIJVING">Overschrijving</SelectItem>
                          <SelectItem value="BANCONTACT">Bancontact</SelectItem>
                          <SelectItem value="CASH">Contant</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Member (Optional) */}
                <FormField
                  control={form.control}
                  name="memberId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Lid (optioneel)</FormLabel>
                      <Select 
                        onValueChange={(value) => {
                          field.onChange(value);
                          handleMemberChange(value);
                        }} 
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger data-testid="select-transaction-member">
                            <SelectValue placeholder="Selecteer lid" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="none">Geen lid gekoppeld</SelectItem>
                          {Array.isArray(members) && members.map((member: any) => (
                            <SelectItem key={member.id} value={member.id}>
                              {member.firstName} {member.lastName}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormDescription>
                        Koppel deze transactie aan een specifiek lid (bijv. voor lidgeld)
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Description */}
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Omschrijving (optioneel)</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Voeg een omschrijving toe..."
                          className="resize-none"
                          rows={3}
                          {...field}
                          data-testid="input-transaction-description"
                        />
                      </FormControl>
                      <FormDescription>
                        Maximaal 500 karakters
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>

              <TabsContent value="overzicht" className="space-y-6 mt-6">
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Overzicht van de transactie</h3>

                  <div className="grid grid-cols-2 gap-4 p-4 bg-muted rounded-lg">
                    <div>
                      <span className="font-medium">Type:</span>
                      <p className="text-sm text-muted-foreground">
                        {form.watch('type') === 'INCOME' ? 'Inkomsten' : 'Uitgaven'}
                      </p>
                    </div>
                    <div>
                      <span className="font-medium">Bedrag:</span>
                      <p className="text-sm text-muted-foreground">â‚¬{form.watch('amount') || 0}</p>
                    </div>
                    <div>
                      <span className="font-medium">Datum:</span>
                      <p className="text-sm text-muted-foreground">{form.watch('date')}</p>
                    </div>
                    <div>
                      <span className="font-medium">Categorie:</span>
                      <p className="text-sm text-muted-foreground">{form.watch('category') || 'Niet geselecteerd'}</p>
                    </div>
                    <div>
                      <span className="font-medium">Betaalmethode:</span>
                      <p className="text-sm text-muted-foreground">{form.watch('method')}</p>
                    </div>
                    <div>
                      <span className="font-medium">Lid:</span>
                      <p className="text-sm text-muted-foreground">
                        {form.watch('memberName') || 'Geen lid gekoppeld'}
                      </p>
                    </div>
                  </div>

                  {form.watch('description') && (
                    <div className="p-4 bg-muted rounded-lg">
                      <span className="font-medium">Omschrijving:</span>
                      <p className="text-sm text-muted-foreground mt-1">{form.watch('description')}</p>
                    </div>
                  )}
                </div>
              </TabsContent>
            </Tabs>

            <DialogFooter className="flex justify-between items-center mt-6">
              <Button 
                type="button" 
                variant="outline" 
                onClick={handleClose}
                data-testid="button-cancel-transaction"
              >
                Annuleren
              </Button>

              <div className="flex gap-2">
                {activeTab !== 'basis' && (
                  <Button type="button" variant="outline" onClick={prevTab}>
                    Vorige
                  </Button>
                )}
                {activeTab !== 'overzicht' && (
                  <Button type="button" onClick={nextTab}>
                    Volgende
                  </Button>
                )}
                <Button 
                  type="submit" 
                  disabled={loading || activeTab !== 'overzicht'}
                  variant={activeTab === 'overzicht' ? 'default' : 'outline'}
                  data-testid="button-save-transaction"
                >
                  {loading ? 'Bezig...' : (isEditing ? 'Wijzigingen opslaan' : 'Transactie toevoegen')}
                </Button>
              </div>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

