components/finance/ExportDialog.tsx.content:
"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Transaction } from "@/lib/mock/transactions";
import { formatDateBE, formatCurrencyBE } from "@/lib/format";
import { exportToCSV } from "@/lib/csv";
import { Download, FileText, FileSpreadsheet, FileImage } from "lucide-react";

interface ExportDialogProps {
  open: boolean;
  onClose: () => void;
  transactions: Transaction[];
}

type ExportFormat = 'csv' | 'excel' | 'pdf';

export function ExportDialog({ open, onClose, transactions }: ExportDialogProps) {
  const [format, setFormat] = useState<ExportFormat>('csv');
  const [loading, setLoading] = useState(false);

  const handleExport = async () => {
    setLoading(true);

    try {
      const exportData = transactions.map(t => ({
        Datum: formatDateBE(t.date),
        Type: t.type === 'INCOME' ? 'Inkomsten' : 'Uitgaven',
        Categorie: t.category,
        Lid: t.memberName || '',
        Bedrag: t.amount,
        'Bedrag (geformatteerd)': formatCurrencyBE(t.amount),
        Methode: t.method,
        Omschrijving: t.description || ''
      }));

      if (format === 'csv') {
        exportToCSV(exportData, `transacties_${new Date().toISOString().split('T')[0]}.csv`);
      } else if (format === 'excel') {
        // Mock Excel export - in real app would use a library like xlsx
        exportToCSV(exportData, `transacties_${new Date().toISOString().split('T')[0]}.xlsx`);
        alert('Excel export is een mock implementatie. In productie zou dit een echte Excel file genereren.');
      } else if (format === 'pdf') {
        // Mock PDF export - in real app would use a library like jsPDF
        alert('PDF export is een mock implementatie. In productie zou dit een echte PDF genereren.');
      }

      onClose();
    } catch (error) {
      alert('Fout bij het exporteren van gegevens');
    } finally {
      setLoading(false);
    }
  };

  const formatOptions = [
    {
      value: 'csv',
      label: 'CSV',
      description: 'Comma-separated values, compatible met Excel',
      icon: FileText,
      recommended: true
    },
    {
      value: 'excel',
      label: 'Excel',
      description: 'Microsoft Excel spreadsheet (.xlsx)',
      icon: FileSpreadsheet,
      recommended: false
    },
    {
      value: 'pdf',
      label: 'PDF',
      description: 'Portable Document Format voor rapporten',
      icon: FileImage,
      recommended: false
    }
  ];

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Transacties exporteren</DialogTitle>
          <DialogDescription>
            Exporteer {transactions.length} transacties naar het gewenste formaat
          </DialogDescription>
        </DialogHeader>

        <div className="py-6">
          <div className="space-y-4">
            <Label className="text-base font-medium">Selecteer export formaat:</Label>

            <RadioGroup value={format} onValueChange={(value) => setFormat(value as ExportFormat)}>
              {formatOptions.map((option) => {
                const Icon = option.icon;
                return (
                  <div key={option.value} className="flex items-center space-x-3">
                    <RadioGroupItem value={option.value} id={option.value} />
                    <Card className="flex-1 cursor-pointer" onClick={() => setFormat(option.value as ExportFormat)}>
                      <CardHeader className="pb-2">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-2">
                            <Icon className="h-5 w-5" />
                            <CardTitle className="text-sm">{option.label}</CardTitle>
                            {option.recommended && (
                              <span className="text-xs bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-400 px-2 py-0.5 rounded-full">
                                Aanbevolen
                              </span>
                            )}
                          </div>
                        </div>
                        <CardDescription className="text-xs">
                          {option.description}
                        </CardDescription>
                      </CardHeader>
                    </Card>
                  </div>
                );
              })}
            </RadioGroup>
          </div>

          {/* Export Summary */}
          <div className="mt-6 p-4 bg-gray-50 dark:bg-gray-900 rounded-lg">
            <h4 className="text-sm font-medium mb-2">Export overzicht:</h4>
            <div className="text-sm text-gray-600 dark:text-gray-400 space-y-1">
              <div className="flex justify-between">
                <span>Aantal transacties:</span>
                <span className="font-medium">{transactions.length}</span>
              </div>
              <div className="flex justify-between">
                <span>Formaat:</span>
                <span className="font-medium">{formatOptions.find(f => f.value === format)?.label}</span>
              </div>
              <div className="flex justify-between">
                <span>Bestandsnaam:</span>
                <span className="font-medium">
                  transacties_{new Date().toISOString().split('T')[0]}.{format === 'excel' ? 'xlsx' : format}
                </span>
              </div>
            </div>
          </div>
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={onClose} data-testid="button-cancel-export">
            Annuleren
          </Button>
          <Button 
            onClick={handleExport} 
            disabled={loading}
            className="gap-2"
            data-testid="button-start-export"
          >
            <Download className="h-4 w-4" />
            {loading ? 'Exporteren...' : 'Exporteren'}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

components/finance/FiltersDrawer.tsx.content:
"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Sheet, SheetContent, SheetDescription, SheetHeader, SheetTitle, SheetFooter } from "@/components/ui/sheet";
import { categories, mockMembers } from "@/lib/mock/transactions";
import { FilterData } from "@/lib/zod/transaction";

interface FiltersDrawerProps {
  open: boolean;
  onClose: () => void;
  filters: FilterData;
  onFiltersChange: (filters: FilterData) => void;
  onApplyFilters: () => void;
  onClearFilters: () => void;
}

export function FiltersDrawer({
  open,
  onClose,
  filters,
  onFiltersChange,
  onApplyFilters,
  onClearFilters
}: FiltersDrawerProps) {
  const updateFilter = (key: keyof FilterData, value: any) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  const allCategories = [...categories.INCOME, ...categories.EXPENSE].sort();

  return (
    <Sheet open={open} onOpenChange={onClose}>
      <SheetContent className="w-[400px] sm:w-[540px]">
        <SheetHeader>
          <SheetTitle>Geavanceerde Filters</SheetTitle>
          <SheetDescription>
            Verfijn je zoekresultaten met specifieke criteria
          </SheetDescription>
        </SheetHeader>

        <div className="grid gap-6 py-6">
          {/* Category Filter */}
          <div className="grid gap-2">
            <Label htmlFor="category">Categorie</Label>
            <Select value={filters.category || ""} onValueChange={(value) => updateFilter('category', value || undefined)}>
              <SelectTrigger data-testid="select-category-filter">
                <SelectValue placeholder="Selecteer categorie" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">Alle categorieÃ«n</SelectItem>
                {allCategories.map((category) => (
                  <SelectItem key={category} value={category}>
                    {category}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Method Filter */}
          <div className="grid gap-2">
            <Label htmlFor="method">Betaalmethode</Label>
            <Select value={filters.method || "ALL"} onValueChange={(value) => updateFilter('method', value)}>
              <SelectTrigger data-testid="select-method-filter">
                <SelectValue placeholder="Selecteer methode" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="ALL">Alle methodes</SelectItem>
                <SelectItem value="SEPA">SEPA</SelectItem>
                <SelectItem value="OVERSCHRIJVING">Overschrijving</SelectItem>
                <SelectItem value="BANCONTACT">Bancontact</SelectItem>
                <SelectItem value="CASH">Contant</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Member Filter */}
          <div className="grid gap-2">
            <Label htmlFor="member">Lid</Label>
            <Select value={filters.memberId || ""} onValueChange={(value) => updateFilter('memberId', value || undefined)}>
              <SelectTrigger data-testid="select-member-filter">
                <SelectValue placeholder="Selecteer lid" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">Alle leden</SelectItem>
                {mockMembers.map((member) => (
                  <SelectItem key={member.id} value={member.id}>
                    {member.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Date Range */}
          <div className="grid grid-cols-2 gap-4">
            <div className="grid gap-2">
              <Label htmlFor="dateFrom">Van datum</Label>
              <Input
                id="dateFrom"
                type="date"
                value={filters.dateFrom || ""}
                onChange={(e) => updateFilter('dateFrom', e.target.value || undefined)}
                data-testid="input-date-from"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="dateTo">Tot datum</Label>
              <Input
                id="dateTo"
                type="date"
                value={filters.dateTo || ""}
                onChange={(e) => updateFilter('dateTo', e.target.value || undefined)}
                data-testid="input-date-to"
              />
            </div>
          </div>

          {/* Amount Range */}
          <div className="grid grid-cols-2 gap-4">
            <div className="grid gap-2">
              <Label htmlFor="amountMin">Min. bedrag (â‚¬)</Label>
              <Input
                id="amountMin"
                type="number"
                step="0.01"
                min="0"
                placeholder="0,00"
                value={filters.amountMin || ""}
                onChange={(e) => updateFilter('amountMin', e.target.value ? parseFloat(e.target.value) : undefined)}
                data-testid="input-amount-min"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="amountMax">Max. bedrag (â‚¬)</Label>
              <Input
                id="amountMax"
                type="number"
                step="0.01"
                min="0"
                placeholder="1000,00"
                value={filters.amountMax || ""}
                onChange={(e) => updateFilter('amountMax', e.target.value ? parseFloat(e.target.value) : undefined)}
                data-testid="input-amount-max"
              />
            </div>
          </div>
        </div>

        <SheetFooter className="gap-2">
          <Button variant="outline" onClick={onClearFilters} data-testid="button-clear-filters">
            Wis filters
          </Button>
          <Button onClick={() => { onApplyFilters(); onClose(); }} data-testid="button-apply-filters">
            Toepassen
          </Button>
        </SheetFooter>
      </SheetContent>
    </Sheet>
  );
}

components/finance/ImportDialog.tsx.content:
"use client";

import { useState, useRef } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { ImportMappingSchema, ImportMappingData } from "@/lib/zod/transaction";
import { parseCSV, parseMT940, parseCODA, CSVRow } from "@/lib/csv";
import { generateTransactionId, Transaction } from "@/lib/mock/transactions";
import { Upload, FileText, AlertTriangle, CheckCircle } from "lucide-react";

interface ImportDialogProps {
  open: boolean;
  onClose: () => void;
  onImport: (transactions: Transaction[]) => void;
}

type ImportStep = 'upload' | 'mapping' | 'preview' | 'complete';

export function ImportDialog({ open, onClose, onImport }: ImportDialogProps) {
  const [step, setStep] = useState<ImportStep>('upload');
  const [fileName, setFileName] = useState('');
  const [fileType, setFileType] = useState<'csv' | 'mt940' | 'coda'>('csv');
  const [headers, setHeaders] = useState<string[]>([]);
  const [rows, setRows] = useState<CSVRow[]>([]);
  const [mappedTransactions, setMappedTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const form = useForm<ImportMappingData>({
    resolver: zodResolver(ImportMappingSchema),
    defaultValues: {
      dateColumn: '',
      amountColumn: '',
      descriptionColumn: '',
      categoryColumn: ''
    }
  });

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setLoading(true);
    setFileName(file.name);

    try {
      const content = await file.text();
      let parseResult;

      // Determine file type and parse accordingly
      if (file.name.toLowerCase().endsWith('.csv')) {
        setFileType('csv');
        parseResult = parseCSV(content);
      } else if (file.name.toLowerCase().includes('mt940')) {
        setFileType('mt940');
        parseResult = parseMT940(content);
      } else if (file.name.toLowerCase().includes('coda')) {
        setFileType('coda');
        parseResult = parseCODA(content);
      } else {
        // Default to CSV
        setFileType('csv');
        parseResult = parseCSV(content);
      }

      if (parseResult.success) {
        setHeaders(parseResult.headers);
        setRows(parseResult.rows.slice(0, 10)); // Preview first 10 rows
        setStep('mapping');
      } else {
        alert(parseResult.error || 'Fout bij het parseren van het bestand');
      }
    } catch (error) {
      alert('Fout bij het lezen van het bestand');
    } finally {
      setLoading(false);
    }
  };

  const handleMapping = (data: ImportMappingData) => {
    setLoading(true);

    try {
      const transactions: Transaction[] = rows.map(row => {
        const dateStr = row[data.dateColumn];
        const amountStr = row[data.amountColumn];
        const description = data.descriptionColumn ? row[data.descriptionColumn] : '';
        const category = data.categoryColumn ? row[data.categoryColumn] : 'Onbekend';

        // Parse amount and determine type
        const amount = Math.abs(parseFloat(amountStr.replace(',', '.')) || 0);
        const isNegative = amountStr.includes('-') || amountStr.startsWith('D') || row.type === 'D' || row.type === 'Debet';
        const type = isNegative ? 'EXPENSE' : 'INCOME';

        // Parse date (try different formats)
        let date = dateStr;
        if (dateStr.includes('/')) {
          const parts = dateStr.split('/');
          if (parts.length === 3) {
            date = `${parts[2]}-${parts[1].padStart(2, '0')}-${parts[0].padStart(2, '0')}`;
          }
        } else if (dateStr.includes('-') && dateStr.length === 8) {
          // Format: ddmmyyyy or yyyymmdd
          if (dateStr.startsWith('20')) {
            date = `${dateStr.substr(0, 4)}-${dateStr.substr(4, 2)}-${dateStr.substr(6, 2)}`;
          } else {
            date = `20${dateStr.substr(4, 2)}-${dateStr.substr(2, 2)}-${dateStr.substr(0, 2)}`;
          }
        }

        return {
          id: generateTransactionId(),
          date,
          type,
          category,
          amount,
          method: fileType === 'csv' ? 'OVERSCHRIJVING' : 'SEPA',
          description
        } as Transaction;
      });

      setMappedTransactions(transactions);
      setStep('preview');
    } catch (error) {
      alert('Fout bij het verwerken van de gegevens');
    } finally {
      setLoading(false);
    }
  };

  const handleImport = () => {
    onImport(mappedTransactions);
    setStep('complete');
  };

  const handleClose = () => {
    setStep('upload');
    setFileName('');
    setHeaders([]);
    setRows([]);
    setMappedTransactions([]);
    form.reset();
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    onClose();
  };

  const renderUploadStep = () => (
    <div className="space-y-6">
      <div className="text-center">
        <Upload className="mx-auto h-12 w-12 text-gray-400" />
        <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-gray-100">
          Upload bestand
        </h3>
        <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          Selecteer een CSV, MT940 of CODA bestand om te importeren
        </p>
      </div>

      <div className="mt-6">
        <input
          ref={fileInputRef}
          type="file"
          accept=".csv,.txt,.mt940,.coda"
          onChange={handleFileUpload}
          className="hidden"
          data-testid="input-file-upload"
        />
        <Button
          onClick={() => fileInputRef.current?.click()}
          className="w-full"
          disabled={loading}
          data-testid="button-select-file"
        >
          {loading ? 'Bezig met laden...' : 'Bestand selecteren'}
        </Button>
      </div>

      <div className="text-xs text-gray-500 dark:text-gray-400 space-y-1">
        <p><strong>Ondersteunde formaten:</strong></p>
        <ul className="list-disc list-inside space-y-1">
          <li>CSV: Comma/semicolon separated values</li>
          <li>MT940: SWIFT bank statement format</li>
          <li>CODA: Belgian bank statement format</li>
        </ul>
      </div>
    </div>
  );

  const renderMappingStep = () => (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-medium">Kolom mapping</h3>
        <p className="text-sm text-gray-500 dark:text-gray-400">
          Koppel de kolommen uit je bestand aan de juiste velden
        </p>
      </div>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(handleMapping)} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="dateColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Datum kolom *</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-date-column">
                        <SelectValue placeholder="Selecteer kolom" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="amountColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Bedrag kolom *</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-amount-column">
                        <SelectValue placeholder="Selecteer kolom" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="descriptionColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Omschrijving kolom</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-description-column">
                        <SelectValue placeholder="Selecteer kolom (optioneel)" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="">Geen</SelectItem>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="categoryColumn"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Categorie kolom</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-category-column">
                        <SelectValue placeholder="Selecteer kolom (optioneel)" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="">Geen</SelectItem>
                      {headers.map(header => (
                        <SelectItem key={header} value={header}>{header}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          {/* Preview of first few rows */}
          <div className="mt-6">
            <h4 className="text-sm font-medium mb-2">Voorbeeld data:</h4>
            <div className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden">
              <Table>
                <TableHeader>
                  <TableRow>
                    {headers.map(header => (
                      <TableHead key={header} className="text-xs">{header}</TableHead>
                    ))}
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {rows.slice(0, 3).map((row, index) => (
                    <TableRow key={index}>
                      {headers.map(header => (
                        <TableCell key={header} className="text-xs">
                          {row[header]}
                        </TableCell>
                      ))}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          </div>

          <div className="flex justify-between pt-4">
            <Button type="button" variant="outline" onClick={() => setStep('upload')}>
              Terug
            </Button>
            <Button type="submit" disabled={loading} data-testid="button-continue-mapping">
              {loading ? 'Verwerken...' : 'Doorgaan'}
            </Button>
          </div>
        </form>
      </Form>
    </div>
  );

  const renderPreviewStep = () => (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-medium">Voorbeeld import</h3>
        <p className="text-sm text-gray-500 dark:text-gray-400">
          Controleer de geÃ¯mporteerde transacties voordat je ze toevoegt
        </p>
      </div>

      <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
        <div className="flex items-center gap-2">
          <FileText className="h-5 w-5 text-blue-600" />
          <span className="text-sm font-medium">
            {mappedTransactions.length} transacties gevonden in {fileName}
          </span>
        </div>
      </div>

      <div className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden max-h-80 overflow-y-auto">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Datum</TableHead>
              <TableHead>Type</TableHead>
              <TableHead>Categorie</TableHead>
              <TableHead>Bedrag</TableHead>
              <TableHead>Omschrijving</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {mappedTransactions.slice(0, 20).map((transaction, index) => (
              <TableRow key={index}>
                <TableCell className="text-xs">{transaction.date}</TableCell>
                <TableCell>
                  <Badge variant={transaction.type === 'INCOME' ? 'secondary' : 'destructive'}>
                    {transaction.type === 'INCOME' ? 'Inkomst' : 'Uitgave'}
                  </Badge>
                </TableCell>
                <TableCell className="text-xs">{transaction.category}</TableCell>
                <TableCell className="text-xs">â‚¬{transaction.amount.toFixed(2)}</TableCell>
                <TableCell className="text-xs max-w-[200px] truncate">
                  {transaction.description || '-'}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      {mappedTransactions.length > 20 && (
        <p className="text-xs text-gray-500 dark:text-gray-400">
          ... en nog {mappedTransactions.length - 20} transacties
        </p>
      )}

      <div className="flex justify-between pt-4">
        <Button type="button" variant="outline" onClick={() => setStep('mapping')}>
          Terug
        </Button>
        <Button onClick={handleImport} data-testid="button-confirm-import">
          Transacties importeren
        </Button>
      </div>
    </div>
  );

  const renderCompleteStep = () => (
    <div className="text-center space-y-6">
      <div>
        <CheckCircle className="mx-auto h-12 w-12 text-green-500" />
        <h3 className="mt-2 text-lg font-medium text-gray-900 dark:text-gray-100">
          Import voltooid!
        </h3>
        <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          {mappedTransactions.length} transacties zijn succesvol geÃ¯mporteerd
        </p>
      </div>

      <Button onClick={handleClose} className="w-full" data-testid="button-close-import">
        Sluiten
      </Button>
    </div>
  );

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-[800px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Transacties importeren</DialogTitle>
          <DialogDescription>
            Import transacties vanuit CSV, MT940 of CODA bestanden
          </DialogDescription>
        </DialogHeader>

        <div className="py-6">
          {step === 'upload' && renderUploadStep()}
          {step === 'mapping' && renderMappingStep()}
          {step === 'preview' && renderPreviewStep()}
          {step === 'complete' && renderCompleteStep()}
        </div>
      </DialogContent>
    </Dialog>
  );
}

components/finance/KpiCards.tsx.content:
"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { formatCurrencyBE } from "@/lib/format";
import { transactions, getTotalByType } from "@/lib/mock/transactions";
import { TrendingUp, TrendingDown, DollarSign, AlertTriangle } from "lucide-react";
import { useMemo } from "react";

export function KpiCards() {
  const kpiData = useMemo(() => {
    const totalIncome = getTotalByType('INCOME');
    const totalExpense = getTotalByType('EXPENSE');
    const balance = totalIncome - totalExpense;

    // Mock achterstallig bedrag (in real app, this would come from overdue fees)
    const overdue = 1450;

    return {
      income: totalIncome,
      expense: totalExpense,
      balance,
      overdue
    };
  }, []);

  const kpis = [
    {
      title: "Ontvangen",
      value: kpiData.income,
      icon: TrendingUp,
      color: "text-green-600",
      bgColor: "bg-green-50 dark:bg-green-900/10",
      description: "Totale inkomsten"
    },
    {
      title: "Uitgegeven",
      value: kpiData.expense,
      icon: TrendingDown,
      color: "text-red-600",
      bgColor: "bg-red-50 dark:bg-red-900/10",
      description: "Totale uitgaven"
    },
    {
      title: "Saldo",
      value: kpiData.balance,
      icon: DollarSign,
      color: kpiData.balance >= 0 ? "text-green-600" : "text-red-600",
      bgColor: kpiData.balance >= 0 
        ? "bg-green-50 dark:bg-green-900/10" 
        : "bg-red-50 dark:bg-red-900/10",
      description: "Netto resultaat"
    },
    {
      title: "Achterstallig",
      value: kpiData.overdue,
      icon: AlertTriangle,
      color: "text-orange-600",
      bgColor: "bg-orange-50 dark:bg-orange-900/10",
      description: "Te betalen lidgeld"
    }
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {kpis.map((kpi) => {
        const Icon = kpi.icon;
        return (
          <Card key={kpi.title} className="relative overflow-hidden">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-gray-600 dark:text-gray-400">
                {kpi.title}
              </CardTitle>
              <div className={`p-2 rounded-lg ${kpi.bgColor}`}>
                <Icon className={`h-4 w-4 ${kpi.color}`} />
              </div>
            </CardHeader>
            <CardContent>
              <div className={`text-2xl font-bold ${kpi.color}`}>
                {formatCurrencyBE(kpi.value)}
              </div>
              <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                {kpi.description}
              </p>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
}

components/finance/NewTransactionDialog..content:
"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { TransactionSchema, TransactionFormData } from "@/lib/zod/transaction";
import { categories, mockMembers, Transaction, generateTransactionId } from "@/lib/mock/transactions";
import { format } from "date-fns";

interface NewTransactionDialogProps {
  open: boolean;
  onClose: () => void;
  onSave: (transaction: Transaction) => void;
  editTransaction?: Transaction | null;
}

export function NewTransactionDialog({
  open,
  onClose,
  onSave,
  editTransaction
}: NewTransactionDialogProps) {
  const [loading, setLoading] = useState(false);

  const isEditing = !!editTransaction;

  const form = useForm<TransactionFormData>({
    resolver: zodResolver(TransactionSchema),
    defaultValues: {
      type: editTransaction?.type || 'INCOME',
      date: editTransaction?.date || format(new Date(), 'yyyy-MM-dd'),
      category: editTransaction?.category || '',
      amount: editTransaction?.amount || 0,
      method: editTransaction?.method || 'SEPA',
      memberId: editTransaction?.memberId || '',
      memberName: editTransaction?.memberName || '',
      description: editTransaction?.description || ''
    }
  });

  const selectedType = form.watch('type');
  const selectedMemberId = form.watch('memberId');

  // Update member name when member is selected
  const handleMemberChange = (memberId: string) => {
    const member = mockMembers.find(m => m.id === memberId);
    form.setValue('memberName', member?.name || '');
  };

  const onSubmit = async (data: TransactionFormData) => {
    setLoading(true);
    try {
      const transaction: Transaction = {
        id: editTransaction?.id || generateTransactionId(),
        date: data.date,
        type: data.type,
        category: data.category,
        amount: data.amount,
        method: data.method,
        memberId: data.memberId || undefined,
        memberName: data.memberName || undefined,
        description: data.description || undefined
      };

      onSave(transaction);

      if (!isEditing) {
        form.reset();
      }
      onClose();
    } catch (error) {
      console.error('Error saving transaction:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    if (!isEditing) {
      form.reset();
    }
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-[600px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>
            {isEditing ? 'Transactie bewerken' : 'Nieuwe transactie'}
          </DialogTitle>
          <DialogDescription>
            {isEditing 
              ? 'Wijzig de details van deze transactie.'
              : 'Voeg een nieuwe inkomst of uitgave toe aan je financiÃ«le administratie.'
            }
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {/* Type */}
            <FormField
              control={form.control}
              name="type"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Type *</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-transaction-type">
                        <SelectValue placeholder="Selecteer type" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="INCOME">Inkomsten</SelectItem>
                      <SelectItem value="EXPENSE">Uitgaven</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Date and Amount Row */}
            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="date"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Datum *</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        {...field}
                        data-testid="input-transaction-date"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="amount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Bedrag (â‚¬) *</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        step="0.01"
                        min="0"
                        placeholder="0,00"
                        {...field}
                        onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                        data-testid="input-transaction-amount"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            {/* Category */}
            <FormField
              control={form.control}
              name="category"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Categorie *</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-transaction-category">
                        <SelectValue placeholder="Selecteer categorie" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {categories[selectedType].map((category) => (
                        <SelectItem key={category} value={category}>
                          {category}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Payment Method */}
            <FormField
              control={form.control}
              name="method"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Betaalmethode *</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-transaction-method">
                        <SelectValue placeholder="Selecteer methode" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="SEPA">SEPA</SelectItem>
                      <SelectItem value="OVERSCHRIJVING">Overschrijving</SelectItem>
                      <SelectItem value="BANCONTACT">Bancontact</SelectItem>
                      <SelectItem value="CASH">Contant</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Member (Optional) */}
            <FormField
              control={form.control}
              name="memberId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Lid (optioneel)</FormLabel>
                  <Select 
                    onValueChange={(value) => {
                      field.onChange(value);
                      handleMemberChange(value);
                    }} 
                    defaultValue={field.value}
                  >
                    <FormControl>
                      <SelectTrigger data-testid="select-transaction-member">
                        <SelectValue placeholder="Selecteer lid" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="">Geen lid gekoppeld</SelectItem>
                      {mockMembers.map((member) => (
                        <SelectItem key={member.id} value={member.id}>
                          {member.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormDescription>
                    Koppel deze transactie aan een specifiek lid (bijv. voor lidgeld)
                  </FormDescription>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Description */}
            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Omschrijving (optioneel)</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Voeg een omschrijving toe..."
                      className="resize-none"
                      rows={3}
                      {...field}
                      data-testid="input-transaction-description"
                    />
                  </FormControl>
                  <FormDescription>
                    Maximaal 500 karakters
                  </FormDescription>
                  <FormMessage />
                </FormItem>
              )}
            />

            <DialogFooter>
              <Button 
                type="button" 
                variant="outline" 
                onClick={handleClose}
                data-testid="button-cancel-transaction"
              >
                Annuleren
              </Button>
              <Button 
                type="submit" 
                disabled={loading}
                data-testid="button-save-transaction"
              >
                {loading ? 'Bezig...' : (isEditing ? 'Wijzigingen opslaan' : 'Transactie toevoegen')}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

components/finance/RowActions.tsx.content:
"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { MoreHorizontal, Eye, Edit, Trash2 } from "lucide-react";
import { Transaction } from "@/lib/mock/transactions";

interface RowActionsProps {
  transaction: Transaction;
  onView: (transaction: Transaction) => void;
  onEdit: (transaction: Transaction) => void;
  onDelete: (transactionId: string) => void;
}

export function RowActions({ transaction, onView, onEdit, onDelete }: RowActionsProps) {
  const [open, setOpen] = useState(false);

  const handleAction = (action: () => void) => {
    action();
    setOpen(false);
  };

  return (
    <DropdownMenu open={open} onOpenChange={setOpen}>
      <DropdownMenuTrigger asChild>
        <Button
          variant="ghost"
          size="sm"
          className="h-8 w-8 p-0"
          onClick={(e) => e.stopPropagation()}
          data-testid={`button-actions-${transaction.id}`}
        >
          <span className="sr-only">Acties openen</span>
          <MoreHorizontal className="h-4 w-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-[160px]">
        <DropdownMenuItem 
          onClick={(e) => {
            e.stopPropagation();
            handleAction(() => onView(transaction));
          }}
          data-testid={`menu-view-${transaction.id}`}
        >
          <Eye className="mr-2 h-4 w-4" />
          Details
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={(e) => {
            e.stopPropagation();
            handleAction(() => onEdit(transaction));
          }}
          data-testid={`menu-edit-${transaction.id}`}
        >
          <Edit className="mr-2 h-4 w-4" />
          Bewerken
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={(e) => {
            e.stopPropagation();
            handleAction(() => onDelete(transaction.id));
          }}
          className="text-red-600 dark:text-red-400"
          data-testid={`menu-delete-${transaction.id}`}
        >
          <Trash2 className="mr-2 h-4 w-4" />
          Verwijderen
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

components/finance/Toolbar.tsx.content:
"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Search, Download, Upload, Plus, Filter } from "lucide-react";

interface ToolbarProps {
  onSearch: (search: string) => void;
  onTypeFilter: (type: string) => void;
  onPeriodFilter: (period: string) => void;
  onNewTransaction: () => void;
  onImport: () => void;
  onExport: () => void;
  onAdvancedFilters: () => void;
  searchValue: string;
  typeFilter: string;
  periodFilter: string;
}

export function Toolbar({
  onSearch,
  onTypeFilter,
  onPeriodFilter,
  onNewTransaction,
  onImport,
  onExport,
  onAdvancedFilters,
  searchValue,
  typeFilter,
  periodFilter
}: ToolbarProps) {
  return (
    <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between bg-white dark:bg-gray-900 p-4 rounded-lg border border-gray-200 dark:border-gray-700">
      {/* Search and Quick Filters */}
      <div className="flex flex-col sm:flex-row gap-3 flex-1 w-full sm:w-auto">
        {/* Search Bar */}
        <div className="relative flex-1 min-w-0 sm:max-w-sm">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
          <Input
            placeholder="Zoek op categorie, omschrijving of lidnummer..."
            value={searchValue}
            onChange={(e) => onSearch(e.target.value)}
            className="pl-10"
            data-testid="input-search"
          />
        </div>

        {/* Type Filter */}
        <Select value={typeFilter} onValueChange={onTypeFilter}>
          <SelectTrigger className="w-full sm:w-40" data-testid="select-type-filter">
            <SelectValue placeholder="Type" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="ALL">Alle types</SelectItem>
            <SelectItem value="INCOME">Inkomsten</SelectItem>
            <SelectItem value="EXPENSE">Uitgaven</SelectItem>
          </SelectContent>
        </Select>

        {/* Period Filter */}
        <Select value={periodFilter} onValueChange={onPeriodFilter}>
          <SelectTrigger className="w-full sm:w-40" data-testid="select-period-filter">
            <SelectValue placeholder="Periode" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="ALL">Alle periodes</SelectItem>
            <SelectItem value="THIS_MONTH">Deze maand</SelectItem>
            <SelectItem value="LAST_MONTH">Vorige maand</SelectItem>
            <SelectItem value="THIS_YEAR">Dit jaar</SelectItem>
            <SelectItem value="LAST_YEAR">Vorig jaar</SelectItem>
          </SelectContent>
        </Select>

        {/* Advanced Filters Button */}
        <Button
          variant="outline"
          onClick={onAdvancedFilters}
          className="gap-2"
          data-testid="button-advanced-filters"
        >
          <Filter className="h-4 w-4" />
          Filters
        </Button>
      </div>

      {/* Action Buttons */}
      <div className="flex gap-2 w-full sm:w-auto">
        <Button
          variant="outline"
          onClick={onImport}
          className="gap-2 flex-1 sm:flex-none"
          data-testid="button-import"
        >
          <Upload className="h-4 w-4" />
          Import
        </Button>
        <Button
          variant="outline"
          onClick={onExport}
          className="gap-2 flex-1 sm:flex-none"
          data-testid="button-export"
        >
          <Download className="h-4 w-4" />
          Export
        </Button>
        <Button
          onClick={onNewTransaction}
          className="gap-2 flex-1 sm:flex-none"
          data-testid="button-new-transaction"
        >
          <Plus className="h-4 w-4" />
          Nieuwe transactie
        </Button>
      </div>
    </div>
  );
}

components/finance/TransactionsTable.tsx.content:
"use client";

import { useState, useMemo } from "react";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { StatusChip } from "@/components/ui/StatusChip";
import { MethodChip } from "@/components/ui/MethodChip";
import { EmptyState } from "@/components/ui/EmptyState";
import { formatDateBE, formatCurrencyBE } from "@/lib/format";
import { Transaction } from "@/lib/mock/transactions";
import { FilterData } from "@/lib/zod/transaction";
import { ChevronUp, ChevronDown, MoreHorizontal } from "lucide-react";
import { RowActions } from "./RowActions";

interface TransactionsTableProps {
  transactions: Transaction[];
  filters: FilterData;
  onTransactionSelect: (transaction: Transaction) => void;
  onTransactionEdit: (transaction: Transaction) => void;
  onTransactionDelete: (transactionId: string) => void;
  loading?: boolean;
}

type SortField = 'date' | 'amount' | 'category' | 'type';
type SortDirection = 'asc' | 'desc';

export function TransactionsTable({
  transactions,
  filters,
  onTransactionSelect,
  onTransactionEdit,
  onTransactionDelete,
  loading = false
}: TransactionsTableProps) {
  const [sortField, setSortField] = useState<SortField>('date');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 20;

  // Filter and sort transactions
  const filteredAndSortedTransactions = useMemo(() => {
    let filtered = transactions;

    // Apply filters
    if (filters.search) {
      const search = filters.search.toLowerCase();
      filtered = filtered.filter(t => 
        t.category.toLowerCase().includes(search) ||
        (t.description?.toLowerCase().includes(search)) ||
        (t.memberName?.toLowerCase().includes(search))
      );
    }

    if (filters.type && filters.type !== 'ALL') {
      filtered = filtered.filter(t => t.type === filters.type);
    }

    if (filters.category) {
      filtered = filtered.filter(t => t.category === filters.category);
    }

    if (filters.method && filters.method !== 'ALL') {
      filtered = filtered.filter(t => t.method === filters.method);
    }

    if (filters.memberId) {
      filtered = filtered.filter(t => t.memberId === filters.memberId);
    }

    if (filters.dateFrom) {
      filtered = filtered.filter(t => t.date >= filters.dateFrom!);
    }

    if (filters.dateTo) {
      filtered = filtered.filter(t => t.date <= filters.dateTo!);
    }

    if (filters.amountMin) {
      filtered = filtered.filter(t => t.amount >= filters.amountMin!);
    }

    if (filters.amountMax) {
      filtered = filtered.filter(t => t.amount <= filters.amountMax!);
    }

    // Sort
    filtered.sort((a, b) => {
      let aValue: any, bValue: any;

      switch (sortField) {
        case 'date':
          aValue = new Date(a.date);
          bValue = new Date(b.date);
          break;
        case 'amount':
          aValue = a.amount;
          bValue = b.amount;
          break;
        case 'category':
          aValue = a.category;
          bValue = b.category;
          break;
        case 'type':
          aValue = a.type;
          bValue = b.type;
          break;
        default:
          return 0;
      }

      if (aValue < bValue) return sortDirection === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortDirection === 'asc' ? 1 : -1;
      return 0;
    });

    return filtered;
  }, [transactions, filters, sortField, sortDirection]);

  // Pagination
  const totalPages = Math.ceil(filteredAndSortedTransactions.length / itemsPerPage);
  const paginatedTransactions = filteredAndSortedTransactions.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const handleSort = (field: SortField) => {
    if (field === sortField) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const SortIcon = ({ field }: { field: SortField }) => {
    if (sortField !== field) return null;
    return sortDirection === 'asc' ? 
      <ChevronUp className="w-4 h-4" /> : 
      <ChevronDown className="w-4 h-4" />;
  };

  if (loading) {
    return (
      <div className="animate-pulse">
        <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded mb-4"></div>
        {Array.from({ length: 5 }).map((_, i) => (
          <div key={i} className="h-12 bg-gray-100 dark:bg-gray-800 rounded mb-2"></div>
        ))}
      </div>
    );
  }

  if (filteredAndSortedTransactions.length === 0) {
    return (
      <EmptyState
        title="Geen transacties gevonden"
        description="Er zijn geen transacties die voldoen aan de geselecteerde filters."
      />
    );
  }

  return (
    <div className="space-y-4">
      {/* Table */}
      <div className="rounded-lg border border-gray-200 dark:border-gray-700 overflow-hidden">
        <Table>
          <TableHeader>
            <TableRow className="bg-gray-50 dark:bg-gray-900">
              <TableHead 
                className="cursor-pointer select-none hover:bg-gray-100 dark:hover:bg-gray-800"
                onClick={() => handleSort('date')}
              >
                <div className="flex items-center gap-2">
                  Datum
                  <SortIcon field="date" />
                </div>
              </TableHead>
              <TableHead 
                className="cursor-pointer select-none hover:bg-gray-100 dark:hover:bg-gray-800"
                onClick={() => handleSort('type')}
              >
                <div className="flex items-center gap-2">
                  Type
                  <SortIcon field="type" />
                </div>
              </TableHead>
              <TableHead 
                className="cursor-pointer select-none hover:bg-gray-100 dark:hover:bg-gray-800"
                onClick={() => handleSort('category')}
              >
                <div className="flex items-center gap-2">
                  Categorie
                  <SortIcon field="category" />
                </div>
              </TableHead>
              <TableHead>Lid</TableHead>
              <TableHead 
                className="cursor-pointer select-none hover:bg-gray-100 dark:hover:bg-gray-800 text-right"
                onClick={() => handleSort('amount')}
              >
                <div className="flex items-center justify-end gap-2">
                  Bedrag
                  <SortIcon field="amount" />
                </div>
              </TableHead>
              <TableHead>Methode</TableHead>
              <TableHead>Omschrijving</TableHead>
              <TableHead className="w-[50px]">Acties</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {paginatedTransactions.map((transaction) => (
              <TableRow 
                key={transaction.id}
                className="cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-900"
                onClick={() => onTransactionSelect(transaction)}
                data-testid={`row-transaction-${transaction.id}`}
              >
                <TableCell className="font-medium">
                  {formatDateBE(transaction.date)}
                </TableCell>
                <TableCell>
                  <StatusChip type={transaction.type} />
                </TableCell>
                <TableCell>{transaction.category}</TableCell>
                <TableCell>
                  {transaction.memberName ? (
                    <span className="text-blue-600 dark:text-blue-400">
                      {transaction.memberName}
                    </span>
                  ) : (
                    <span className="text-gray-400">-</span>
                  )}
                </TableCell>
                <TableCell className="text-right font-medium">
                  <span className={transaction.type === 'INCOME' 
                    ? 'text-green-600 dark:text-green-400' 
                    : 'text-red-600 dark:text-red-400'
                  }>
                    {formatCurrencyBE(transaction.amount)}
                  </span>
                </TableCell>
                <TableCell>
                  <MethodChip method={transaction.method} />
                </TableCell>
                <TableCell className="max-w-[200px] truncate">
                  {transaction.description || '-'}
                </TableCell>
                <TableCell>
                  <RowActions
                    transaction={transaction}
                    onEdit={onTransactionEdit}
                    onDelete={onTransactionDelete}
                    onView={onTransactionSelect}
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-gray-500 dark:text-gray-400">
            Toont {(currentPage - 1) * itemsPerPage + 1} tot {Math.min(currentPage * itemsPerPage, filteredAndSortedTransactions.length)} van {filteredAndSortedTransactions.length} transacties
          </div>
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
              disabled={currentPage === 1}
              data-testid="button-prev-page"
            >
              Vorige
            </Button>
            <span className="flex items-center px-3 text-sm">
              Pagina {currentPage} van {totalPages}
            </span>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
              disabled={currentPage === totalPages}
              data-testid="button-next-page"
            >
              Volgende
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

components/ui/ConfirmDialog.tsx.content:
"use client";

import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface ConfirmDialogProps {
  open: boolean;
  onClose: () => void;
  onConfirm: () => void;
  title?: string;
  description?: string;
  confirmText?: string;
  cancelText?: string;
  variant?: 'default' | 'destructive';
}

export function ConfirmDialog({
  open,
  onClose,
  onConfirm,
  title = "Weet je het zeker?",
  description = "Deze actie kan niet ongedaan worden gemaakt.",
  confirmText = "Doorgaan",
  cancelText = "Annuleren",
  variant = 'default'
}: ConfirmDialogProps) {
  const handleConfirm = () => {
    onConfirm();
    onClose();
  };

  return (
    <AlertDialog open={open} onOpenChange={onClose}>
      <AlertDialogContent>
        <AlertDialogHeader>
          <AlertDialogTitle>{title}</AlertDialogTitle>
          <AlertDialogDescription>
            {description}
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter>
          <AlertDialogCancel onClick={onClose}>
            {cancelText}
          </AlertDialogCancel>
          <AlertDialogAction
            onClick={handleConfirm}
            className={variant === 'destructive' 
              ? 'bg-red-600 hover:bg-red-700 dark:bg-red-600 dark:hover:bg-red-700' 
              : ''
            }
          >
            {confirmText}
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

