import { z } from 'zod';

export const TransactionSchema = z.object({
  type: z.enum(['INCOME', 'EXPENSE'], {
    required_error: 'Type is verplicht',
    invalid_type_error: 'Selecteer Inkomsten of Uitgaven'
  }),
