import { Request, Response } from 'express';
import { z } from 'zod';
import { db } from '../../db';
import { members, memberFinancialSettings, tenants } from '../../../shared/schema';
import { eq } from 'drizzle-orm';
import { createFirstFeeForMember } from '../../../lib/server/fees/first-fee';
import { beNow } from '../../../lib/server/time';

const createMemberSchema = z.object({
  memberNumber: z.string(),
  firstName: z.string(),
  lastName: z.string(),
  gender: z.enum(['M', 'V']),
  birthDate: z.string().optional().transform(val => val ? new Date(val) : undefined),
  category: z.enum(['STUDENT', 'VOLWASSEN', 'SENIOR']),
  email: z.string().email().optional(),
  phone: z.string().optional(),
  street: z.string().optional(),
  number: z.string().optional(),
  postalCode: z.string().optional(),
  city: z.string().optional(),
  country: z.string().optional(),
  joinDate: z.string().optional().transform(val => val ? new Date(val) : undefined),

  // Financial settings
  preferredMethod: z.enum(['SEPA', 'OVERSCHRIJVING', 'BANCONTACT', 'CASH']),
  preferredTerm: z.enum(['MONTHLY', 'YEARLY']),
  monthlyAmount: z.number().default(0),
  yearlyAmount: z.number().default(0),
  iban: z.string().optional(),
  sepaMandate: z.string().optional(),
});

export async function createMemberHandler(req: Request, res: Response) {
  try {
    const tenantId = (req as any).tenantId;
    if (!tenantId) {
      return res.status(400).json({ error: 'Tenant context required' });
    }

    const body = createMemberSchema.parse(req.body);
    const joinDate = body.joinDate || beNow();

    // Start transaction
    const result = await db.transaction(async (tx) => {
      // Create member
      const [member] = await tx.insert(members).values({
        tenantId,
        memberNumber: body.memberNumber,
        firstName: body.firstName,
        lastName: body.lastName,
        gender: body.gender,
        birthDate: body.birthDate,
        category: body.category,
        email: body.email,
        phone: body.phone,
        street: body.street,
        number: body.number,
        postalCode: body.postalCode,
        city: body.city,
        country: body.country,
        active: true
      }).returning();

      // Get tenant settings for calculating amounts
      const tenant = await tx.select().from(tenants).where(eq(tenants.id, tenantId)).limit(1);
      const tenantData = tenant[0];

      // Calculate amounts based on category and tenant settings
      let yearlyAmount = parseFloat(tenantData?.adultFee || '25.00'); // Default for VOLWASSEN
      if (body.category === 'STUDENT') yearlyAmount = parseFloat(tenantData?.studentFee || '15.00');
      if (body.category === 'SENIOR') yearlyAmount = parseFloat(tenantData?.seniorFee || '20.00');

      const monthlyAmount = yearlyAmount / 12;

      // Create financial settings
      await tx.insert(memberFinancialSettings).values({
        memberId: member.id,
        paymentMethod: body.preferredMethod,
        paymentTerm: body.preferredTerm,
        monthlyAmount: monthlyAmount.toString(),
        yearlyAmount: yearlyAmount.toString(),
        iban: body.iban
      });

      return member;
    });

    // Create first fee (outside transaction to avoid issues)
    try {
      await createFirstFeeForMember(result.id, joinDate);
    } catch (feeError) {
      console.error('Failed to create first fee:', feeError);
      // Don't fail the member creation, just log the error
    }

    res.status(201).json({
      success: true,
      member: result,
      message: 'Member created successfully with first fee'
    });

  } catch (error) {
    console.error('Member creation error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ 
      error: 'Internal server error',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
}

tests/fees/generator.rolling.test.ts.content:
import { describe, it, expect, beforeEach, vi } from 'vitest';
import { ensureCurrentRollingFeeForMember, generateTenantFees } from '../../lib/server/fees/generator';

// Mock dependencies
vi.mock('../../lib/server/db', () => ({
  db: {
    query: {
      members: {
        findFirst: vi.fn(),
        findMany: vi.fn()
      },
      membershipFees: {
        findFirst: vi.fn()
      }
    },
    insert: vi.fn().mockReturnValue({
      values: vi.fn()
    })
  }
}));

vi.mock('../../lib/server/fees/anchor', () => ({
  getBillingAnchor: vi.fn()
}));

vi.mock('../../lib/server/time', () => ({
  beNow: vi.fn(),
  toUtcISO: vi.fn((date) => date.toISOString())
}));

import { db } from '../../lib/server/db';
import { getBillingAnchor } from '../../lib/server/fees/anchor';
import { beNow } from '../../lib/server/time';

describe('Fee Generator', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  describe('ensureCurrentRollingFeeForMember', () => {
    it('should create fee for active member with no existing fee', async () => {
      const memberId = 'member-123';
      const anchor = new Date('2025-01-15T10:00:00.000Z');
      const asOf = new Date('2025-06-01T12:00:00.000Z');

      // Mock getBillingAnchor
      (getBillingAnchor as any).mockResolvedValue(anchor);

      // Mock member with financial settings
      (db.query.members.findFirst as any).mockResolvedValue({
        id: memberId,
        tenantId: 'tenant-123',
        memberNumber: 'M001',
        firstName: 'John',
        lastName: 'Doe',
        isActive: true,
        financialSettings: {
          preferredTerm: 'MONTHLY',
          preferredMethod: 'SEPA',
          monthlyAmount: 25.00,
          yearlyAmount: 300.00,
          sepaMandate: 'MANDATE-123'
        }
      });

      // Mock no existing fee
      (db.query.membershipFees.findFirst as any).mockResolvedValue(null);

      // Mock insert
      const mockInsert = {
        values: vi.fn()
      };
      (db.insert as any).mockReturnValue(mockInsert);

      await ensureCurrentRollingFeeForMember(memberId, asOf);

      expect(getBillingAnchor).toHaveBeenCalledWith(memberId);
      expect(db.query.members.findFirst).toHaveBeenCalled();
      expect(db.query.membershipFees.findFirst).toHaveBeenCalled();
      expect(db.insert).toHaveBeenCalled();
      expect(mockInsert.values).toHaveBeenCalledWith(
        expect.objectContaining({
          tenantId: 'tenant-123',
          memberId,
          memberNumber: 'M001',
          memberName: 'John Doe',
          amount: 25.00,
          method: 'SEPA',
          status: 'OPEN',
          sepaEligible: true
        })
      );
    });

    it('should skip inactive members', async () => {
      const memberId = 'member-456';

      (getBillingAnchor as any).mockResolvedValue(new Date());
      (db.query.members.findFirst as any).mockResolvedValue({
        id: memberId,
        isActive: false
      });

      await ensureCurrentRollingFeeForMember(memberId);

      expect(db.query.membershipFees.findFirst).not.toHaveBeenCalled();
      expect(db.insert).not.toHaveBeenCalled();
    });

    it('should skip members with zero amount', async () => {
      const memberId = 'member-789';

      (getBillingAnchor as any).mockResolvedValue(new Date());
      (db.query.members.findFirst as any).mockResolvedValue({
        id: memberId,
        isActive: true,
        financialSettings: {
          preferredTerm: 'MONTHLY',
          monthlyAmount: 0,
          yearlyAmount: 0
        }
      });

      await ensureCurrentRollingFeeForMember(memberId);

      expect(db.query.membershipFees.findFirst).not.toHaveBeenCalled();
      expect(db.insert).not.toHaveBeenCalled();
    });

    it('should not duplicate existing fees', async () => {
      const memberId = 'member-existing';

      (getBillingAnchor as any).mockResolvedValue(new Date('2025-01-15T10:00:00.000Z'));
      (db.query.members.findFirst as any).mockResolvedValue({
        id: memberId,
        isActive: true,
        financialSettings: {
          preferredTerm: 'MONTHLY',
          monthlyAmount: 25.00
        }
      });

      // Mock existing fee
      (db.query.membershipFees.findFirst as any).mockResolvedValue({
        id: 'fee-123',
        memberId
      });

      await ensureCurrentRollingFeeForMember(memberId);

      expect(db.insert).not.toHaveBeenCalled();
    });
  });

  describe('generateTenantFees', () => {
    it('should process all active members in tenant', async () => {
      const tenantId = 'tenant-abc';
      const members = [
        {
          id: 'member-1',
          isActive: true,
          financialSettings: { preferredTerm: 'MONTHLY', monthlyAmount: 25 }
        },
        {
          id: 'member-2',
          isActive: true,
          financialSettings: { preferredTerm: 'YEARLY', yearlyAmount: 300 }
        }
      ];

      (db.query.members.findMany as any).mockResolvedValue(members);
      (beNow as any).mockReturnValue(new Date('2025-06-01T12:00:00.000Z'));

      // Mock the other calls for each member
      (getBillingAnchor as any).mockResolvedValue(new Date('2025-01-15T10:00:00.000Z'));
      (db.query.members.findFirst as any).mockImplementation(({ where }) => {
        const memberId = where.toString(); // Simplified for test
        return Promise.resolve(members.find(m => m.id.includes(memberId.slice(-1))));
      });
      (db.query.membershipFees.findFirst as any).mockResolvedValue(null);

      const mockInsert = { values: vi.fn() };
      (db.insert as any).mockReturnValue(mockInsert);

      await generateTenantFees(tenantId, undefined, 'current');

      expect(db.query.members.findMany).toHaveBeenCalledWith(
        expect.objectContaining({
          where: expect.anything(),
          with: { financialSettings: true }
        })
      );
    });

    it('should handle errors gracefully and continue with other members', async () => {
      const tenantId = 'tenant-error';
      const members = [
        { id: 'member-1', isActive: true, financialSettings: {} },
        { id: 'member-2', isActive: true, financialSettings: {} }
      ];

      (db.query.members.findMany as any).mockResolvedValue(members);
      (getBillingAnchor as any).mockImplementation((memberId) => {
        if (memberId === 'member-1') {
          throw new Error('Test error');
        }
        return Promise.resolve(new Date());
      });

      // Should not throw, but continue processing
      await expect(generateTenantFees(tenantId)).resolves.toBeUndefined();
    });
  });

  describe('Idempotency', () => {
    it('should not create duplicate fees when run multiple times', async () => {
      const memberId = 'member-idempotent';
      const anchor = new Date('2025-01-15T10:00:00.000Z');

      (getBillingAnchor as any).mockResolvedValue(anchor);
      (db.query.members.findFirst as any).mockResolvedValue({
        id: memberId,
        isActive: true,
        financialSettings: {
          preferredTerm: 'MONTHLY',
          monthlyAmount: 25.00
        }
      });

      let callCount = 0;
      (db.query.membershipFees.findFirst as any).mockImplementation(() => {
        callCount++;
        // First call: no existing fee, second call: fee exists
        return callCount === 1 ? null : { id: 'fee-123', memberId };
      });

      const mockInsert = { values: vi.fn() };
      (db.insert as any).mockReturnValue(mockInsert);

      // First run - should create fee
      await ensureCurrentRollingFeeForMember(memberId);
      expect(mockInsert.values).toHaveBeenCalledTimes(1);

      // Second run - should not create duplicate
      await ensureCurrentRollingFeeForMember(memberId);
      expect(mockInsert.values).toHaveBeenCalledTimes(1); // Still 1, not 2
    });
  });
});

tests/fees/periods.rolling.test.ts.content:
import { describe, it, expect } from 'vitest';
import {
  rollingMonthly,
  rollingYearly,
  addMonthsClamped,
  addYearsClamped,
  nextRollingPeriod,
  floorToRollingStart
} from '../../lib/server/fees/periods';

describe('Rolling Period Calculations', () => {
  describe('addMonthsClamped', () => {
    it('should handle month overflow correctly', () => {
      // January 31 + 1 month should clamp to February 28/29
      const anchor = new Date('2025-01-31T10:00:00.000Z');
      const result = addMonthsClamped(anchor, 1);

      // Should be February 28, 2025 (not a leap year)
      expect(result.getUTCDate()).toBe(28);
      expect(result.getUTCMonth()).toBe(1); // February (0-indexed)
      expect(result.getUTCFullYear()).toBe(2025);
    });

    it('should handle leap year correctly', () => {
      // January 31, 2024 + 1 month should clamp to February 29 (leap year)
      const anchor = new Date('2024-01-31T10:00:00.000Z');
      const result = addMonthsClamped(anchor, 1);

      expect(result.getUTCDate()).toBe(29);
      expect(result.getUTCMonth()).toBe(1); // February
      expect(result.getUTCFullYear()).toBe(2024);
    });
  });

  describe('addYearsClamped', () => {
    it('should handle leap year to non-leap year', () => {
      // February 29, 2024 + 1 year should clamp to February 28, 2025
      const anchor = new Date('2024-02-29T10:00:00.000Z');
      const result = addYearsClamped(anchor, 1);

      expect(result.getUTCDate()).toBe(28);
      expect(result.getUTCMonth()).toBe(1); // February
      expect(result.getUTCFullYear()).toBe(2025);
    });

    it('should preserve normal dates', () => {
      const anchor = new Date('2025-03-15T10:00:00.000Z');
      const result = addYearsClamped(anchor, 1);

      expect(result.getUTCDate()).toBe(15);
      expect(result.getUTCMonth()).toBe(2); // March
      expect(result.getUTCFullYear()).toBe(2026);
    });
  });

  describe('rollingMonthly', () => {
    it('should calculate current monthly period correctly', () => {
      const anchor = new Date('2025-03-15T10:00:00.000Z');
      const asOf = new Date('2025-06-01T12:00:00.000Z');

      const result = rollingMonthly(anchor, asOf);

      // Current period should be 2025-05-15 10:00 to 2025-06-14 09:59:59.999
      expect(result.start.getUTCDate()).toBe(15);
      expect(result.start.getUTCMonth()).toBe(4); // May (0-indexed)
      expect(result.start.getUTCFullYear()).toBe(2025);

      expect(result.end.getUTCDate()).toBe(14);
      expect(result.end.getUTCMonth()).toBe(5); // June
      expect(result.end.getUTCFullYear()).toBe(2025);
    });

    it('should handle anchor clamping in February', () => {
      const anchor = new Date('2025-01-31T09:00:00.000Z');
      const asOf = new Date('2025-02-15T12:00:00.000Z');

      const result = rollingMonthly(anchor, asOf);

      // Should clamp to Feb 28 for the February period
      expect(result.start.getUTCDate()).toBe(28);
      expect(result.start.getUTCMonth()).toBe(1); // February
    });
  });

  describe('rollingYearly', () => {
    it('should calculate yearly period correctly', () => {
      const anchor = new Date('2025-03-10T10:00:00.000Z');
      const asOf = new Date('2025-06-01T12:00:00.000Z');

      const result = rollingYearly(anchor, asOf);

      // Period should be 2025-03-10 10:00 to 2026-03-09 09:59:59.999
      expect(result.start.getUTCDate()).toBe(10);
      expect(result.start.getUTCMonth()).toBe(2); // March
      expect(result.start.getUTCFullYear()).toBe(2025);

      expect(result.end.getUTCDate()).toBe(9);
      expect(result.end.getUTCMonth()).toBe(2); // March
      expect(result.end.getUTCFullYear()).toBe(2026);
    });
  });

  describe('nextRollingPeriod', () => {
    it('should calculate next monthly period', () => {
      const start = new Date('2025-03-15T10:00:00.000Z');
      const result = nextRollingPeriod(start, 'MONTHLY');

      expect(result.start.getUTCDate()).toBe(15);
      expect(result.start.getUTCMonth()).toBe(3); // April
      expect(result.start.getUTCFullYear()).toBe(2025);
    });

    it('should calculate next yearly period', () => {
      const start = new Date('2025-03-15T10:00:00.000Z');
      const result = nextRollingPeriod(start, 'YEARLY');

      expect(result.start.getUTCDate()).toBe(15);
      expect(result.start.getUTCMonth()).toBe(2); // March
      expect(result.start.getUTCFullYear()).toBe(2026);
    });
  });

  describe('Period Contiguity', () => {
    it('should ensure periods are contiguous', () => {
      const anchor = new Date('2025-03-15T10:00:00.000Z');
      const firstPeriod = rollingMonthly(anchor, anchor);
      const secondPeriod = nextRollingPeriod(firstPeriod.start, 'MONTHLY');

      // End of first period + 1ms should equal start of second period
      const endPlusOne = new Date(firstPeriod.end.getTime() + 1);
      expect(endPlusOne.getTime()).toBe(secondPeriod.start.getTime());
    });
  });

  describe('Edge Cases', () => {
    it('should handle future anchor date', () => {
      const anchor = new Date('2025-12-01T10:00:00.000Z');
      const asOf = new Date('2025-06-01T12:00:00.000Z');

      const result = rollingMonthly(anchor, asOf);

      // Current period should be the anchor itself
      expect(result.start.getTime()).toBe(anchor.getTime());
    });

    it('should handle DST transitions correctly', () => {
      // Test around DST transition in Brussels (last Sunday in March)
      const anchor = new Date('2025-03-15T10:00:00.000Z');
      const asOf = new Date('2025-04-01T12:00:00.000Z');

      const result = rollingMonthly(anchor, asOf);

      // Should maintain the same local time despite DST
      expect(result.start.getUTCDate()).toBe(15);
      expect(result.start.getUTCMonth()).toBe(2); // March
    });
  });
});

