"use client";

import { useState } from "react";
import { PublicScreen } from "@/lib/mock/public-screens";
import { formatDateBE } from "@/lib/mock/utils";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { ConfirmDialog } from "@/components/ui/ConfirmDialog";
import { CopyField } from "@/components/ui/CopyField";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Edit, Trash2, ExternalLink, Check } from "lucide-react";
import { GiPowerButton } from "react-icons/gi";
import { FaRegCopy } from "react-icons/fa";
import { EditScreenDialog } from "./EditScreenDialog";

interface ScreenCardProps {
  screen: PublicScreen;
  onToggleStatus: () => void;
  onDelete: () => void;
}

export function ScreenCard({ screen, onToggleStatus, onDelete }: ScreenCardProps) {
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [showUrlDialog, setShowUrlDialog] = useState(false);
  const [justCopied, setJustCopied] = useState(false);
  const [showEditDialog, setShowEditDialog] = useState(false);

  const handleCopyUrl = async () => {
    try {
      await navigator.clipboard.writeText(publicUrl);
      setJustCopied(true);
      setTimeout(() => setJustCopied(false), 2000);
    } catch (err) {
      // Fallback for older browsers
      setShowUrlDialog(true);
    }
  };

  const publicUrl = `${window.location.origin}/screen/${screen.publicToken || screen.id}`;

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'PAYMENT_MATRIX': return 'Betaalstatus';
      case 'ANNOUNCEMENTS': return 'Mededelingen';
      default: return type;
    }
  };

  const getTypeVariant = (type: string) => {
    switch (type) {
      case 'PAYMENT_MATRIX': return 'default';
      case 'ANNOUNCEMENTS': return 'secondary';
      default: return 'outline';
    }
  };

  const handleOpenPublic = () => {
    window.open(publicUrl, '_blank');
  };

  return (
    <>
      <Card className="glass-card card-hover animate-fade-in group border-0">
        <CardHeader className="pb-3">
          <div className="flex items-start justify-between">
            <div className="space-y-1 flex-1 min-w-0">
              <h3 className="font-semibold text-gray-900 truncate" title={screen.name}>
                {screen.name}
              </h3>
              <div className="flex items-center gap-2">
                <Badge variant={getTypeVariant(screen.type)} className="text-xs">
                  {getTypeLabel(screen.type)}
                </Badge>
                <Badge 
                  variant={screen.active ? "default" : "secondary"}
                  className={screen.active 
                    ? "bg-green-100 text-green-800 hover:bg-green-100" 
                    : "bg-red-100 text-red-800 hover:bg-red-100"
                  }
                >
                  {screen.active ? "Actief" : "Inactief"}
                </Badge>
              </div>
            </div>
            <div className="flex gap-1">
              <Button
                variant="outline"
                size="sm"
                onClick={handleCopyUrl}
                className={`h-8 w-8 p-0 ${justCopied ? 'text-green-600' : ''}`}
                title={justCopied ? "Gekopieerd!" : "Kopieer URL"}
              >
                {justCopied ? (
                  <Check className="h-4 w-4" />
                ) : (
                  <FaRegCopy className="h-4 w-4" />
                )}
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={handleOpenPublic}
                className="h-8 w-8 p-0"
                title="Open publieke view"
              >
                <ExternalLink className="h-4 w-4" />
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowEditDialog(true)}
                className="h-8 w-8 p-0"
                title="Bewerken"
              >
                <Edit className="h-4 w-4" />
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={onToggleStatus}
                className={`h-8 w-8 p-0 ${screen.active ? 'text-green-600' : 'text-gray-400'}`}
                title={screen.active ? "Deactiveren" : "Activeren"}
              >
                <GiPowerButton className="h-4 w-4" />
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowDeleteConfirm(true)}
                className="h-8 w-8 p-0 text-red-600 hover:text-red-700"
                title="Verwijderen"
              >
                <Trash2 className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </CardHeader>

        <CardContent className="pt-0">
          <div className="text-sm text-gray-500">
            Laatst gewijzigd: {formatDateBE(screen.updatedAt)}
          </div>

          <div className="mt-2 p-2 bg-gray-50 rounded text-xs">
            <div className="flex items-center gap-1 text-gray-600">
              <span>Publieke URL:</span>
              <a 
                href={`/screen/${screen.publicToken || screen.id}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 hover:text-blue-800 underline truncate"
                data-testid={`link-public-${screen.id}`}
              >
                /screen/{screen.publicToken || screen.id}
              </a>
            </div>
          </div>

        </CardContent>
      </Card>
      {/* Copy URL Dialog */}
      <Dialog open={showUrlDialog} onOpenChange={setShowUrlDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Publieke URL</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p className="text-sm text-gray-600">
              Deel deze URL om het scherm publiek te tonen:
            </p>
            <CopyField value={publicUrl} />
          </div>
        </DialogContent>
      </Dialog>
      {/* Delete Confirmation */}
      <ConfirmDialog
        open={showDeleteConfirm}
        onClose={() => setShowDeleteConfirm(false)}
        onConfirm={() => {
          onDelete();
          setShowDeleteConfirm(false);
        }}
        title="Scherm verwijderen"
        description={`Weet je zeker dat je "${screen.name}" wilt verwijderen? Deze actie kan niet ongedaan worden gemaakt.`}
        confirmText="Verwijderen"
        variant="destructive"
      />

      {/* Edit Screen Dialog */}
      <EditScreenDialog
        screen={screen}
        open={showEditDialog}
        onOpenChange={setShowEditDialog}
      />
    </>
  );
}

client/src/components/public-screens/typ.content:
"use client";

import { useState } from "react";
import { AnnouncementsConfig, AnnouncementSlide } from "@/lib/mock/public-screens";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Textarea } from "@/components/ui/textarea";
import { Separator } from "@/components/ui/separator";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Plus, Trash2, GripVertical } from "lucide-react";

interface AnnouncementsConfigFormProps {
  config: AnnouncementsConfig;
  onChange: (config: AnnouncementsConfig) => void;
}

export function AnnouncementsConfigForm({ config, onChange }: AnnouncementsConfigFormProps) {
  const updateConfig = (updates: Partial<AnnouncementsConfig>) => {
    onChange({ ...config, ...updates });
  };

  const updateAutoplay = (updates: Partial<AnnouncementsConfig['autoplay']>) => {
    onChange({
      ...config,
      autoplay: { ...config.autoplay, ...updates }
    });
  };

  const updateStyle = (updates: Partial<AnnouncementsConfig['style']>) => {
    onChange({
      ...config,
      style: { ...config.style, ...updates }
    });
  };

  const addSlide = () => {
    const newSlide: AnnouncementSlide = {
      id: `slide-${Date.now()}`,
      title: '',
      body: '',
      active: true,
      durationSec: 8
    };
    updateConfig({ slides: [...config.slides, newSlide] });
  };

  const updateSlide = (index: number, updates: Partial<AnnouncementSlide>) => {
    const updatedSlides = [...config.slides];
    updatedSlides[index] = { ...updatedSlides[index], ...updates };
    updateConfig({ slides: updatedSlides });
  };

  const removeSlide = (index: number) => {
    const updatedSlides = config.slides.filter((_, i) => i !== index);
    updateConfig({ slides: updatedSlides });
  };

  return (
    <div className="space-y-6">
      {/* Slides Management */}
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <h4 className="font-medium">Slides</h4>
          <Button onClick={addSlide} size="sm" variant="outline">
            <Plus className="h-4 w-4 mr-2" />
            Slide toevoegen
          </Button>
        </div>

        <div className="space-y-4 max-h-96 overflow-y-auto">
          {config.slides.map((slide, index) => (
            <Card key={slide.id}>
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <CardTitle className="text-sm flex items-center gap-2">
                    <GripVertical className="h-4 w-4 text-gray-400" />
                    Slide {index + 1}
                  </CardTitle>
                  <div className="flex items-center gap-2">
                    <Switch
                      checked={slide.active}
                      onCheckedChange={(checked) => updateSlide(index, { active: checked })}
                    />
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeSlide(index)}
                      className="text-red-600 hover:text-red-700"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label>Titel *</Label>
                  <Input
                    value={slide.title}
                    onChange={(e) => updateSlide(index, { title: e.target.value })}
                    placeholder="Bijvoorbeeld: Belangrijke mededeling"
                  />
                </div>

                <div className="space-y-2">
                  <Label>Inhoud</Label>
                  <Textarea
                    value={slide.body || ''}
                    onChange={(e) => updateSlide(index, { body: e.target.value })}
                    placeholder="Voeg hier de inhoud van je mededeling toe..."
                    rows={3}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label>Media URL</Label>
                    <Input
                      value={slide.mediaUrl || ''}
                      onChange={(e) => updateSlide(index, { mediaUrl: e.target.value })}
                      placeholder="https://..."
                    />
                  </div>

