"use client";

import { useEffect, useState } from "react";

export function SyncClient() {
  const [lastSync, setLastSync] = useState<Date>(new Date());
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  useEffect(() => {
    // Check reduced motion preference
    const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;

    if (prefersReducedMotion) {
      return; // Skip sync animations if user prefers reduced motion
    }

    // Mock sync indicator (in real app this would sync with server)
    const interval = setInterval(() => {
      setLastSync(new Date());
    }, 30000); // Update every 30 seconds

    // Online/offline detection
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      clearInterval(interval);
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  return (
    <div 
      className="fixed bottom-4 left-4 text-xs text-gray-500 z-50"
      aria-live="polite"
    >
      <div className={`flex items-center gap-2 ${isOnline ? 'text-green-600' : 'text-red-600'}`}>
        <div 
          className={`w-2 h-2 rounded-full ${
            isOnline ? 'bg-green-500' : 'bg-red-500'
          }`}
        />
        <span>
          {isOnline ? 'Online' : 'Offline'} â€¢ Laatst gesynchroniseerd: {lastSync.toLocaleTimeString('nl-BE')}
        </span>
      </div>
    </div>
  );
}

client/src/components/ui/CopyField.tsx.content:
"use client";

import { useState } from "react";
import { Button } from "./button";
import { Input } from "./input";
import { Copy, Check } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface CopyFieldProps {
  value: string;
  placeholder?: string;
}

export function CopyField({ value, placeholder }: CopyFieldProps) {
  const [copied, setCopied] = useState(false);
  const { toast } = useToast();

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(value);
      setCopied(true);
      toast({
        title: "Gekopieerd",
        description: "URL is gekopieerd naar klembord",
      });
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      toast({
        title: "Fout",
        description: "Kon niet kopiÃ«ren naar klembord",
        variant: "destructive"
      });
    }
  };

  return (
    <div className="flex gap-2">
      <Input 
        value={value} 
        placeholder={placeholder}
        readOnly 
        className="font-mono text-sm"
      />
      <Button
        type="button"
        variant="outline"
        size="sm"
        onClick={handleCopy}
        className="shrink-0"
      >
        {copied ? <Check className="h-4 w-4" /> : <Copy className="h-4 w-4" />}
      </Button>
    </div>
  );
}

client/src/lib/mock/announcements.ts.content:
export interface Slide {
  id: string;
  title: string;
  body?: string;
  mediaUrl?: string;
  mediaType?: 'image' | 'video';
  validFrom?: string;
  validTo?: string;
  active: boolean;
  durationSec?: number;
}

const mockSlides: Slide[] = [
  {
    id: "slide1",
    title: "Ramadan Mubarak",
    body: "Wensen jullie allemaal een gezegend Ramadan. Moge Allah jullie gebeden en goede daden aanvaarden.",
    active: true,
    durationSec: 12,
    validFrom: "2025-03-01T00:00:00Z",
    validTo: "2025-04-30T23:59:59Z"
  },
  {
    id: "slide2", 
    title: "Vrijdaggebed",
    body: "Elke vrijdag om 13:00 uur. Kom op tijd voor een plaats vooraan.",
    active: true,
    durationSec: 8,
    validFrom: "2025-01-01T00:00:00Z",
    validTo: "2025-12-31T23:59:59Z"
  },
  {
    id: "slide3",
    title: "Gemeenschapsfeest",
    body: "Zaterdag 15 februari - groot feest voor alle families. Inschrijving bij de receptie.",
    active: true,
    durationSec: 10,
    validFrom: "2025-01-15T00:00:00Z",
    validTo: "2025-02-15T23:59:59Z"
  },
  {
    id: "slide4",
    title: "Quran Lessen",
    body: "Nieuwe cursus begint maandag. Voor kinderen van 6-12 jaar.",
    active: false,
    durationSec: 8,
    validFrom: "2025-01-01T00:00:00Z",
    validTo: "2025-06-30T23:59:59Z"
  }
];

export function getActiveSlides(nowISO: string): Slide[] {
  const now = new Date(nowISO);

  return mockSlides.filter(slide => {
    if (!slide.active) return false;

    if (slide.validFrom) {
      const validFrom = new Date(slide.validFrom);
      if (now < validFrom) return false;
    }

    if (slide.validTo) {
      const validTo = new Date(slide.validTo);
      if (now > validTo) return false;
    }

    return true;
  });
}

export function getAllSlides(): Slide[] {
  return [...mockSlides];
}

export function updateSlide(id: string, updates: Partial<Slide>): Slide {
  const index = mockSlides.findIndex(s => s.id === id);
  if (index === -1) throw new Error('Slide not found');

  mockSlides[index] = { ...mockSlides[index], ...updates };
  return mockSlides[index];
}

export function createSlide(slide: Omit<Slide, 'id'>): Slide {
  const newSlide: Slide = {
    ...slide,
    id: `slide${Date.now()}`
  };
  mockSlides.push(newSlide);
  return newSlide;
}

export function deleteSlide(id: string): void {
  const index = mockSlides.findIndex(s => s.id === id);
  if (index !== -1) {
    mockSlides.splice(index, 1);
  }
}

