import { MededelingenCarouselStep } from "./steps/MededelingenCarouselStep";

interface ScreenWizardProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onComplete: (screenData: {
    name: string;
    type: ScreenType;
    config: LedenlijstConfig | MededelingenConfig;
  }) => void;
  editingScreen?: {
    id: string;
    name: string;
    type: ScreenType;
    active: boolean;
    config: LedenlijstConfig | MededelingenConfig;
  };
  mode?: 'create' | 'edit';
}

interface WizardData {
  type?: ScreenType;
  name: string;
  description?: string;
  title: TitleStyling;
  subtitle: TitleStyling;
  ledenlijstSettings?: {
    useFullNames: boolean;
    useInitials: boolean;
    filterByCategories: boolean;
    showVotingRights: boolean;
    rowsPerPage: number;
    year: number;
    categories: string[];
  };
  mededelingenSettings?: {
    slides: Array<{
      id: string;
      title: string;
      body?: string;
      mediaUrl?: string;
      mediaType?: 'image' | 'video';
      active: boolean;
      durationSec: number;
    }>;
    autoplay: {
      enabled: boolean;
      interval: number;
      order: 'date' | 'manual' | 'shuffle';
    };
    style: {
      textColor: string;
      backgroundColor: string;
      maxTextWidth: number;
    };
  };
}

const defaultTitleStyling: TitleStyling = {
  text: "",
  fontSize: 32,
  fontFamily: "Poppins",
  color: "#1f2937",
  fontWeight: "bold"
};

const defaultSubtitleStyling: TitleStyling = {
  text: "",
  fontSize: 18,
  fontFamily: "Poppins", 
  color: "#6b7280",
  fontWeight: "normal"
};

const resetWizardData = (editingScreen?: ScreenWizardProps['editingScreen']): WizardData => {
  if (editingScreen) {
    // Pre-populate with existing screen data
    const config = editingScreen.config;

    return {
      type: editingScreen.type,
      name: editingScreen.name,
      description: config.description,
      title: config.title || { ...defaultTitleStyling },
      subtitle: config.subtitle || { ...defaultSubtitleStyling },
      ledenlijstSettings: editingScreen.type === 'LEDENLIJST' ? {
        useFullNames: (config as LedenlijstConfig).display?.useFullNames ?? true,
        useInitials: (config as LedenlijstConfig).display?.useInitials ?? false,
        filterByCategories: (config as LedenlijstConfig).display?.filterByCategories ?? true,
        showVotingRights: (config as LedenlijstConfig).display?.showVotingRights ?? false,
        rowsPerPage: (config as LedenlijstConfig).display?.rowsPerPage ?? 20,
        year: (config as LedenlijstConfig).year ?? new Date().getFullYear(),
        categories: (config as LedenlijstConfig).categories ?? []
      } : undefined,
      mededelingenSettings: editingScreen.type === 'MEDEDELINGEN' ? {
        slides: (config as MededelingenConfig).slides || [],
        autoplay: (config as MededelingenConfig).autoplay || {
          enabled: true,
          interval: 8,
          order: 'date'
        },
        style: {
          textColor: (config as MededelingenConfig).style?.textContrast === 'light' ? '#ffffff' : '#000000',
          backgroundColor: (config as MededelingenConfig).style?.background === 'white' ? '#ffffff' : '#000000',
          maxTextWidth: (config as MededelingenConfig).style?.maxTextWidth || 800
        }
      } : undefined
    };
  }

  return {
    name: "",
    title: { ...defaultTitleStyling },
    subtitle: { ...defaultSubtitleStyling }
  };
};

export function ScreenWizard({ open, onOpenChange, onComplete, editingScreen, mode = 'create' }: ScreenWizardProps) {
  const [currentStep, setCurrentStep] = useState(0);
  const [wizardData, setWizardData] = useState<WizardData>(resetWizardData());

  // Reset wizard when dialog opens
  useEffect(() => {
    if (open) {
      setCurrentStep(mode === 'edit' ? 1 : 0); // Skip type selection for edit mode
      setWizardData(resetWizardData(editingScreen));
    }
  }, [open, editingScreen, mode]);

  const getSteps = () => {
    const baseSteps: Array<{ title: string; component: any }> = [];

    // Only show type selection for create mode
    if (mode === 'create') {
      baseSteps.push({ title: "Type selecteren", component: TypeSelectionStep });
    }

    baseSteps.push(
      { title: "Beschrijving", component: DescriptionStep },
      { title: "Opmaak", component: StylingStep }
    );

    if (wizardData.type === 'LEDENLIJST') {
      baseSteps.push({ title: "Configuratie", component: LedenlijstConfigStep });
    } else if (wizardData.type === 'MEDEDELINGEN') {
      // Voor mededelingen slaan we de opmaak stap over en gaan direct naar berichten
      baseSteps.splice(2, 1); // Verwijder de "Opmaak" stap
      baseSteps.push({ title: "Berichten", component: MededelingenMessagesStep });
      baseSteps.push({ title: "Carrousel", component: MededelingenCarouselStep });
    }

    return baseSteps;
  };

  const steps = getSteps();

  const isLastStep = currentStep === steps.length - 1;
  const canGoNext = () => {
    const adjustedStep = mode === 'edit' ? currentStep + 1 : currentStep;

    switch (adjustedStep) {
      case 0: return !!wizardData.type;
      case 1: return wizardData.name.length > 0;
      case 2: 
        if (wizardData.type === 'MEDEDELINGEN') {
          // Voor mededelingen is stap 2 de berichten stap
          return !!wizardData.mededelingenSettings && wizardData.mededelingenSettings.slides.length > 0;
        } else {
          // Voor andere types is stap 2 de opmaak stap
          return wizardData.title && wizardData.title.text && wizardData.title.text.length > 0;
        }
      case 3: 
        if (wizardData.type === 'LEDENLIJST') return !!wizardData.ledenlijstSettings;
        if (wizardData.type === 'MEDEDELINGEN') {
          // Voor mededelingen is stap 3 de carrousel stap
          return !!wizardData.mededelingenSettings?.autoplay;
        }
        return true;
      default: return true;
    }
  };

  const handleNext = () => {
    if (isLastStep) {
      handleComplete();
    } else {
      setCurrentStep(prev => prev + 1);
    }
  };

  const handleComplete = () => {
    if (!wizardData.type) return;

    let config: LedenlijstConfig | MededelingenConfig;

    if (wizardData.type === 'LEDENLIJST') {
      config = {
        description: wizardData.description,
        title: wizardData.title,
        subtitle: wizardData.subtitle,
        display: {
          useFullNames: wizardData.ledenlijstSettings?.useFullNames ?? true,
          useInitials: wizardData.ledenlijstSettings?.useInitials ?? false,
          filterByCategories: wizardData.ledenlijstSettings?.filterByCategories ?? true,
          showVotingRights: wizardData.ledenlijstSettings?.showVotingRights ?? false,
          rowsPerPage: wizardData.ledenlijstSettings?.rowsPerPage ?? 20
        },
        year: wizardData.ledenlijstSettings?.year ?? new Date().getFullYear(),
        categories: wizardData.ledenlijstSettings?.categories ?? []
      } as LedenlijstConfig;
    } else if (wizardData.type === 'MEDEDELINGEN') {
      config = {
        description: wizardData.description,
        title: wizardData.title,
        subtitle: wizardData.subtitle,
        slides: wizardData.mededelingenSettings?.slides || [],
        autoplay: wizardData.mededelingenSettings?.autoplay || {
          enabled: true,
          interval: 8,
          order: 'date'
        },
        style: {
          textContrast: wizardData.mededelingenSettings?.style.textColor === '#ffffff' ? 'light' : 'dark',
          background: wizardData.mededelingenSettings?.style.backgroundColor === '#ffffff' ? 'white' : 'black',
          maxTextWidth: wizardData.mededelingenSettings?.style.maxTextWidth || 800
        }
      } as MededelingenConfig;
    } else {
      // Fallback voor onbekende types - gebruik ledenlijst config
      config = {
        description: wizardData.description,
        title: wizardData.title,
        subtitle: wizardData.subtitle,
        display: {
          useFullNames: true,
          useInitials: false,
          filterByCategories: true,
          showVotingRights: false,
          rowsPerPage: 20
        },
        year: new Date().getFullYear(),
        categories: []
      } as LedenlijstConfig;
    }

    onComplete({
      name: wizardData.name,
      type: wizardData.type,
      config
    });

    // Close dialog (reset happens automatically when reopened)
    onOpenChange(false);
  };

  const StepComponent = steps[currentStep]?.component;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-6xl h-[95vh] flex flex-col" data-testid="screen-wizard">
        <DialogHeader className="flex-shrink-0 pb-6 border-b border-border">
          <DialogTitle className="flex items-center gap-3 text-xl">
            <Monitor className="h-6 w-6" />
            {mode === 'edit' ? 'Scherm bewerken' : 'Nieuw scherm aanmaken'}: {steps[currentStep]?.title}
          </DialogTitle>
          <DialogDescription className="text-base mt-2">
            {currentStep === 0 && mode === 'create' && "Kies het type scherm dat je wilt aanmaken"}
            {(currentStep === 0 && mode === 'edit') || (currentStep === 1 && mode === 'create') ? "Geef je scherm een duidelijke naam" : ''}
            {((currentStep === 1 && mode === 'edit') || (currentStep === 2 && mode === 'create')) && wizardData.type === 'MEDEDELINGEN' && "Maak en beheer je berichten"}
            {((currentStep === 1 && mode === 'edit') || (currentStep === 2 && mode === 'create')) && wizardData.type !== 'MEDEDELINGEN' && "Pas de titel en ondertitel aan"}
            {((currentStep === 2 && mode === 'edit') || (currentStep === 3 && mode === 'create')) && wizardData.type === 'MEDEDELINGEN' && "Stel de carrousel instellingen in"}
            {((currentStep === 2 && mode === 'edit') || (currentStep === 3 && mode === 'create')) && wizardData.type !== 'MEDEDELINGEN' && "Configureer de specifieke instellingen"}
          </DialogDescription>

          {/* Progress indicator */}
          <div className="flex items-center gap-4 mt-4">
            <div className="flex items-center gap-2">
              {steps.map((step, index) => (
                <div key={index} className="flex items-center">
                  <div className={`flex items-center justify-center w-8 h-8 rounded-full text-sm font-medium transition-colors ${
                    index < currentStep ? 'bg-primary text-primary-foreground' :
                    index === currentStep ? 'bg-primary text-primary-foreground' :
                    'bg-muted text-muted-foreground'
                  }`}>
                    {index + 1}
                  </div>
                  {index < steps.length - 1 && (
                    <div className={`w-12 h-px mx-2 transition-colors ${
                      index < currentStep ? 'bg-primary' : 'bg-muted'
                    }`} />
                  )}
                </div>
              ))}
            </div>
            <Badge variant="outline" className="ml-auto">
              Stap {currentStep + 1} van {steps.length}
            </Badge>
          </div>
        </DialogHeader>

        {/* Main content area with fixed height */}
        <div className="flex-1 py-6 overflow-y-auto">
          {StepComponent && (
            <StepComponent
              data={wizardData}
              onUpdate={setWizardData}
            />
          )}
        </div>

        {/* Fixed footer */}
        <div className="flex-shrink-0 flex justify-between items-center pt-6 border-t border-border">
          <Button
            variant="outline"
            onClick={() => setCurrentStep(prev => prev - 1)}
            disabled={mode === 'edit' ? currentStep === 0 : currentStep === 0}
            data-testid="wizard-back"
            size="lg"
          >
            <ChevronLeft className="w-4 h-4 mr-2" />
            Vorige
          </Button>

          <div className="flex items-center gap-3">
            <Button
              variant="ghost"
              onClick={() => onOpenChange(false)}
              data-testid="wizard-cancel"
            >
              Annuleren
            </Button>

            <Button
              onClick={handleNext}
              disabled={!canGoNext()}
              data-testid="wizard-next"
              size="lg"
            >
              {isLastStep ? 'Scherm Aanmaken' : 'Volgende'}
              {!isLastStep && <ChevronRight className="w-4 h-4 ml-2" />}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

