client/src/components/public-view/Multim.content:
"use client";

import { useState, useEffect, useRef } from "react";
import { MultimediaConfig } from "@/lib/mock/public-screens";

interface MultimediaViewProps {
  config: MultimediaConfig;
}

export function MultimediaView({ config }: MultimediaViewProps) {
  const [currentItemIndex, setCurrentItemIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);

  const activeItems = config.mediaItems.filter(item => item.active);
  const currentItem = activeItems[currentItemIndex];

  useEffect(() => {
    if (!config.autoplay.enabled || activeItems.length <= 1) return;

    // Voor video's met loop, gebruik de video duration als timing
    let duration = currentItem?.duration || config.autoplay.interval;

    // Als het een video is met loop disabled, gebruik de standaard timing
    if (currentItem?.type === 'video' && !currentItem.loop && videoRef.current) {
      const videoDuration = videoRef.current.duration;
      if (videoDuration && !isNaN(videoDuration)) {
        duration = Math.min(videoDuration, currentItem.duration);
      }
    }

    const timer = setTimeout(() => {
      handleTransition();
    }, duration * 1000);

    return () => clearTimeout(timer);
  }, [currentItemIndex, activeItems.length, config.autoplay, currentItem]);

  const handleTransition = () => {
    setIsTransitioning(true);

    setTimeout(() => {
      setCurrentItemIndex(prev => (prev + 1) % activeItems.length);
      setIsTransitioning(false);
    }, 300); // Transition duration
  };

  const getTransitionClass = () => {
    if (!isTransitioning) return "opacity-100 transform scale-100";

    switch (currentItem?.transition) {
      case 'fade':
        return "opacity-0 transform scale-100";
      case 'slide':
        return "opacity-100 transform translate-x-full";
      case 'zoom':
        return "opacity-0 transform scale-110";
      default:
        return "opacity-100 transform scale-100";
    }
  };

  if (!currentItem) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-900 to-cyan-800 text-white">
        <div className="text-center">
          <h1 className="text-4xl font-bold mb-4">Geen actieve media</h1>
          <p className="text-xl opacity-75">Er zijn momenteel geen media items om weer te geven.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen relative overflow-hidden flex flex-col bg-gradient-to-br from-blue-950 to-cyan-900">
      {/* Header with title and subtitle */}
      <div className="relative z-10 p-8 text-center">
        <h1 
          className="mb-2"
          style={{
            fontSize: `${config.title.fontSize}px`,
            fontFamily: config.title.fontFamily,
            fontWeight: config.title.fontWeight,
            color: config.title.color
          }}
        >
          {config.title.text}
        </h1>
        {config.subtitle.text && (
          <h2 
            style={{
              fontSize: `${config.subtitle.fontSize}px`,
              fontFamily: config.subtitle.fontFamily,
              fontWeight: config.subtitle.fontWeight,
              color: config.subtitle.color
            }}
          >
            {config.subtitle.text}
          </h2>
        )}
      </div>

      {/* Main media area */}
      <div className="relative z-10 flex-1 flex items-center justify-center p-8">
        <div 
          className={`w-full h-full max-w-6xl max-h-[70vh] transition-all duration-300 ease-in-out ${getTransitionClass()}`}
        >
          {currentItem.type === 'video' ? (
            <video
              ref={videoRef}
              src={currentItem.url}
              className="w-full h-full object-contain rounded-lg shadow-2xl"
              autoPlay
              muted
              loop={currentItem.loop}
              controls={currentItem.loop} // Show controls only for looped videos
              onEnded={() => {
                if (!currentItem.loop && activeItems.length > 1) {
                  handleTransition();
                }
              }}
            />
          ) : (
            <img 
              src={currentItem.url}
              alt={currentItem.name || 'Media item'}
              className="w-full h-full object-contain rounded-lg shadow-2xl"
            />
          )}
        </div>
      </div>

      {/* Media info overlay */}
      <div className="absolute bottom-4 left-4 bg-blue-900 bg-opacity-90 text-white px-4 py-2 rounded-lg border border-blue-600/30 backdrop-blur-sm">
        <div className="text-sm font-medium">{currentItem.name}</div>
        <div className="text-xs opacity-75">
          {currentItem.type === 'video' ? 
            (currentItem.loop ? 'Video (Loop)' : `Video (${currentItem.duration}s)`) :
            `Afbeelding (${currentItem.duration}s)`
          }
        </div>
      </div>

      {/* Progress indicators */}
      {activeItems.length > 1 && (
        <div className="absolute bottom-4 right-4 flex gap-2">
          {activeItems.map((_, index) => (
            <div
              key={index}
              className={`w-3 h-3 rounded-full transition-all duration-300 ${
                index === currentItemIndex 
                  ? 'bg-white scale-110' 
                  : 'bg-white opacity-40'
              }`}
            />
          ))}
        </div>
      )}

      {/* Auto-play progress bar (alleen voor niet-loop video's en afbeeldingen) */}
      {config.autoplay.enabled && activeItems.length > 1 && 
       (!currentItem.loop || currentItem.type !== 'video') && (
        <div className="absolute bottom-0 left-0 right-0 z-10">
          <div className="h-1 bg-white opacity-20">
            <div 
              className="h-full bg-white transition-all linear"
              style={{
                width: '0%',
                animation: `progress ${currentItem.duration}s linear infinite`
              }}
            />
          </div>
        </div>
      )}

      {/* Navigation overlay (bij hover) */}
      {activeItems.length > 1 && (
        <div className="absolute inset-0 z-10 opacity-0 hover:opacity-100 transition-opacity duration-300">
          <button
            className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-blue-900 bg-opacity-80 text-white p-3 rounded-full hover:bg-opacity-100 transition-all border border-blue-600/30 backdrop-blur-sm"
            onClick={() => setCurrentItemIndex(prev => 
              prev > 0 ? prev - 1 : activeItems.length - 1
            )}
          >
            â†
          </button>
          <button
            className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-blue-900 bg-opacity-80 text-white p-3 rounded-full hover:bg-opacity-100 transition-all border border-blue-600/30 backdrop-blur-sm"
            onClick={() => setCurrentItemIndex(prev => 
              prev < activeItems.length - 1 ? prev + 1 : 0
            )}
          >
            â†’
          </button>
        </div>
      )}

      <style>{`
        @keyframes progress {
          from { width: 0%; }
          to { width: 100%; }
        }
      `}</style>
    </div>
  );
}

client/src/components/public-screens/wiz.content:
"use client";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Play, Pause, RotateCcw, Shuffle } from "lucide-react";

interface MededelingenSettings {
  slides: Array<{
    id: string;
    title: string;
    body?: string;
    active: boolean;
    durationSec: number;
  }>;
  autoplay: {
    enabled: boolean;
    interval: number;
    order: 'date' | 'manual' | 'shuffle';
  };
  style: {
    textColor: string;
    backgroundColor: string;
    maxTextWidth: number;
  };
}

interface MededelingenCarouselStepProps {
  data: {
    mededelingenSettings?: MededelingenSettings;
  };
  onUpdate: (data: any) => void;
}

const orderOptions = [
  { value: 'manual', label: 'Handmatige volgorde', icon: RotateCcw },
  { value: 'date', label: 'Op datum', icon: RotateCcw },
  { value: 'shuffle', label: 'Willekeurig', icon: Shuffle }
];

export function MededelingenCarouselStep({ data, onUpdate }: MededelingenCarouselStepProps) {
  const settings = data.mededelingenSettings || {
    slides: [],
    autoplay: {
      enabled: true,
      interval: 8,
      order: 'manual'
    },
    style: {
      textColor: '#ffffff',
      backgroundColor: '#1f2937',
      maxTextWidth: 800
    }
  };

  const updateSettings = (newSettings: Partial<MededelingenSettings>) => {
    onUpdate({
      ...data,
      mededelingenSettings: {
        ...settings,
        ...newSettings
      }
    });
  };

  const updateAutoplay = (key: keyof typeof settings.autoplay, value: any) => {
    updateSettings({
      autoplay: {
        ...settings.autoplay,
        [key]: value
      }
    });
  };

  const activeSlides = settings.slides.filter(slide => slide.active);
  const totalDuration = activeSlides.reduce((sum, slide) => sum + slide.durationSec, 0);

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-medium mb-2">Carrousel instellingen</h3>
        <p className="text-sm text-gray-600">
          Configureer hoe de berichten worden afgespeeld
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Afspeel instellingen */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Play className="h-5 w-5" />
              Afspeel instellingen
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between">
              <Label htmlFor="autoplay-enabled">Automatisch afspelen</Label>
              <Switch
                id="autoplay-enabled"
                checked={settings.autoplay.enabled}
                onCheckedChange={(checked) => updateAutoplay('enabled', checked)}
                data-testid="switch-autoplay"
              />
            </div>

            {settings.autoplay.enabled && (
              <>
                <div>
                  <Label htmlFor="interval">Interval tussen berichten (seconden)</Label>
                  <Input
                    id="interval"
                    type="number"
                    value={settings.autoplay.interval}
                    onChange={(e) => updateAutoplay('interval', parseInt(e.target.value) || 8)}
                    min="1"
                    max="60"
                    data-testid="input-interval"
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Tijd tussen berichten (onafhankelijk van bericht duur)
                  </p>
                </div>

                <div>
                  <Label htmlFor="order">Afspeel volgorde</Label>
                  <Select
                    value={settings.autoplay.order}
                    onValueChange={(value: 'date' | 'manual' | 'shuffle') => updateAutoplay('order', value)}
                  >
                    <SelectTrigger data-testid="select-order">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {orderOptions.map(option => {
                        const Icon = option.icon;
                        return (
                          <SelectItem key={option.value} value={option.value}>
                            <div className="flex items-center gap-2">
                              <Icon className="h-4 w-4" />
                              {option.label}
                            </div>
                          </SelectItem>
                        );
                      })}
                    </SelectContent>
                  </Select>
                </div>
              </>
            )}
          </CardContent>
        </Card>

        {/* Overzicht */}
        <Card>
          <CardHeader>
            <CardTitle>Carrousel overzicht</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-3">
              <div className="flex justify-between text-sm">
                <span>Totaal berichten:</span>
                <span className="font-medium">{settings.slides.length}</span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Actieve berichten:</span>
                <span className="font-medium">{activeSlides.length}</span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Totale inhoud duur:</span>
                <span className="font-medium">{totalDuration}s</span>
              </div>
              {settings.autoplay.enabled && (
                <div className="flex justify-between text-sm">
                  <span>Cyclus duur:</span>
                  <span className="font-medium">
                    {totalDuration + (activeSlides.length * settings.autoplay.interval)}s
                  </span>
                </div>
              )}
            </div>

            {activeSlides.length === 0 && (
              <div className="p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <p className="text-sm text-yellow-800">
                  âš ï¸ Geen actieve berichten. Ga terug om berichten te activeren.
                </p>
              </div>
            )}

            {!settings.autoplay.enabled && (
              <div className="p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-sm text-blue-800">
                  â„¹ï¸ Handmatige besturing. Berichten worden alleen getoond op actie.
                </p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Voorbeeld tijdlijn */}
      {settings.autoplay.enabled && activeSlides.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Voorbeeld tijdlijn</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {activeSlides.slice(0, 3).map((slide, index) => (
                <div key={slide.id} className="flex items-center gap-3 p-2 bg-gray-50 rounded">
                  <div className="w-6 h-6 bg-primary text-white rounded-full flex items-center justify-center text-xs font-medium">
                    {index + 1}
                  </div>
                  <div className="flex-1">
                    <p className="text-sm font-medium">{slide.title}</p>
                  </div>
                  <div className="text-xs text-gray-500">
                    {slide.durationSec}s + {settings.autoplay.interval}s
                  </div>
                </div>
              ))}
              {activeSlides.length > 3 && (
                <div className="text-center text-sm text-gray-500 py-2">
                  ... en {activeSlides.length - 3} andere berichten
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Configuratie samenvatting */}
      <div className="p-4 bg-green-50 rounded-lg">
        <h4 className="font-medium text-green-900 mb-2">Carrousel configuratie</h4>
        <div className="text-sm text-green-700 space-y-1">
          <p>â€¢ Autoplay: {settings.autoplay.enabled ? 'Aan' : 'Uit'}</p>
          {settings.autoplay.enabled && (
            <>
              <p>â€¢ Interval: {settings.autoplay.interval} seconden</p>
              <p>â€¢ Volgorde: {orderOptions.find(o => o.value === settings.autoplay.order)?.label}</p>
              <p>â€¢ Volledige cyclus: {totalDuration + (activeSlides.length * settings.autoplay.interval)} seconden</p>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

