                          result.duplicateCheck.duplicateNameAddress
                        )
                        .map((result, index) => (
                          <div key={index} className="flex items-start gap-2 text-sm">
                            <X className="h-4 w-4 mt-0.5 flex-shrink-0 text-red-600" />
                            <div className="text-red-800">
                              <div className="font-medium mb-1">{result.member.firstName} {result.member.lastName}</div>
                              <div className="space-y-1 text-sm">
                                {result.duplicateCheck.duplicateNumber && (
                                  <div>â€¢ Lidnummer bestaat al: {result.duplicateCheck.duplicateNumber.memberNumber} 
                                    {result.duplicateCheck.duplicateNameAddress && (
                                      <span className="text-gray-600"> (zou vervangen worden door nieuw uniek nummer)</span>
                                    )}
                                  </div>
                                )}
                                {result.duplicateCheck.duplicateNameAddress && (
                                  <div>â€¢ De persoon lijkt al te bestaan met dezelfde gegevens</div>
                                )}
                              </div>
                            </div>
                          </div>
                        ))}
                    </div>
                  </div>
                )}
                {/* Leden met dubbel lidnummer die WEL geÃ¯mporteerd worden */}
                {duplicateCheckResults.filter(result => 
                  result.duplicateCheck.duplicateNumber && !result.duplicateCheck.duplicateNameAddress
                ).length > 0 && (
                  <div className="bg-amber-50 border border-amber-200 rounded-lg p-4">
                    <h4 className="font-medium text-amber-800 mb-3">
                      Leden met dubbel lidnummer (krijgen nieuw nummer):
                    </h4>
                    <div className="space-y-2">
                      {duplicateCheckResults
                        .filter(result => result.duplicateCheck.duplicateNumber && !result.duplicateCheck.duplicateNameAddress)
                        .map((result, index) => (
                          <div key={index} className="flex items-start gap-2 text-sm">
                            <AlertTriangle className="h-4 w-4 mt-0.5 flex-shrink-0 text-amber-600" />
                            <div className="text-amber-800">
                              <strong>{result.member.firstName} {result.member.lastName}</strong> 
                              <span className="text-amber-600"> - lidnummer {result.member.memberNumber} â†’ {result.duplicateCheck.suggestedNumber}</span>
                            </div>
                          </div>
                        ))}
                    </div>
                  </div>
                )}
                <DialogFooter>
                  <Button variant="outline" onClick={() => setShowDuplicateConfirmation(false)}>
                    Annuleren
                  </Button>
                  <Button 
                    onClick={() => {
                      const membersToImport = validMembers.filter(member => {
                        const result = duplicateCheckResults.find(r => r.member === member);
                        return !result || !(result.duplicateCheck.duplicateNumber && result.duplicateCheck.duplicateNameAddress) && !result.duplicateCheck.duplicateNameAddress;
                      });
                      proceedWithImport(membersToImport);
                    }}
                    disabled={validMembers.filter(member => {
                      const result = duplicateCheckResults.find(r => r.member === member);
                      return !result || !(result.duplicateCheck.duplicateNumber && result.duplicateCheck.duplicateNameAddress) && !result.duplicateCheck.duplicateNameAddress;
                    }).length === 0}
                    className="gap-2 bg-red-600 hover:bg-red-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
                  >
                    {validMembers.filter(member => {
                      const result = duplicateCheckResults.find(r => r.member === member);
                      return !result || !(result.duplicateCheck.duplicateNumber && result.duplicateCheck.duplicateNameAddress) && !result.duplicateCheck.duplicateNameAddress;
                    }).length === 0 ? 'Geen leden om te importeren' : 'Toch Importeren'}
                  </Button>
                </DialogFooter>
              </div>)
            ) : importProgress === 0 ? (
              // Preview before import
              (<div className="space-y-4 flex-1 flex flex-col">
                <div>
                  <h3 className="text-lg font-semibold">Import Preview</h3>
                  <p className="text-sm text-gray-600">
                    {validMembers.length} rijen klaar voor import. Controleer de gegevens en klik op 'Importeren' om door te gaan.
                  </p>
                </div>
                <div className="flex-1 overflow-auto border rounded">
                  <div className="min-w-max">
                    <Table>
                      <TableHeader>
                        <TableRow>
                          {csvData.length > 0 && Object.keys(csvData[0]).map((header) => (
                            <TableHead key={header} className="whitespace-nowrap min-w-[120px]">{header}</TableHead>
                          ))}
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {csvData.slice(0, validMembers.length).map((row, index) => (
                          <TableRow key={index}>
                            {Object.entries(row).map(([key, value]) => (
                              <TableCell key={key} className="whitespace-nowrap min-w-[120px] text-sm">
                                {value || '-'}
                              </TableCell>
                            ))}
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </div>
                </div>
                {validMembers.length !== csvData.length && (
                  <div className="text-sm text-amber-600 bg-amber-50 p-3 rounded border">
                    Let op: {csvData.length - validMembers.length} rijen hebben validatiefouten en worden niet geÃ¯mporteerd.
                  </div>
                )}
                <DialogFooter>
                  <Button variant="outline" onClick={() => setStep('validation')}>
                    Terug
                  </Button>
                  <Button 
                    onClick={handleImport}
                    className="gap-2"
                  >
                    <CiImport className="h-4 w-4" />
                    {validMembers.length} leden importeren
                  </Button>
                </DialogFooter>
              </div>)
            ) : (
              // Progress during import
              (<div className="text-center space-y-4">
                <div>
                  <h3 className="text-lg font-semibold">Leden aan het importeren...</h3>
                  <p className="text-sm text-gray-600">
                    {importedCount} van {validMembers.length} leden geÃ¯mporteerd
                  </p>
                </div>
                <div className="space-y-2">
                  <Progress value={importProgress} className="w-full" />
                  <p className="text-sm text-gray-500">
                    {Math.round(importProgress)}% voltooid
                  </p>
                </div>
              </div>)
            )}
          </TabsContent>

          <TabsContent value="complete" className="space-y-4">
            <div className="text-center space-y-4">
              <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center">
                <Check className="h-8 w-8 text-green-600" />
              </div>

              <div>
                <h3 className="text-lg font-semibold text-green-800">Import Voltooid!</h3>
                <p className="text-sm text-gray-600">
                  {importedCount} leden succesvol geÃ¯mporteerd
                </p>
              </div>
            </div>

            {/* Duplicate warnings */}
            {duplicateWarnings.length > 0 && (
              <div className="space-y-2">
                <h4 className="font-medium text-amber-800">Duplicate meldingen:</h4>
                <div className="bg-amber-50 border border-amber-200 rounded-lg p-4">
                  {duplicateWarnings.map((warning, index) => (
                    <div key={index} className="flex items-start gap-2 text-sm text-amber-800">
                      <AlertTriangle className="h-4 w-4 mt-0.5 flex-shrink-0" />
                      <span>{warning}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <DialogFooter>
              <Button onClick={handleClose} className="w-full">
                Sluiten
              </Button>
            </DialogFooter>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/public-screens/Edi.content:
"use client";

import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Save, X } from "lucide-react";
import { PublicScreen } from "@/lib/mock/public-screens";

interface EditScreenModalProps {
  screen: PublicScreen | null;
  open: boolean;
  onClose: () => void;
  onSave: (screen: PublicScreen) => void;
}

export function EditScreenModal({ screen, open, onClose, onSave }: EditScreenModalProps) {
  const [name, setName] = useState("");
  const [active, setActive] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);

  useEffect(() => {
    if (screen) {
      setName(screen.name);
      setActive(screen.active);
      setHasChanges(false);
    }
  }, [screen]);

  const handleSave = () => {
    if (!screen) return;

    // Create updated screen object
    const updatedScreen: PublicScreen = {
      ...screen,
      name: name.trim(),
      active,
      updatedAt: new Date().toISOString()
    };

    onSave(updatedScreen);
    setHasChanges(false);
  };

  const handleNameChange = (value: string) => {
    setName(value);
    setHasChanges(true);
  };

  const handleActiveChange = (checked: boolean) => {
    setActive(checked);
    setHasChanges(true);
  };

  const handleClose = () => {
    if (hasChanges) {
      if (confirm("Er zijn niet-opgeslagen wijzigingen. Weet je zeker dat je wilt sluiten?")) {
        onClose();
      }
    } else {
      onClose();
    }
  };

  if (!screen) return null;

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'LEDENLIJST': return 'Ledenlijst';
      case 'MEDEDELINGEN': return 'Mededelingen';
      default: return type;
    }
  };

  const publicUrl = `${window.location.origin}/screen/${screen.publicToken || screen.id}`;

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader className="pb-6">
          <div className="flex items-center justify-between">
            <div>
              <DialogTitle className="text-2xl">Scherm bewerken</DialogTitle>
              <div className="flex items-center gap-2 mt-2">
                <Badge variant="outline">{getTypeLabel(screen.type)}</Badge>
                <Badge 
                  variant={active ? "default" : "secondary"}
                  className={active ? "bg-green-100 text-green-800" : "bg-gray-100 text-gray-800"}
                >
                  {active ? "Actief" : "Inactief"}
                </Badge>
              </div>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClose}
              className="h-8 w-8 p-0"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </DialogHeader>

        <div className="space-y-6">
          {/* Basic Settings */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Basis Instellingen</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="screen-name">Naam van het scherm</Label>
                <Input
                  id="screen-name"
                  value={name}
                  onChange={(e) => handleNameChange(e.target.value)}
                  placeholder="Voer een naam in voor dit scherm"
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="screen-active">Scherm actief</Label>
                  <p className="text-sm text-gray-500">
                    Wanneer actief, is het scherm toegankelijk via de publieke URL
                  </p>
                </div>
                <Switch
                  id="screen-active"
                  checked={active}
                  onCheckedChange={handleActiveChange}
                />
              </div>
            </CardContent>
          </Card>

          {/* Screen Configuration */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Scherm Configuratie</CardTitle>
            </CardHeader>
            <CardContent>
              <Tabs defaultValue="basic" className="w-full">
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="basic">Basis</TabsTrigger>
                  <TabsTrigger value="advanced">Geavanceerd</TabsTrigger>
                </TabsList>

                <TabsContent value="basic" className="space-y-4 mt-4">
                  <div className="space-y-2">
                    <Label>Scherm Type</Label>
                    <div className="p-3 bg-gray-50 rounded border">
                      <Badge variant="outline" className="text-sm">
                        {getTypeLabel(screen.type)}
                      </Badge>
                      <p className="text-sm text-gray-500 mt-1">
                        Het scherm type kan niet worden gewijzigd na aanmaak
                      </p>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label>Publieke URL</Label>
                    <div className="p-3 bg-gray-50 rounded border">
                      <code className="text-sm text-blue-600">
                        {publicUrl}
                      </code>
                      <p className="text-sm text-gray-500 mt-1">
                        Deze URL wordt automatisch gegenereerd en kan niet worden gewijzigd
                      </p>
                    </div>
                  </div>
                </TabsContent>

                <TabsContent value="advanced" className="space-y-4 mt-4">
                  <div className="space-y-2">
                    <Label>Token</Label>
                    <div className="p-3 bg-gray-50 rounded border">
                      <code className="text-sm font-mono">
                        {screen.publicToken || screen.id}
                      </code>
                      <p className="text-sm text-gray-500 mt-1">
                        Unieke identificator voor dit scherm
                      </p>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label>Aangemaakt</Label>
                    <div className="p-3 bg-gray-50 rounded border">
                      <p className="text-sm">
                        {new Date(screen.createdAt).toLocaleString('nl-NL')}
                      </p>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label>Laatst gewijzigd</Label>
                    <div className="p-3 bg-gray-50 rounded border">
                      <p className="text-sm">
                        {new Date(screen.updatedAt).toLocaleString('nl-NL')}
                      </p>
                    </div>
                  </div>
                </TabsContent>
              </Tabs>
            </CardContent>
          </Card>

          {/* Actions */}
          <div className="flex justify-end gap-3 pt-4 border-t">
            <Button
              variant="outline"
              onClick={handleClose}
            >
              Annuleren
            </Button>
            <Button
              onClick={handleSave}
              disabled={!hasChanges || !name.trim()}
              className="gap-2"
            >
              <Save className="h-4 w-4" />
              {hasChanges ? "Wijzigingen opslaan" : "Opgeslagen"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import { useState, useEffect } from "react";
import { PublicScreen, TitleStyling, LedenlijstConfig, MededelingenConfig } from "@/lib/mock/public-screens";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ChevronLeft, ChevronRight, Edit } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useQueryClient } from "@tanstack/react-query";
import { DescriptionStep } from "./wizard/steps/DescriptionStep";
import { StylingStep } from "./wizard/steps/StylingStep";
import { LedenlijstConfigStep } from "./wizard/steps/LedenlijstConfigStep";
import { MededelingenMessagesStep } from "./wizard/steps/MededelingenMessagesStep";
import { MededelingenCarouselStep } from "./wizard/steps/MededelingenCarouselStep";

interface EditScreenDialogProps {
  screen: PublicScreen | null;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

interface WizardData {
  name: string;
  description?: string;
  title: TitleStyling;
  subtitle: TitleStyling;
  ledenlijstSettings?: {
    useFullNames: boolean;
    useInitials: boolean;
    filterByCategories: boolean;
    showVotingRights: boolean;
    rowsPerPage: number;
    year: number;
    categories: string[];
  };
  mededelingenSettings?: {
    slides: Array<{
      id: string;
      title: string;
      body?: string;
      mediaUrl?: string;
      mediaType?: 'image' | 'video';
      active: boolean;
      durationSec: number;
    }>;
    autoplay: {
      enabled: boolean;
      interval: number;
      order: 'date' | 'manual' | 'shuffle';
    };
    style: {
      textColor: string;
      backgroundColor: string;
      maxTextWidth: number;
    };
  };
}

const defaultTitleStyling: TitleStyling = {
  text: "",
  fontSize: 32,
  fontFamily: "Poppins",
  color: "#1f2937",
  fontWeight: "bold"
};

const defaultSubtitleStyling: TitleStyling = {
  text: "",
  fontSize: 18,
  fontFamily: "Poppins", 
  color: "#6b7280",
  fontWeight: "normal"
};

export function EditScreenDialog({ screen, open, onOpenChange }: EditScreenDialogProps) {
  const [currentStep, setCurrentStep] = useState(0);
  const [wizardData, setWizardData] = useState<WizardData>({
    name: "",
    title: { ...defaultTitleStyling },
    subtitle: { ...defaultSubtitleStyling }
  });
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    if (screen && open) {
      const config = screen.config;
      setCurrentStep(0);
      setWizardData({
        name: screen.name,
        description: config.description,
        title: config.title || { ...defaultTitleStyling },
        subtitle: config.subtitle || { ...defaultSubtitleStyling },
        ledenlijstSettings: screen.type === 'LEDENLIJST' ? {
          useFullNames: (config as LedenlijstConfig).display?.useFullNames ?? true,
          useInitials: (config as LedenlijstConfig).display?.useInitials ?? false,
          filterByCategories: (config as LedenlijstConfig).display?.filterByCategories ?? true,
          showVotingRights: (config as LedenlijstConfig).display?.showVotingRights ?? false,
          rowsPerPage: (config as LedenlijstConfig).display?.rowsPerPage ?? 20,
          year: (config as LedenlijstConfig).year ?? new Date().getFullYear(),
          categories: (config as LedenlijstConfig).categories ?? []
        } : undefined,
        mededelingenSettings: screen.type === 'MEDEDELINGEN' ? {
          slides: (config as MededelingenConfig).slides || [],
          autoplay: (config as MededelingenConfig).autoplay || {
            enabled: true,
            interval: 8,
            order: 'date'
          },
          style: {
            textColor: (config as MededelingenConfig).style?.textContrast === 'light' ? '#ffffff' : '#000000',
            backgroundColor: (config as MededelingenConfig).style?.background === 'white' ? '#ffffff' : '#000000',
            maxTextWidth: (config as MededelingenConfig).style?.maxTextWidth || 800
          }
        } : undefined
      });
    }
  }, [screen, open]);

  const getSteps = () => {
    if (!screen) return [];

    const baseSteps: Array<{ title: string; component: any }> = [
      { title: "Beschrijving", component: DescriptionStep },
      { title: "Opmaak", component: StylingStep }
    ];

    if (screen.type === 'LEDENLIJST') {
      baseSteps.push({ title: "Configuratie", component: LedenlijstConfigStep });
    } else if (screen.type === 'MEDEDELINGEN') {
      // Voor mededelingen slaan we de opmaak stap over en gaan direct naar berichten
      baseSteps.splice(1, 1); // Verwijder de "Opmaak" stap
      baseSteps.push({ title: "Berichten", component: MededelingenMessagesStep });
      baseSteps.push({ title: "Carrousel", component: MededelingenCarouselStep });
    }

    return baseSteps;
  };

  const steps = getSteps();
  const isLastStep = currentStep === steps.length - 1;

  const canGoNext = () => {
    switch (currentStep) {
      case 0: return wizardData.name.length > 0;
      case 1: 
        if (screen?.type === 'MEDEDELINGEN') {
          // Voor mededelingen is stap 1 de berichten stap
          return !!wizardData.mededelingenSettings && wizardData.mededelingenSettings.slides.length > 0;
        } else {
          // Voor andere types is stap 1 de opmaak stap
          return wizardData.title && wizardData.title.text && wizardData.title.text.length > 0;
        }
      case 2: 
        if (screen?.type === 'LEDENLIJST') return !!wizardData.ledenlijstSettings;
        if (screen?.type === 'MEDEDELINGEN') {
          // Voor mededelingen is stap 2 de carrousel stap
          return !!wizardData.mededelingenSettings?.autoplay;
        }
        return true;
      default: return true;
    }
  };

  const handleNext = () => {
    if (isLastStep) {
      handleComplete();
    } else {
      setCurrentStep(prev => prev + 1);
    }
  };

  const handleComplete = async () => {
    if (!screen) return;

    setIsLoading(true);
    try {
      let config: LedenlijstConfig | MededelingenConfig;

      if (screen.type === 'LEDENLIJST') {
        config = {
          description: wizardData.description,
          title: wizardData.title,
          subtitle: wizardData.subtitle,
          display: {
            useFullNames: wizardData.ledenlijstSettings?.useFullNames ?? true,
            useInitials: wizardData.ledenlijstSettings?.useInitials ?? false,
            filterByCategories: wizardData.ledenlijstSettings?.filterByCategories ?? true,
            showVotingRights: wizardData.ledenlijstSettings?.showVotingRights ?? false,
            rowsPerPage: wizardData.ledenlijstSettings?.rowsPerPage ?? 20
          },
          year: wizardData.ledenlijstSettings?.year ?? new Date().getFullYear(),
          categories: wizardData.ledenlijstSettings?.categories ?? []
        } as LedenlijstConfig;
      } else if (screen.type === 'MEDEDELINGEN') {
        config = {
          description: wizardData.description,
          title: wizardData.title,
          subtitle: wizardData.subtitle,
          slides: wizardData.mededelingenSettings?.slides || [],
          autoplay: wizardData.mededelingenSettings?.autoplay || {
            enabled: true,
            interval: 8,
            order: 'date'
          },
          style: {
            textContrast: wizardData.mededelingenSettings?.style.textColor === '#ffffff' ? 'light' : 'dark',
            background: wizardData.mededelingenSettings?.style.backgroundColor === '#ffffff' ? 'white' : 'black',
            maxTextWidth: wizardData.mededelingenSettings?.style.maxTextWidth || 800
          }
        } as MededelingenConfig;
      } else {
        // Fallback
        config = {
          description: wizardData.description,
          title: wizardData.title,
          subtitle: wizardData.subtitle,
          display: {
            useFullNames: true,
            useInitials: false,
            filterByCategories: true,
            showVotingRights: false,
            rowsPerPage: 20
          },
          year: new Date().getFullYear(),
          categories: []
        } as LedenlijstConfig;
      }

      await apiRequest('PATCH', `/api/public-screens/${screen.id}`, {
        name: wizardData.name,
        config: config
      });

      queryClient.invalidateQueries({ queryKey: ["/api/public-screens"] });
      toast({
        title: "Scherm bijgewerkt",
        description: `${wizardData.name} is succesvol bijgewerkt.`,
      });
      onOpenChange(false);
    } catch (error: any) {
      toast({
        title: "Fout",
        description: error.message || "Er is een fout opgetreden bij het bijwerken van het scherm.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'LEDENLIJST': return 'Ledenlijst';
      case 'MEDEDELINGEN': return 'Mededelingen';
      case 'MULTIMEDIA': return 'Multimedia';
      default: return type;
    }
  };

  const StepComponent = steps[currentStep]?.component;

  if (!screen) return null;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-6xl h-[95vh] flex flex-col !bg-white" data-testid="edit-screen-dialog">
        <DialogHeader className="flex-shrink-0 pb-6 border-b border-border">
          <DialogTitle className="flex items-center gap-3 text-xl">
            <Edit className="h-6 w-6" />
            Scherm bewerken: {steps[currentStep]?.title}
          </DialogTitle>
          <DialogDescription className="text-base mt-2">
            <div className="flex items-center gap-2 mb-2">
              <Badge variant="outline" className="h-8">
                {getTypeLabel(screen.type)}
              </Badge>
              <Badge 
                variant={screen.active ? "default" : "secondary"}
                className={screen.active ? "bg-green-100 text-green-800" : "bg-gray-100 text-gray-800"}
              >
                {screen.active ? "Actief" : "Inactief"}
              </Badge>
            </div>
            {currentStep === 0 && "Geef je scherm een duidelijke naam en beschrijving"}
            {currentStep === 1 && screen.type === 'MEDEDELINGEN' && "Maak en beheer je berichten"}
            {currentStep === 1 && screen.type !== 'MEDEDELINGEN' && "Pas de titel en ondertitel aan"}
            {currentStep === 2 && screen.type === 'MEDEDELINGEN' && "Stel de carrousel instellingen in"}
            {currentStep === 2 && screen.type !== 'MEDEDELINGEN' && "Configureer de specifieke instellingen"}
          </DialogDescription>

          {/* Progress indicator */}
          <div className="flex items-center gap-4 mt-4">
            <div className="flex items-center gap-2">
              {steps.map((step, index) => (
                <div key={index} className="flex items-center">
                  <div className={`flex items-center justify-center w-8 h-8 rounded-full text-sm font-medium transition-colors ${
                    index < currentStep ? 'bg-primary text-primary-foreground' :
                    index === currentStep ? 'bg-primary text-primary-foreground' :
                    'bg-muted text-muted-foreground'
                  }`}>
                    {index + 1}
                  </div>
                  {index < steps.length - 1 && (
                    <div className={`w-12 h-px mx-2 transition-colors ${
                      index < currentStep ? 'bg-primary' : 'bg-muted'
                    }`} />
                  )}
                </div>
              ))}
            </div>
            <Badge variant="outline" className="ml-auto">
              Stap {currentStep + 1} van {steps.length}
            </Badge>
          </div>
        </DialogHeader>

        {/* Main content area with fixed height */}
        <div className="flex-1 py-6 overflow-y-auto">
          {StepComponent && (
            <StepComponent
              data={wizardData}
              onUpdate={setWizardData}
            />
          )}
        </div>

        {/* Fixed footer */}
        <div className="flex-shrink-0 flex justify-between items-center pt-6 border-t border-border">
          <Button
            variant="outline"
            onClick={() => setCurrentStep(prev => prev - 1)}
            disabled={currentStep === 0}
            data-testid="wizard-back"
            size="lg"
          >
            <ChevronLeft className="w-4 h-4 mr-2" />
            Vorige
          </Button>

          <div className="flex items-center gap-3">
            <Button
              variant="ghost"
              onClick={() => onOpenChange(false)}
              data-testid="wizard-cancel"
            >
              Annuleren
            </Button>

            <Button
              onClick={handleNext}
              disabled={!canGoNext() || isLoading}
              data-testid="wizard-next"
              size="lg"
            >
              {isLoading ? 'Opslaan...' : (isLastStep ? 'Wijzigingen Opslaan' : 'Volgende')}
              {!isLastStep && !isLoading && <ChevronRight className="w-4 h-4 ml-2" />}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

