/* Card font - Space Grotesk for bank-card feel */
.card-font {
  font-family: 'Space Grotesk', -apple-system, BlinkMacSystemFont, sans-serif;
  font-feature-settings: 'tnum' 1; /* Tabular numerals */
}

/* Embossed text effect */
