import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Lock, Save } from "lucide-react";

// Schema voor profiel gegevens
const profileSchema = z.object({
  name: z.string().min(1, "Naam is verplicht"),
  email: z.string().email("Ongeldig email adres"),
});

// Schema voor wachtwoord wijzigen
const passwordSchema = z.object({
  currentPassword: z.string().min(1, "Huidig wachtwoord is verplicht"),
  newPassword: z.string().min(6, "Nieuw wachtwoord moet minimaal 6 karakters zijn"),
  confirmPassword: z.string().min(1, "Bevestig het nieuwe wachtwoord"),
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "Wachtwoorden komen niet overeen",
  path: ["confirmPassword"],
});

type ProfileFormData = z.infer<typeof profileSchema>;
type PasswordFormData = z.infer<typeof passwordSchema>;

export default function Profiel() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [profileSaved, setProfileSaved] = useState(false);

  // Profiel formulier
  const profileForm = useForm<ProfileFormData>({
    resolver: zodResolver(profileSchema),
    defaultValues: {
      name: "",
      email: "",
    },
  });

  // Wachtwoord formulier
  const passwordForm = useForm<PasswordFormData>({
    resolver: zodResolver(passwordSchema),
    defaultValues: {
      currentPassword: "",
      newPassword: "",
      confirmPassword: "",
    },
  });

  // Watch voor profiel formulier wijzigingen
  const profileFormValues = profileForm.watch();

  // Reset profiel saved state wanneer formulier wijzigt
  useEffect(() => {
    if (profileSaved) {
      setProfileSaved(false);
    }
  }, [profileFormValues]);

  // Initialiseer formulier met gebruikersgegevens
  useEffect(() => {
    if (user) {
      profileForm.reset({
        name: user.name || "",
        email: user.email || "",
      });
      setProfileSaved(false);
    }
  }, [user, profileForm]);

  // Profiel update mutatie
  const updateProfileMutation = useMutation({
    mutationFn: async (data: ProfileFormData) => {
      const response = await apiRequest("PUT", "/api/profile", data);
      return response.json();
    },
    onSuccess: async (result, variables) => {
      // Update de auth cache met nieuwe gebruikersgegevens
      queryClient.setQueryData(["/api/auth/me"], (oldData: any) => {
        if (!oldData || !oldData.user) return oldData;
        return { 
          ...oldData, 
          user: { ...oldData.user, ...variables }
        };
      });

      setProfileSaved(true);
      toast({
        title: "Profiel bijgewerkt",
        description: "Uw profiel gegevens zijn succesvol bijgewerkt.",
      });
    },
    onError: () => {
      toast({
        title: "Fout",
        description: "Er is een fout opgetreden bij het bijwerken van uw profiel.",
        variant: "destructive",
      });
    },
  });

  // Wachtwoord wijzigen mutatie
  const changePasswordMutation = useMutation({
    mutationFn: async (data: PasswordFormData) => {
      const response = await apiRequest("PUT", "/api/profile/password", {
        currentPassword: data.currentPassword,
        newPassword: data.newPassword,
      });
      return response.json();
    },
    onSuccess: () => {
      passwordForm.reset();
      toast({
        title: "Wachtwoord gewijzigd",
        description: "Uw wachtwoord is succesvol gewijzigd.",
      });
    },
    onError: (error: any) => {
      const errorMessage = error.message || "Er is een fout opgetreden bij het wijzigen van het wachtwoord.";
      toast({
        title: "Fout",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const onProfileSubmit = (data: ProfileFormData) => {
    updateProfileMutation.mutate(data);
  };

  const onPasswordSubmit = (data: PasswordFormData) => {
    changePasswordMutation.mutate(data);
  };

  if (!user) {
    return null;
  }

  return (
    <main className="flex-1 py-4">
      <div className="px-4 sm:px-6 lg:px-8 w-full">
        {/* Page Header */}
        <div className="border-b border-gray-200 pb-5">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent tracking-tight" data-testid="page-title">Mijn Profiel</h1>
              <p className="mt-2 text-sm text-gray-600 font-medium">Beheer uw account gegevens en instellingen</p>
            </div>
          </div>
        </div>

        <div className="grid gap-6 md:grid-cols-2 mt-6">
        {/* Profiel Gegevens */}
        <Card className="glass-card card-hover animate-fade-in group border-0">
          <CardHeader>
            <CardTitle>Profiel Gegevens</CardTitle>
            <CardDescription>
              Werk uw naam en email adres bij
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...profileForm}>
              <form onSubmit={profileForm.handleSubmit(onProfileSubmit)} className="space-y-4">
                <FormField
                  control={profileForm.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Naam</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          placeholder="Uw volledige naam"
                          data-testid="input-profile-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={profileForm.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="email"
                          placeholder="uw.email@voorbeeld.be"
                          data-testid="input-profile-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex items-center gap-2">
                  <Button 
                    type="submit" 
                    disabled={updateProfileMutation.isPending || profileSaved}
                    data-testid="button-save-profile"
                  >
                    <Save className="h-4 w-4 mr-2" />
                    {updateProfileMutation.isPending 
                      ? "Opslaan..." 
                      : profileSaved 
                        ? "Opgeslagen" 
                        : "Opslaan"
                    }
                  </Button>
                  {profileSaved && (
                    <span className="text-sm text-green-600">
                      âœ“ Wijzigingen opgeslagen
                    </span>
                  )}
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>

        {/* Wachtwoord Wijzigen */}
        <Card className="glass-card card-hover animate-fade-in group border-0">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Lock className="h-5 w-5" />
              Wachtwoord Wijzigen
            </CardTitle>
            <CardDescription>
              Wijzig uw account wachtwoord voor extra beveiliging
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...passwordForm}>
              <form onSubmit={passwordForm.handleSubmit(onPasswordSubmit)} className="space-y-4">
                <FormField
                  control={passwordForm.control}
                  name="currentPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Huidig Wachtwoord</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password"
                          placeholder="Voer uw huidige wachtwoord in"
                          data-testid="input-current-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={passwordForm.control}
                  name="newPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Nieuw Wachtwoord</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password"
                          placeholder="Voer uw nieuwe wachtwoord in"
                          data-testid="input-new-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={passwordForm.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Bevestig Nieuw Wachtwoord</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password"
                          placeholder="Bevestig uw nieuwe wachtwoord"
                          data-testid="input-confirm-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  disabled={changePasswordMutation.isPending}
                  variant="outline"
                  data-testid="button-change-password"
                >
                  <Lock className="h-4 w-4 mr-2" />
                  {changePasswordMutation.isPending ? "Wijzigen..." : "Wachtwoord Wijzigen"}
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>
        </div>

        {/* Account Info */}
        <Card className="mt-6 glass-card card-hover animate-fade-in group border-0">
        <CardHeader>
          <CardTitle>Account Informatie</CardTitle>
          <CardDescription>
            Alleen-lezen informatie over uw account
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label className="text-sm font-medium text-muted-foreground">Rol</Label>
              <p className="text-sm" data-testid="text-user-role">{user?.role}</p>
            </div>
            <div>
              <Label className="text-sm font-medium text-muted-foreground">Account aangemaakt</Label>
              <p className="text-sm" data-testid="text-created-at">
                {user?.createdAt ? new Date(user.createdAt).toLocaleDateString('nl-BE') : 'Onbekend'}
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
      </div>
    </main>
  );
}

client/src/components/members/export-dia.content:
import React, { useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Checkbox } from '@/components/ui/checkbox';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { CiExport } from 'react-icons/ci';
import { Loader2, Check, FileSpreadsheet } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface ExportDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  filteredMembers: any[];
}

interface ExportField {
  key: string;
  label: string;
  category: string;
}

const EXPORT_FIELDS: ExportField[] = [
  // Basis informatie
  { key: 'memberNumber', label: 'Lidnummer', category: 'Basis' },
  { key: 'firstName', label: 'Voornaam', category: 'Basis' },
  { key: 'lastName', label: 'Achternaam', category: 'Basis' },
  { key: 'birthDate', label: 'Geboortedatum', category: 'Basis' },
  { key: 'gender', label: 'Geslacht', category: 'Basis' },
  { key: 'nationality', label: 'Nationaliteit', category: 'Basis' },

  // Contact informatie
  { key: 'email', label: 'E-mail', category: 'Contact' },
  { key: 'phone', label: 'Telefoon', category: 'Contact' },
  { key: 'address', label: 'Adres', category: 'Contact' },
  { key: 'postalCode', label: 'Postcode', category: 'Contact' },
  { key: 'city', label: 'Stad', category: 'Contact' },
  { key: 'country', label: 'Land', category: 'Contact' },

  // Lidmaatschap
  { key: 'status', label: 'Status', category: 'Lidmaatschap' },
  { key: 'category', label: 'Categorie', category: 'Lidmaatschap' },
  { key: 'joinDate', label: 'Inschrijfdatum', category: 'Lidmaatschap' },
  { key: 'votingRights', label: 'Stemrecht', category: 'Lidmaatschap' },

  // Financieel
  { key: 'membershipFee', label: 'Lidmaatschapsbijdrage', category: 'Financieel' },
  { key: 'paymentMethod', label: 'Betaalmethode', category: 'Financieel' },
  { key: 'iban', label: 'IBAN', category: 'Financieel' },
  { key: 'paymentStatus', label: 'Betaalstatus', category: 'Financieel' },

  // Overig
  { key: 'emergencyContact', label: 'Noodcontact', category: 'Overig' },
  { key: 'emergencyPhone', label: 'Noodcontact telefoon', category: 'Overig' },
  { key: 'notes', label: 'Notities', category: 'Overig' }
];

const CATEGORIES = ['Basis', 'Contact', 'Lidmaatschap', 'Financieel', 'Overig'];

export function ExportDialog({ open, onOpenChange, filteredMembers }: ExportDialogProps) {
  const [currentStep, setCurrentStep] = useState<'selection' | 'exporting' | 'complete'>('selection');
  const [selectedFields, setSelectedFields] = useState<string[]>(['memberNumber', 'firstName', 'lastName', 'email', 'phone']);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportedFile, setExportedFile] = useState<string | null>(null);
  const { toast } = useToast();

  const handleFieldToggle = (fieldKey: string) => {
    setSelectedFields(prev => 
      prev.includes(fieldKey) 
        ? prev.filter(key => key !== fieldKey)
        : [...prev, fieldKey]
    );
  };

  const handleCategoryToggle = (category: string, isChecked: boolean) => {
    const categoryFields = EXPORT_FIELDS.filter(field => field.category === category).map(field => field.key);

    if (isChecked) {
      setSelectedFields(prev => {
        const newFields = [...prev, ...categoryFields];
        return Array.from(new Set(newFields));
      });
    } else {
      setSelectedFields(prev => prev.filter(key => !categoryFields.includes(key)));
    }
  };

  const isCategorySelected = (category: string) => {
    const categoryFields = EXPORT_FIELDS.filter(field => field.category === category).map(field => field.key);
    return categoryFields.every(key => selectedFields.includes(key));
  };

  const isCategoryPartiallySelected = (category: string) => {
    const categoryFields = EXPORT_FIELDS.filter(field => field.category === category).map(field => field.key);
    return categoryFields.some(key => selectedFields.includes(key)) && !isCategorySelected(category);
  };

  const handleSelectAll = () => {
    setSelectedFields(EXPORT_FIELDS.map(field => field.key));
  };

  const handleSelectNone = () => {
    setSelectedFields([]);
  };

  const handleExport = async () => {
    if (selectedFields.length === 0) {
      toast({
        title: "Geen velden geselecteerd",
        description: "Selecteer minimaal Ã©Ã©n veld om te exporteren.",
        variant: "destructive"
      });
      return;
    }

    setCurrentStep('exporting');
    setExportProgress(0);

    try {
      // Simulate progress steps
      setExportProgress(25);

      // Get member IDs from filtered members
      const memberIds = filteredMembers?.map(member => member.id) || [];

      setExportProgress(50);

      const response = await apiRequest('POST', '/api/members/export', {
        memberIds,
        fields: selectedFields
      });

      if (!response.ok) {
        throw new Error('Export failed');
      }

      setExportProgress(75);

      // Download the file
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      const fileName = `leden_export_${new Date().toISOString().split('T')[0]}.xlsx`;
      a.download = fileName;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      setExportProgress(100);
      setExportedFile(fileName);
      setCurrentStep('complete');

      toast({
        title: "Export succesvol",
        description: `${filteredMembers?.length || 0} leden geÃ«xporteerd naar Excel bestand.`
      });
    } catch (error) {
      console.error('Export error:', error);
      setCurrentStep('selection');
      toast({
        title: "Export mislukt",
        description: "Er is een fout opgetreden bij het exporteren van de leden.",
        variant: "destructive"
      });
    }
  };

  const handleClose = () => {
    setCurrentStep('selection');
    setExportProgress(0);
    setExportedFile(null);
    onOpenChange(false);
  };

  const goToNext = () => {
    if (currentStep === 'selection') {
      handleExport();
    }
  };

  const goToPrevious = () => {
    if (currentStep === 'complete') {
      setCurrentStep('selection');
      setExportProgress(0);
      setExportedFile(null);
    }
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-[650px] max-h-[85vh] flex flex-col">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FileSpreadsheet className="h-5 w-5" />
            Leden Export Wizard
          </DialogTitle>
          <p className="text-sm text-gray-600">
            {filteredMembers?.length || 0} leden klaar voor export
          </p>
        </DialogHeader>

        {/* Progress Steps */}
        <div className="flex items-center justify-between mb-6">
          <div className={`flex items-center gap-2 ${currentStep === 'selection' ? 'text-blue-600' : currentStep === 'exporting' || currentStep === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${currentStep === 'selection' ? 'bg-blue-100' : currentStep === 'exporting' || currentStep === 'complete' ? 'bg-green-100' : 'bg-gray-100'}`}>
              1
            </div>
            <span className="text-sm font-medium">Velden</span>
          </div>
          <div className={`flex-1 h-0.5 mx-4 ${currentStep === 'exporting' || currentStep === 'complete' ? 'bg-green-200' : 'bg-gray-200'}`}></div>
          <div className={`flex items-center gap-2 ${currentStep === 'exporting' ? 'text-blue-600' : currentStep === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${currentStep === 'exporting' ? 'bg-blue-100' : currentStep === 'complete' ? 'bg-green-100' : 'bg-gray-100'}`}>
              2
            </div>
            <span className="text-sm font-medium">Export</span>
          </div>
          <div className={`flex-1 h-0.5 mx-4 ${currentStep === 'complete' ? 'bg-green-200' : 'bg-gray-200'}`}></div>
          <div className={`flex items-center gap-2 ${currentStep === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${currentStep === 'complete' ? 'bg-green-100' : 'bg-gray-100'}`}>
              3
            </div>
            <span className="text-sm font-medium">Klaar</span>
          </div>
        </div>

        {/* Step Content */}
        <div className="flex-1 flex flex-col min-h-0">
          {currentStep === 'selection' && (
            <div className="flex-1 flex flex-col space-y-4">
              <div>
                <h3 className="text-lg font-semibold">Selecteer Export Velden</h3>
                <p className="text-sm text-gray-600">
                  Kies welke velden u wilt exporteren naar het Excel bestand.
                </p>
              </div>

              <div className="flex gap-2">
                <Button variant="outline" size="sm" onClick={handleSelectAll}>
                  Alles selecteren
                </Button>
                <Button variant="outline" size="sm" onClick={handleSelectNone}>
                  Niets selecteren
                </Button>
              </div>

              <div className="border rounded-lg p-4 max-h-[300px] overflow-y-auto">
                <div className="space-y-4">
                  {CATEGORIES.map(category => {
                    const categoryFields = EXPORT_FIELDS.filter(field => field.category === category);
                    const isSelected = isCategorySelected(category);
                    const isPartiallySelected = isCategoryPartiallySelected(category);

                    return (
                      <div key={category} className="space-y-2">
                        <div className="flex items-center space-x-2">
                          <Checkbox
                            id={`category-${category}`}
                            checked={isSelected}
                            data-state={isPartiallySelected ? "indeterminate" : undefined}
                            onCheckedChange={(checked) => handleCategoryToggle(category, checked as boolean)}
                          />
                          <label 
                            htmlFor={`category-${category}`}
                            className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
                          >
                            {category}
                          </label>
                        </div>

                        <div className="ml-6 space-y-2">
                          {categoryFields.map(field => (
                            <div key={field.key} className="flex items-center space-x-2">
                              <Checkbox
                                id={field.key}
                                checked={selectedFields.includes(field.key)}
                                onCheckedChange={() => handleFieldToggle(field.key)}
                              />
                              <label 
                                htmlFor={field.key}
                                className="text-sm leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
                              >
                                {field.label}
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          )}

          {currentStep === 'exporting' && (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center space-y-4">
                <div>
                  <h3 className="text-lg font-semibold">Export wordt uitgevoerd...</h3>
                  <p className="text-sm text-gray-600">
                    {filteredMembers?.length || 0} leden met {selectedFields.length} velden worden geÃ«xporteerd
                  </p>
                </div>

                <div className="space-y-2">
                  <Progress value={exportProgress} className="w-full" />
                  <p className="text-sm text-gray-500">
                    {Math.round(exportProgress)}% voltooid
                  </p>
                </div>
              </div>
            </div>
          )}

          {currentStep === 'complete' && (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center space-y-4">
                <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center">
                  <Check className="h-8 w-8 text-green-600" />
                </div>

                <div>
                  <h3 className="text-lg font-semibold text-green-800">Export Voltooid!</h3>
                  <p className="text-sm text-gray-600">
                    {filteredMembers?.length || 0} leden succesvol geÃ«xporteerd
                  </p>
                  {exportedFile && (
                    <p className="text-sm text-gray-500 mt-2">
                      Bestand: {exportedFile}
                    </p>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Footer Buttons */}
        <div className="flex justify-between pt-4 border-t">
          {currentStep === 'selection' && (
            <>
              <Button variant="outline" onClick={handleClose}>
                Annuleren
              </Button>
              <Button 
                onClick={goToNext}
                disabled={selectedFields.length === 0}
                className="gap-2"
              >
                <CiExport className="h-4 w-4" />
                Volgende - Exporteren ({selectedFields.length} velden)
              </Button>
            </>
          )}

          {currentStep === 'complete' && (
            <>
              <Button variant="outline" onClick={goToPrevious}>
                Vorige
              </Button>
              <Button onClick={handleClose}>
                Sluiten
              </Button>
            </>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/fees/FeeDetailDial.content:
import { 
  Dialog, 
  DialogContent, 
  DialogHeader, 
  DialogTitle, 
  DialogDescription
} from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Calendar, 
  Euro, 
  CreditCard, 
  Check, 
  Download, 
  Trash2, 
  User,
  Phone,
  Mail,
  UserCircle,
  Building2
} from "lucide-react";
import { format, addDays } from "date-fns";
import { toZonedTime, fromZonedTime } from "date-fns-tz";
import { nl } from "date-fns/locale";

// Types
type FeeStatus = "OPENSTAAND" | "BETAALD" | "VERVALLEN";
type PaymentTerm = "MONTHLY" | "YEARLY";
type PaymentMethod = "SEPA" | "OVERSCHRIJVING" | "BANCONTACT" | "CASH" | "UNKNOWN";

type MemberMini = {
  id: string;
  firstName: string;
  lastName: string;
  category: "STUDENT" | "STANDAARD" | "SENIOR";
  email?: string | null;
  phone?: string | null;
  memberNumber: string;
};

type FeeDetail = {
  id: string;
  amountCents: number;
  currency?: "EUR";
  term: PaymentTerm;
  method?: PaymentMethod | null;
  status: FeeStatus;
  periodStart: string;
  periodEnd: string;
  paidAt?: string | null;
  sepaBatchRef?: string | null;
  tenantName: string;
  member: MemberMini;
};

type FeeDetailDialogProps = {
  open: boolean;
  onOpenChange: (v: boolean) => void;
  fee: FeeDetail;
  canManage?: boolean;
  onMarkPaid?: (feeId: string) => Promise<void> | void;
  onGenerateSepa?: (feeId: string) => Promise<void> | void;
  onDelete?: (feeId: string) => Promise<void> | void;
  onActionSuccess?: (msg: string) => void;
  onActionError?: (msg: string) => void;
};

// Helper functions
const formatEUR = (amountCents: number): string => {
  const euros = amountCents / 100;
  return new Intl.NumberFormat('nl-BE', {
    style: 'currency',
    currency: 'EUR',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  }).format(euros);
};

const formatDateBE = (iso: string): string => {
  const date = new Date(iso);
  const belgianDate = toZonedTime(date, 'Europe/Brussels');
  return format(belgianDate, 'dd-MM-yyyy', { locale: nl });
};

const expiryDate = (isoEnd: string): string => {
  const endDate = new Date(isoEnd);
  const belgianEndDate = toZonedTime(endDate, 'Europe/Brussels');
  const expiry = addDays(belgianEndDate, 1);
  return format(expiry, 'dd-MM-yyyy', { locale: nl });
};

const statusLabel = (status: FeeStatus): string => {
  switch (status) {
    case "OPENSTAAND": return "Openstaand";
    case "BETAALD": return "Betaald";
    case "VERVALLEN": return "Vervallen";
    default: return status;
  }
};

const getStatusBadgeVariant = (status: FeeStatus) => {
  switch (status) {
    case "OPENSTAAND": return "bg-amber-100 text-amber-800 border-amber-300";
    case "BETAALD": return "bg-green-100 text-green-800 border-green-300";
    case "VERVALLEN": return "bg-red-100 text-red-800 border-red-300";
    default: return "bg-gray-100 text-gray-800 border-gray-300";
  }
};

const getPaymentMethodLabel = (method?: PaymentMethod | null): string => {
  if (!method) return "Niet gespecificeerd";
  switch (method) {
    case "SEPA": return "SEPA";
    case "OVERSCHRIJVING": return "Overschrijving";
    case "BANCONTACT": return "Bancontact";
    case "CASH": return "Contant";
    case "UNKNOWN": return "Onbekend";
    default: return method;
  }
};

const getTermLabel = (term: PaymentTerm): string => {
  switch (term) {
    case "MONTHLY": return "Maandelijks";
    case "YEARLY": return "Jaarlijks";
    default: return term;
  }
};

const getCategoryLabel = (category: string): string => {
  switch (category) {
    case "STUDENT": return "Student";
    case "STANDAARD": return "Standaard";
    case "SENIOR": return "Senior";
    default: return category;
  }
};

