import { fromZonedTime, toZonedTime } from 'date-fns-tz';
import { format, isValid } from 'date-fns';
import { nl } from 'date-fns/locale';

const BE_TIMEZONE = 'Europe/Brussels';

/**
 * Get current time in Belgium timezone
 */
export function nowBE(): Date {
  return toZonedTime(new Date(), BE_TIMEZONE);
}

/**
 * Convert any date to Belgium timezone
 */
export function toBEDate(date: Date): Date {
  if (!isValid(date)) {
    throw new Error('Invalid date provided');
  }
  return toZonedTime(date, BE_TIMEZONE);
}

/**
 * Format date in Belgian format (dd-mm-yyyy)
 */
export function formatDateBE(date: Date): string {
  if (!isValid(date)) {
    return '';
  }

  const beDate = toBEDate(date);
  return format(beDate, 'dd-MM-yyyy', { locale: nl });
}

/**
 * Format date and time in Belgian format
 */
export function formatDateTimeBE(date: Date): string {
  if (!isValid(date)) {
    return '';
  }

  const beDate = toBEDate(date);
  return format(beDate, 'dd-MM-yyyy HH:mm', { locale: nl });
}

/**
 * Convert local Belgium time to UTC for storage
 */
export function beTimeToUtc(beDate: Date): Date {
  if (!isValid(beDate)) {
    throw new Error('Invalid date provided');
  }
  return fromZonedTime(beDate, BE_TIMEZONE);
}

/**
 * Check if a date is today in Belgium timezone
 */
export function isTodayBE(date: Date): boolean {
  if (!isValid(date)) {
    return false;
  }

  const today = nowBE();
  const checkDate = toBEDate(date);

  return format(today, 'yyyy-MM-dd') === format(checkDate, 'yyyy-MM-dd');
}

/**
 * Get start of day in Belgium timezone
 */
export function startOfDayBE(date: Date): Date {
  if (!isValid(date)) {
    throw new Error('Invalid date provided');
  }

  const beDate = toBEDate(date);
  const startOfDay = new Date(beDate);
  startOfDay.setHours(0, 0, 0, 0);

  return startOfDay;
}

/**
 * Get end of day in Belgium timezone
 */
export function endOfDayBE(date: Date): Date {
  if (!isValid(date)) {
    throw new Error('Invalid date provided');
  }

  const beDate = toBEDate(date);
  const endOfDay = new Date(beDate);
  endOfDay.setHours(23, 59, 59, 999);

  return endOfDay;
}

server/lib/fees/rollover.ts.content:
import { storage } from '../../storage';
import { nowBE, startOfDayBE, toBEDate } from '../time/beTime';
import { computeCardStatus } from '../card/status';

export interface RolloverSummary {
  processedAt: Date;
  tenantId?: string;
  expiredFeesCount: number;
  newPeriodsCreated: number;
  cardsUpdated: number;
  errors: Array<{
    memberId: string;
    error: string;
  }>;
}

/**
 * Daily rollover process to handle expired fees and create new periods
 * This should be run once per day, typically at midnight BE time
 */
export async function rolloverDaily(tenantId?: string): Promise<RolloverSummary> {
  const startTime = nowBE();
  const today = startOfDayBE(nowBE());

  console.log(`Starting daily rollover for ${tenantId ? `tenant ${tenantId}` : 'all tenants'} at ${startTime.toISOString()}`);

  const summary: RolloverSummary = {
    processedAt: startTime,
    tenantId,
    expiredFeesCount: 0,
    newPeriodsCreated: 0,
    cardsUpdated: 0,
    errors: [],
  };

  try {
    // Step 1: Get all open/outstanding fees that have passed their periodEnd
    const allFees = tenantId 
      ? await storage.getMembershipFeesByTenant(tenantId)
      : await storage.getAllMembershipFees();

    const expiredFees = allFees.filter(fee => {
      const periodEnd = toBEDate(fee.periodEnd);
      return fee.status === 'OPEN' && today > periodEnd;
    });

    console.log(`Found ${expiredFees.length} expired fees to mark as overdue`);

    // Step 2: Mark expired fees as OVERDUE
    for (const fee of expiredFees) {
      try {
        await storage.updateMembershipFee(fee.id, {
          status: 'OVERDUE',
        });
        summary.expiredFeesCount++;
      } catch (error) {
        summary.errors.push({
          memberId: fee.memberId,
          error: `Failed to mark fee ${fee.id} as overdue: ${error}`,
        });
      }
    }

    // Step 3: Update card statuses for affected members
    const affectedMemberIds = [...new Set(expiredFees.map(fee => fee.memberId))];

    for (const memberId of affectedMemberIds) {
      try {
        const newStatus = await computeCardStatus(memberId);
        const cardMeta = await storage.getCardMetaByMember(memberId);

        if (cardMeta && cardMeta.status !== newStatus) {
          await storage.updateCardMeta(cardMeta.id, {
            status: newStatus,
            version: (cardMeta.version || 1) + 1,
          });
          summary.cardsUpdated++;
          console.log(`Updated card status for member ${memberId}: ${cardMeta.status} -> ${newStatus}`);
        }
      } catch (error) {
        summary.errors.push({
          memberId,
          error: `Failed to update card status: ${error}`,
        });
      }
    }

    console.log(`Daily rollover completed: ${summary.expiredFeesCount} fees expired, ${summary.cardsUpdated} cards updated`);

  } catch (error) {
    console.error('Daily rollover failed:', error);
    summary.errors.push({
      memberId: 'SYSTEM',
      error: `Rollover process failed: ${error}`,
    });
  }

  return summary;
}

/**
 * Process rollover for a specific member (useful for testing or manual correction)
 */
export async function rolloverForMember(memberId: string): Promise<void> {
  const today = startOfDayBE(nowBE());

  // Get member's fees
  const fees = await storage.getMembershipFeesByMember(memberId);

  // Mark expired open fees as overdue
  for (const fee of fees) {
    if (fee.status === 'OPEN' && toBEDate(fee.periodEnd) < today) {
      await storage.updateMembershipFee(fee.id, {
        status: 'OVERDUE',
      });
    }
  }

  // Update card status
  const newStatus = await computeCardStatus(memberId);
  const cardMeta = await storage.getCardMetaByMember(memberId);

  if (cardMeta && cardMeta.status !== newStatus) {
    await storage.updateCardMeta(cardMeta.id, {
      status: newStatus,
      version: (cardMeta.version || 1) + 1,
    });
  }
}

/**
 * Check if rollover is needed (has overdue fees that haven't been processed)
 */
export async function checkRolloverNeeded(tenantId?: string): Promise<boolean> {
  const today = startOfDayBE(nowBE());

  const allFees = tenantId 
    ? await storage.getMembershipFeesByTenant(tenantId)
    : await storage.getAllMembershipFees();

  // Check if there are any OPEN fees past their periodEnd
  const needsRollover = allFees.some(fee => {
    const periodEnd = toBEDate(fee.periodEnd);
    return fee.status === 'OPEN' && today > periodEnd;
  });

  return needsRollover;
}

tests/periods.test.ts.content:
import { describe, it, expect } from 'vitest';
import {
  monthlyFrom,
  yearlyFrom,
  nextFrom,
  createPeriod,
  isDateInPeriod,
  isPeriodActive,
  isPeriodExpired,
  generatePeriods,
  type PaymentTerm
} from '../server/lib/fees/periods';

describe('Period Calculations', () => {
  describe('monthlyFrom', () => {
    it('creates monthly periods correctly', () => {
      const jan2024 = monthlyFrom(new Date(2024, 0, 1)); // January 2024
      expect(jan2024.start).toEqual(new Date(2024, 0, 1)); // Jan 1
      expect(jan2024.end).toEqual(new Date(2024, 1, 1)); // Feb 1

      const dec2024 = monthlyFrom(new Date(2024, 11, 1)); // December 2024
      expect(dec2024.start).toEqual(new Date(2024, 11, 1)); // Dec 1
      expect(dec2024.end).toEqual(new Date(2025, 0, 1)); // Jan 1 2025
    });
  });

  describe('yearlyFrom', () => {
    it('creates yearly periods correctly', () => {
      const year2024 = yearlyFrom(new Date(2024, 0, 1));
      expect(year2024.start).toEqual(new Date(2024, 0, 1)); // Jan 1, 2024
      expect(year2024.end).toEqual(new Date(2025, 0, 1)); // Jan 1, 2025
    });
  });

  describe('generatePeriods', () => {
    it('generates multiple monthly periods', () => {
      const start = new Date(2024, 0, 1); // Jan 1, 2024
      const periods = generatePeriods(start, 'MONTHLY', 3);

      expect(periods).toHaveLength(3);
      expect(periods[0].start).toEqual(new Date(2024, 0, 1)); // Jan 1
      expect(periods[0].end).toEqual(new Date(2024, 1, 1)); // Feb 1
      expect(periods[1].start).toEqual(new Date(2024, 1, 1)); // Feb 1
      expect(periods[2].end).toEqual(new Date(2024, 3, 1)); // Apr 1
    });

    it('generates multiple yearly periods', () => {
      const start = new Date(2024, 5, 15); // June 15, 2024
      const periods = generatePeriods(start, 'YEARLY', 2);

      expect(periods).toHaveLength(2);
      expect(periods[0].start).toEqual(new Date(2024, 5, 15)); // June 15, 2024
      expect(periods[0].end).toEqual(new Date(2025, 5, 15)); // June 15, 2025
      expect(periods[1].start).toEqual(new Date(2025, 5, 15)); // June 15, 2025
      expect(periods[1].end).toEqual(new Date(2026, 5, 15)); // June 15, 2026
    });
  });

  describe('isDateInPeriod', () => {
    const period = {
      start: new Date(2024, 0, 1), // Jan 1, 2024
      end: new Date(2024, 1, 1),   // Feb 1, 2024
    };

    it('returns true for dates within period', () => {
      expect(isDateInPeriod(new Date(2024, 0, 1), period)).toBe(true); // Start date
      expect(isDateInPeriod(new Date(2024, 0, 15), period)).toBe(true); // Middle
      expect(isDateInPeriod(new Date(2024, 0, 31), period)).toBe(true); // Last day of Jan
    });

    it('returns false for dates outside period', () => {
      expect(isDateInPeriod(new Date(2023, 11, 31), period)).toBe(false); // Before start
      expect(isDateInPeriod(new Date(2024, 1, 1), period)).toBe(false); // On end date (exclusive)
      expect(isDateInPeriod(new Date(2024, 1, 2), period)).toBe(false); // After end
    });
  });

  describe('isPeriodActive', () => {
    it('correctly identifies active periods with current time', () => {
      const activePeriod = {
        start: new Date(2024, 0, 1),   // Jan 1, 2024
        end: new Date(2024, 1, 1),     // Feb 1, 2024
      };

      // Test uses current time, so just test structure
      const result = isPeriodActive(activePeriod);
      expect(typeof result).toBe('boolean');
    });
  });

  describe('isPeriodExpired', () => {
    it('correctly identifies expired periods with current time', () => {
      const expiredPeriod = {
        start: new Date(2020, 0, 1),   // Jan 1, 2020 (definitely expired)
        end: new Date(2020, 1, 1),     // Feb 1, 2020
      };

      expect(isPeriodExpired(expiredPeriod)).toBe(true);

      const futurePeriod = {
        start: new Date(2030, 1, 1),   // Future period
        end: new Date(2030, 2, 1),
      };
      expect(isPeriodExpired(futurePeriod)).toBe(false);
    });
  });
});

server/lib/card/simple-validuntil.ts.content:
import { storage } from '../../storage';
import { toBEDate } from '../time/beTime';

/**
 * Simplified validUntil service that works with existing data structures
 * Updates validUntil to the end date of the latest paid period
 */
export async function updateMemberValidUntil(memberId: string): Promise<Date | null> {
  try {
    // Get all membership fees for this member
    const fees = await storage.getMembershipFeesByMember(memberId);

    if (fees.length === 0) {
      return null;
    }

    // Find all paid periods
    const paidFees = fees.filter(fee => fee.status === 'PAID');

    if (paidFees.length === 0) {
      return null;
    }

    // Get the latest paid period
    const latestPaidPeriod = paidFees.reduce((latest, current) => 
      toBEDate(current.periodEnd) > toBEDate(latest.periodEnd) ? current : latest
    );

    const validUntilDate = toBEDate(latestPaidPeriod.periodEnd);

    // Try to update card meta if it exists
    try {
      const cardMeta = await storage.getCardMetaByMember(memberId);
      if (cardMeta) {
        await storage.updateCardMeta(cardMeta.id, {
          validUntil: validUntilDate,
          version: (cardMeta.version || 1) + 1,
        });
      }
    } catch (error) {
      console.warn('Could not update card meta:', error);
      // Continue, this is not critical for the validUntil calculation
    }

    return validUntilDate;

  } catch (error) {
    console.error('Error updating validUntil:', error);
    throw error;
  }
}

/**
 * Get the current validUntil date for a member based on their paid periods
 */
export async function getMemberValidUntil(memberId: string): Promise<Date | null> {
  try {
    const fees = await storage.getMembershipFeesByMember(memberId);
    const paidFees = fees.filter(fee => fee.status === 'PAID');

    if (paidFees.length === 0) {
      return null;
    }

    const latestPaidPeriod = paidFees.reduce((latest, current) => 
      toBEDate(current.periodEnd) > toBEDate(latest.periodEnd) ? current : latest
    );

    return toBEDate(latestPaidPeriod.periodEnd);

  } catch (error) {
    console.error('Error getting validUntil:', error);
    return null;
  }
}

/**
 * Check if a member's card is currently valid
 */
export async function isMemberCardValid(memberId: string): Promise<boolean> {
  const validUntil = await getMemberValidUntil(memberId);

  if (!validUntil) {
    return false;
  }

  const now = new Date();
  return now <= validUntil;
}

