import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { AlertTriangle, CheckCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useVotingRightsOverride } from "@/hooks/useRuleValidation";

interface RuleOverrideDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  memberId: string;
  memberName: string;
  currentStatus: boolean;
  warnings: string[];
  ruleScope: 'STEMRECHT' | 'VERKIESBAAR';
  onSuccess?: () => void;
}

export function RuleOverrideDialog({
  open,
  onOpenChange,
  memberId,
  memberName,
  currentStatus,
  warnings,
  ruleScope,
  onSuccess
}: RuleOverrideDialogProps) {
  const [overrideValue, setOverrideValue] = useState<string>(currentStatus ? 'true' : 'false');
  const [reason, setReason] = useState('');
  const { toast } = useToast();

  const overrideMutation = useVotingRightsOverride();

  const scopeLabels = {
    'STEMRECHT': 'stemrecht',
    'VERKIESBAAR': 'verkiesbaarheid'
  };

  const handleSubmit = async () => {
    if (!reason.trim()) {
      toast({
        title: "Reden vereist",
        description: "Geef een reden op voor deze handmatige toekenning.",
        variant: "destructive",
      });
      return;
    }

    try {
      await overrideMutation.mutateAsync({
        memberId,
        overrideValue: overrideValue === 'true',
        reason: reason.trim()
      });

      toast({
        title: "Override toegepast",
        description: `${scopeLabels[ruleScope]} voor ${memberName} is handmatig ${overrideValue === 'true' ? 'toegekend' : 'ingetrokken'}.`,
      });

      onSuccess?.();
      onOpenChange(false);
      setReason('');
    } catch (error) {
      toast({
        title: "Fout",
        description: "Er is een fout opgetreden bij het toepassen van de override.",
        variant: "destructive",
      });
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <AlertTriangle className="h-5 w-5 text-amber-500" />
            Handmatige toekenning {scopeLabels[ruleScope]}
          </DialogTitle>
          <DialogDescription>
            Handmatig toekennen van {scopeLabels[ruleScope]} voor <strong>{memberName}</strong>
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* Waarschuwingen tonen */}
          {warnings.length > 0 && (
            <div className="bg-amber-50 border border-amber-200 rounded-md p-3">
              <h4 className="text-sm font-medium text-amber-800 mb-2">
                Regelovertredingen gedetecteerd:
              </h4>
              <ul className="text-sm text-amber-700 space-y-1">
                {warnings.map((warning, index) => (
                  <li key={index}>â€¢ {warning}</li>
                ))}
              </ul>
            </div>
          )}

          {/* Override keuze */}
          <div className="space-y-3">
            <Label className="text-sm font-medium">
              {scopeLabels[ruleScope]} toekennen aan {memberName}?
            </Label>

            <RadioGroup value={overrideValue} onValueChange={setOverrideValue}>
              <div className="flex items-center space-x-2">
                <RadioGroupItem value="true" id="grant" />
                <Label htmlFor="grant" className="flex items-center gap-2 text-sm">
                  <CheckCircle className="h-4 w-4 text-green-600" />
                  Ja, {scopeLabels[ruleScope]} handmatig toekennen
                </Label>
              </div>
              <div className="flex items-center space-x-2">
                <RadioGroupItem value="false" id="deny" />
                <Label htmlFor="deny" className="flex items-center gap-2 text-sm">
                  <AlertTriangle className="h-4 w-4 text-red-600" />
                  Nee, {scopeLabels[ruleScope]} intrekken
                </Label>
              </div>
            </RadioGroup>
          </div>

          {/* Reden */}
          <div className="space-y-2">
            <Label htmlFor="reason" className="text-sm font-medium">
              Reden voor handmatige toekenning *
            </Label>
            <Textarea
              id="reason"
              placeholder={`Leg uit waarom je ${scopeLabels[ruleScope]} handmatig ${overrideValue === 'true' ? 'toekent' : 'intrekt'}...`}
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              rows={3}
              className="resize-none"
            />
            <p className="text-xs text-muted-foreground">
              Deze reden wordt vastgelegd voor controle en auditdoeleinden.
            </p>
          </div>

          {/* Buttons */}
          <div className="flex justify-end gap-2 pt-4">
            <Button
              variant="outline"
              onClick={() => {
                onOpenChange(false);
                setReason('');
              }}
            >
              Annuleren
            </Button>
            <Button
              onClick={handleSubmit}
              disabled={!reason.trim() || overrideMutation.isPending}
              className={overrideValue === 'true' ? 'bg-green-600 hover:bg-green-700' : 'bg-red-600 hover:bg-red-700'}
            >
              {overrideMutation.isPending ? 'Bezig...' : 
               overrideValue === 'true' ? 'Toekennen' : 'Intrekken'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/members/voting-sta.content:
import { Badge } from "@/components/ui/badge";
import { Check, X } from "lucide-react";
import { Member } from "@shared/schema";

interface VotingStatusProps {
  member: Member;
  compact?: boolean;
}

export function VotingStatus({ member, compact = false }: VotingStatusProps) {
  // Check if member has voting rights enabled in their settings
  const hasVotingRights = member.votingRights;

  if (compact) {
    // Compact view for mobile - just show icon and status
    if (!hasVotingRights) {
      return (
        <Badge variant="secondary" className="text-xs">
          <X className="h-3 w-3 mr-1" />
          Geen
        </Badge>
      );
    }

    return (
      <Badge variant="default" className="bg-green-100 text-green-800 text-xs">
        <Check className="h-3 w-3 mr-1" />
        Ja
      </Badge>
    );
  }

  // Full view for desktop
  if (!hasVotingRights) {
    return (
      <div className="flex items-center gap-2">
        <Badge variant="secondary">
          <X className="h-3 w-3 mr-1" />
          Geen stemrecht
        </Badge>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-2">
      <Badge variant="default" className="bg-green-100 text-green-800">
        <Check className="h-4 w-4 mr-1" />
        Stemgerechtigd
      </Badge>
    </div>
  );
}

client/src/components/ui/rule-warning.ts.content:
import { AlertTriangle, Info, CheckCircle } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface RuleWarningProps {
  type: 'warning' | 'error' | 'info' | 'success';
  title: string;
  warnings: string[];
  reasons?: string[];
  showOverride?: boolean;
  onOverride?: () => void;
  overrideLabel?: string;
  className?: string;
}

export function RuleWarning({
  type = 'warning',
  title,
  warnings,
  reasons = [],
  showOverride = false,
  onOverride,
  overrideLabel = 'Handmatig toekennen',
  className
}: RuleWarningProps) {
  const getIcon = () => {
    switch (type) {
      case 'error':
        return <AlertTriangle className="h-4 w-4" />;
      case 'warning':
        return <AlertTriangle className="h-4 w-4" />;
      case 'info':
        return <Info className="h-4 w-4" />;
      case 'success':
        return <CheckCircle className="h-4 w-4" />;
      default:
        return <Info className="h-4 w-4" />;
    }
  };

  const getVariant = () => {
    switch (type) {
      case 'error':
      case 'warning':
        return 'destructive';
      case 'success':
        return 'default';
      case 'info':
      default:
        return 'default';
    }
  };

  return (
    <Alert variant={getVariant()} className={cn("border-l-4", className)}>
      <div className="flex items-start gap-3">
        {getIcon()}
        <div className="flex-1 space-y-2">
          <h4 className="font-medium">{title}</h4>

          {warnings.length > 0 && (
            <div className="space-y-1">
              {warnings.map((warning, index) => (
                <AlertDescription key={index} className="text-sm">
                  {warning}
                </AlertDescription>
              ))}
            </div>
          )}

          {reasons.length > 0 && (
            <div className="mt-3 space-y-1">
              <p className="text-xs font-medium text-muted-foreground">Reden:</p>
              {reasons.map((reason, index) => (
                <p key={index} className="text-xs text-muted-foreground">
                  â€¢ {reason}
                </p>
              ))}
            </div>
          )}

          {showOverride && onOverride && (
            <div className="mt-3">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={onOverride}
                className="text-xs"
              >
                {overrideLabel}
              </Button>
            </div>
          )}
        </div>
      </div>
    </Alert>
  );
}

client/src/hooks/useRuleValidation.ts.content:
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

export interface VotingRightsStatus {
  eligible: boolean;
  hasOverride: boolean;
  reason: string;
  warnings: string[];
}

export interface RuleOverride {
  id: string;
  memberId: string;
  ruleScope: 'STEMRECHT' | 'VERKIESBAAR';
  overrideValue: boolean;
  reason: string;
  overriddenBy: string;
  overriddenAt: string;
  active: boolean;
}

export const useVotingRights = (memberId: string) => {
  return useQuery<VotingRightsStatus>({
    queryKey: [`/api/members/${memberId}/voting-rights`],
    enabled: !!memberId,
  });
};

export const useVotingRightsOverride = () => {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({ 
      memberId, 
      overrideValue, 
      reason 
    }: { 
      memberId: string; 
      overrideValue: boolean; 
      reason: string; 
    }) => {
      const response = await apiRequest('POST', `/api/members/${memberId}/voting-rights/override`, {
        overrideValue,
        reason
      });
      return response.json();
    },
    onSuccess: (data, variables) => {
      // Invalidate voting rights query for this member
      queryClient.invalidateQueries({ 
        queryKey: [`/api/members/${variables.memberId}/voting-rights`] 
      });

      // Also invalidate member list to refresh any voting rights indicators
      queryClient.invalidateQueries({ 
        queryKey: ['/api/members'] 
      });
    },
  });
};

// Hook for getting organizational rules from the database
export const useOrganizationRules = () => {
  return useQuery({
    queryKey: ['/api/rules'],
  });
};

export const useRuleValidation = () => {
  const { data: rules = [] } = useOrganizationRules();

  const validateVotingRights = (member: any): { 
    isValid: boolean; 
    warnings: string[]; 
    reasons: string[] 
  } => {
    const warnings: string[] = [];
    const reasons: string[] = [];
    let isValid = true;

    // Get voting rules from the database
    const votingRules = rules.filter((rule: any) => 
      rule.scope === 'STEMRECHT' && rule.active
    );

    // If no custom rules exist, apply default validation
    if (votingRules.length === 0) {
      // Age validation for voting rights
      if (member.birthDate) {
        const birthDate = new Date(member.birthDate);
        const today = new Date();
        let age = today.getFullYear() - birthDate.getFullYear();
        const monthDiff = today.getMonth() - birthDate.getMonth();

        if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
          age--;
        }

        if (age < 18) {
          isValid = false;
          warnings.push('âš ï¸ Lid is jonger dan 18 jaar');
          reasons.push('Minimumleeftijd voor stemrecht is 18 jaar');
        }
      }

      // Category validation
      if (member.category === 'STUDENT') {
        isValid = false;
        warnings.push(`âš ï¸ Categorie '${member.category}' heeft normaliter geen stemrecht`);
        reasons.push('Studenten hebben volgens standaard regels geen stemrecht');
      }

      // Active membership check
      if (!member.active) {
        isValid = false;
        warnings.push('âš ï¸ Lid is niet actief');
        reasons.push('Alleen actieve leden hebben stemrecht');
      }
    } else {
      // Apply custom rules from the database
      for (const rule of votingRules) {
        const params = rule.parameters || {};

        // Check minimum years (if rule exists)
        if (params.minYears) {
          const membershipYears = member.createdAt ? 
            (Date.now() - new Date(member.createdAt).getTime()) / (1000 * 60 * 60 * 24 * 365) 
            : 0;

          if (membershipYears < params.minYears) {
            isValid = false;
            warnings.push(`âš ï¸ ${rule.name || 'Regel overtreden'}`);
            reasons.push(`Minimaal ${params.minYears} jaar lidmaatschap vereist`);
          }
        }

        // Check minimum age
        if (params.minAge && member.birthDate) {
          const birthDate = new Date(member.birthDate);
          const today = new Date();
          let age = today.getFullYear() - birthDate.getFullYear();
          const monthDiff = today.getMonth() - birthDate.getMonth();

          if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
            age--;
          }

          if (age < params.minAge) {
            isValid = false;
            warnings.push(`âš ï¸ ${rule.name || 'Regel overtreden'}`);
            reasons.push(`Minimumleeftijd ${params.minAge} jaar vereist`);
          }
        }

        // Check categories (if restricted)
        if (params.categories && params.categories.length > 0) {
          if (!params.categories.includes(member.category)) {
            isValid = false;
            warnings.push(`âš ï¸ ${rule.name || 'Regel overtreden'}`);
            reasons.push(`Categorie '${member.category}' komt niet voor in toegestane categorieÃ«n`);
          }
        }
      }

      // Always check if member is active
      if (!member.active) {
        isValid = false;
        warnings.push('âš ï¸ Lid is niet actief');
        reasons.push('Alleen actieve leden hebben stemrecht');
      }
    }

    return { isValid, warnings, reasons };
  };

  return {
    validateVotingRights
  };
};

