          <TabsTrigger value="send" data-testid="tab-send">
            <Send className="w-4 h-4 mr-2" />
            Verzenden
          </TabsTrigger>
        </TabsList>

        {/* Templates Tab */}
        <TabsContent value="templates" className="space-y-6">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-xl font-semibold text-gray-900">E-mail Templates</h2>
              <p className="text-sm text-gray-600 mt-1">Beheer en bewerk je e-mail templates voor automatische berichten</p>
            </div>
            {canEdit && (
              <Button onClick={handleNewTemplate} className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700" data-testid="button-new-template">
                <Plus className="w-4 h-4" />
                Nieuwe Template
              </Button>
